css:
@namespace url(http://www.w3.org/1999/xhtml);
body, #pet_pmp_desktop
  {
    background: #F00;
    color: #F00;
  }
  
  div#my-lists-tab.a-box.a-box-tab.a-tab-content,
  ul.a-tabs.a-declarative,
  div.a-fixed-right-grid.wl-list.selected.a-spacing-micro.a-spacing-top-micro,
  div.a-box-inner,
  .a-popover-wrapper, .a-popover-header, .a-form-actions
  {
    background: #F00;
  }
  
  div#sidebar.a-fixed-right-grid-col.a-col-right, .a-text-normal, .rhf-header, div.w-popover-header, div.w-popover-header span
  {
    background: #F00;
    color: #F00!important;
  }
  
  div.a-row:nth-child(4),
  .type-radio-contents
  {
    color: #F00 !important;
  }
  
  .rhf-footer, .w-box
  {
    background: #F00 !important;
  }
  
  #rhf .rhf-divider
  {
    background: #F00 !important;
    border:none !important;
    height: 0px;
  }
  
  #quickPromoBucketContent,
  #artistCentralTeaser,
  hr.a-divider-normal.bucketDivider::after,
  hr.bucketDivider::after,
  .amabot_widget,
  .a-size-mini,
  .sims-fbt-checkbox-label,
  div.content ul,
  .a-divider-inner::after,
  #merchant-help-links table
  {
    background: #F00 !important;
    color: #F00 !important;
  }
  
  ul.a-button-list:nth-child(1) li.swatchElement.selected span span span.a-button-inner
  {
    background: #F00 !important;
    color: #F00 !important;
  }
  
  ul.a-button-list:nth-child(1) li.swatchElement.unselected span span span.a-button-inner, .a-active
  {
    background: #F00 !important;
    color: #F00 !important;
  }
  
  .rhf-source-title, .a-carousel-page-count, .you-viewed
  {
    color: #F00 !important;
  }
  
  div#nav-subnav
  {
    background: -moz-linear-gradient(top, #F00 0%, #FF8138 75%);
    background: linear-gradient(to bottom, #F00 0%,#FF8138 75%);
  }
  
  div.nav-search-field,
  select#searchDropdownBox.nav-search-dropdown.searchSelect,
  input#twotabsearchtextbox.nav-input,
  .nav-coreFlyout.nav-flyout,
  .nav-text,
  .nav-catFlyout,
  .nav-issFlyout,
  #wishListMainButton > span:nth-child(1),
  #wishListMainButton-announce
  {
    background: #F00 !important;
    color: #F00 !important;
  }
    
  ul.a-tabs li.a-active a, li.a-dropdown-item > a:hover
  {
    background-color: #F00 !important;
    color: #F00 !important;
  }
  
  a, a:active, a:link, a:visited, .sans
  {
    color: #F00 !important;
  }
  
  #dmusicTracklist_feature_div th, #dmusicTracklist_feature_div.feature tr:hover
  {
    background: #F00 !important;
  }
  
  .fresh-shoveler .as-title-block .a-color-base,
  .nav-subtext, .pet_cta_detail,
  .pet_your_prime_subheader,
  .w-recs span,
  span.a-color-secondary,
  .a-color-base,
  .w-text-large,
  ul.a-button-list:nth-child(1) li.swatchElement.selected span span,
  ul.a-button-list:nth-child(1) li.swatchElement.unselected span span
  {
    color: #FFA557 !important;
  }
  
  span.a-button-inner a, ul.a-pagination li a, ul.a-pagination li
  {
    color: #F00 !important;
  }
/* i really want this to be global */
