css:
@-moz-document domain("twitcasting.tv") {
div#mainwrapper{
    background: none;
    background-color: #393A4E;
}

h2#movietitle{
    color: aliceblue;
}

.commentareatitle{
    color: cadetblue;
}

.username{
    color: aliceblue;
}
div#wrapper h2 {
    padding: 8px 0;
    font-size: 140%;
    color: cadetblue;
}

a:hover {
    color: yellow;
    text-decoration: underline;
}

a:link {
    color: #CBB093;
    text-decoration: none;
}
a:visited{
    color: #3A443C;
}

div#navcontainer {
    width: 100%;
    text-align: center;
    background: none;
    background-color: cadetblue;
}

div#itembox {
    min-width: 474px;
    padding: 3px;
    background-color: darkcyan;
 }

.btn-sendgift{
    background-color: maroon;
}

div#submenu li {
    width: 240px;
    float: left;
    text-align: center;
    font-weight: normal;
    background: darkolivegreen;
    border-bottom: 3px solid #94c6fe;
}
div#submenu li.selected {
    padding: 10px 0;
    border-bottom: 3px solid #3381ff;
    background-color: darkolivegreen;
    font-weight: bold;
}

form.commentpost textarea {
    width: 20pc;
    background-color: beige;
}

form.commentpost button {
    width: 99px;
    height: 50px;
    margin-top: 2px;
    margin-left: 6px;
    background-color: burlywood;
}
div.global-nav {

    background-color: #738C76;
}

body {
    background-color: cadetblue;
 }

table.commentlist td.comment {
    color: #ecf0f1;
    font-weight: bold;
}

table.commentlist tr.oldcomment {
    background: #34495e;
}

table.commentlist tr.you {
   background: #34495e;
}

.user a:visited {
    color: yellow;
}

.author a:visited {
    color: yellowgreen;
}


}
