css:
/* 

V.5

based on Teal-17  and  V01 from albertd



*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.digitalspy.com") 
  
 
{

  
 .Item.ItemComment.has-ad   {background-color: #a5a5a5 !important;}


  .MItem.Category a , .info-panel a 
   {color: #000 !important;}
  
  
  .markering.leaderboard  , .markering     {background-color: #a5a5a5 !important;}
  
 
  
   section .Button.PreviewButton, 
section .Button.DraftButton, 
section .Button.Cancel, .Button.Primary.CommentButton  , .ReactButton.Quote.Visible , .reaction
   
   {background-color: #a5a5a5 !important;}
   
  
   .ItemContent.Discussion  , .Item.Alt.New.ItemDiscussion , .Item.New.ItemDiscussion
   {background-color: #a5a5a5 !important;}
   
  
  a.EditProfileLink , a.Hijack    {color: #000 !important;}
  
  
   .Item.Bookmarked.Mine.Unread.ItemDiscussion , .Item.Alt.Bookmarked.Mine.Read.ItemDiscussion, .Item.Bookmarked.Mine.Read.ItemDiscussion  {background-color: #a5a5a5 !important;}
 

   .InformMessage, .popover , .atwho-container, .InformWrapper ,.HasSprite,.Dismissable  {background-color: #a5a5a5 !important;}   /*   POP UP message  */  
 

   
   .ReactLabel , .ReactButton.Quote  {border: 2px solid #a5a5a5 !important;}  /* button */  
  
     
 .InputBox,  .TextBoxWrapper , .editor-dialog-fire-close, .MessageForm, .CommentForm, .FormTitleWrapper  {background-color: #a5a5a5 !important; font-size: 15pt !important; }
   
   
   .Buttons   {background-color: #a5a5a5 !important;}
   
   .bodybox-wrap , .editor, .editor-format-bbcode, .editor-uploads-disabled, .editor-desktop  {background-color: #a5a5a5 !important;}
 
  .info-panel.a    {color: #000 !important;}  /* username */  

  
  
     
   .ItemContent , .item , .Flyout.FlyoutMenu  {
background-color: #a5a5a5 !important; /*  PM's    */
}  
  
   .Item.Item-Search  
           {background-color: #a5a5a5 !important; /*  Search    */
}  
  
  
body , footer , nav , .CategoryName {background-color: #a5a5a5 !important;
}  
  
  
  .DiscussionName, .Wrap ,.BlockColumn, .BlockColumn-Use, .FirstUser, .BigCount ,.CountComments   {color: #000000 !important;
background-color: #a5a5a5 !important;  font-size: 14pt !important;
}   
  
  
  
  

 
  
  
div.nav-sticky  , .nav 
  {color: #c1c1c1 !important;
background-color: #a5a5a5 !important;  position: relative !important; /* part of the top bar - #668888 */  
}
  
  
  
div.Options
  {color: #000000 !important;
background-color: #a5a5a5 !important; /* top bar - #668888 */  
}   
 

div.ds-container
  {color: #000 !important;
background-color: #a5a5a5 !important;  padding:5px 5px 5px; width:1200% !important;/* background to top bar - #668888 */  
}  
  
div.page-sidebar
  {color: #000 !important;
background-color: #a5a5a5 !important; /* Thin line above second bar - #aabbbb */
} 
  
div.info-panel.col-xs-12.col-md-12
 {color: #000000 !important;
background-color: #a5a5a5 !important; /* background to forum path - white */  
}
  
div.profile-nav.col-xs-6.col-md-6  
 {color: #000000 !important;
background-color: #a5a5a5 !important; font-size: 14pt !important;/* background to options on right - white */  
}  
  
div.Wrap  
  {color: #000 !important;
background-color: transparent !important; /* Background to list of threads - transparent */
}  

div.P.PageDescription  
  {color: #000 !important;
background-color: #a5a5a5 !important; font-size: 14pt !important; /* round the description of thread list pages - #668888 */
}   
  
div.Item-BodyWrap
  {color: #000 ; padding-top: 60px !important;
background-color: #a5a5a5 !important; /* Background to posts - #ccdddd */
} 
    
div.QuoteText , .Quote ,  .UserQuote ,blockquote.Quote.UserQuote
  {color: #000 !important;
background-color: #a5a5a5 !important; font-size: 14pt !important; /* Background to text in quote boxes - white */
}
  
  

div.QuoteAuthor
  {color: #000 !important;
background-color: #a5a5a5 !important; font-size: 14pt !important; /* Background to author line in quote boxes - white */
}  
  
div.DataTableWrap  
  {color: #000 !important;
background-color: #a5a5a5 !important; 
/* Background to alternate lines in thread lists (and hover on the others)- #ccdddd */
/* Set to #ffffff (white) if you don't like the flashing */
}
 

div.Item-Body
{color: #000 !important;
background-color: #a5a5a5 !important; /* Round message display area - #ccdddd */
}  
  
  
  span ,a  {color: #000000 !important; 
background-color: #a5a5a5 !important; font-size: 14pt !important;  /* url link   */
}  
  
  
  time {color: #000000 !important; 
background-color: #a5a5a5 !important; font-size: 11pt !important;  /* url link   */
}  
  
  
  
  
  .Breadcrumbs ,.CrumbLabel ,.Category-general-discussion  {color: #000000 !important; 
font: #000 !important; font-size: 14pt !important; /* forum menu  */
}  
  
  
  
  .Item-Header.CommentHeader , .Item-Header, .DiscussionHeader  {color: #000000 !important;
background-color: #a5a5a5 !important;  font-size: 14pt !important; /* member id    */
}  
  
   
  .row.site-content   {
background-color: #a5a5a5 !important; /*  edges    */
}  
  
  
  
div.Message
{color: #000000 !important;
background-color: #a5a5a5 !important; padding-bottom: 80px !important; font-size: 14pt !important; /* round message display text - #ccdddd */
}  
    
 
div.CommentFormWrap
{color: #000 !important;
background-color: #a5a5a5 !important;  /* round message entering area - #668888 */
}
  
div.CommentsWrap
{color: #000 !important;
background-color: #a5a5a5 !important; /* above message entering area and between posts - #668888 */
}  

  
div.page-content
{color: #000 !important;
background-color: #a5a5a5 !important; font-size: 14pt !important; /* Stripe above message entering area - #668888 */
}
  
div.markering.mid
{color: #000 !important;
background-color: transparent !important; /* Stripe above message entering area - transparent */
}  


}
