css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com") {

  .js_related-module, .related-module, .sidebar, .sharingfooter
  {
    display: none !important;
  }
  
  .meta--pe .meta__byline, .meta--pe .meta__byline a {
    color: #fff;
  }
  
  .headline, .headline a, .headline a:active, .headline a:focus, .headline a:hover {
    color: #fff;
  }
  
  
  html body {
    background-color: #373a3e;
    color: #eee;
    border-style: none;
  }

 .topnav__logostrip, .topnav__logostrip.topnav__logostrip--subtitle .topnav__logo-wrapper, .section-nav
  {
    background-color: #333;
  }
 
.author-bio {
    background: #80a6ba;
}

.f_branding_on.blog-group-lifehacker .post-content p a {
    color: #00caf4;
}


