css:
@-moz-document domain("animespirit.ru") {
/* @import url(https://fonts.googleapis.com/css?family=Roboto+Condensed:400,300,700&subset=latin,cyrillic-ext); */
/* @import url(http://fonts.googleapis.com/css?family=Dancing+Script:700); */
/* -= Based on style "Zen AnimeSpirit" by bru-san (License CC BY) =- */
/************************************************/
/*            - Mod by ReColManiac -            */
/*               (License CC BY)                */
/* Befor install this style Your need install:  */
/* userstyles.org/styles/116803/zen-animespirit */
/************************************************/
/* -= Modification by ReColManiac =- */
span[style$="color:#191970"] {color: #3388ee !important}
span[style$="color:#473C8B"] {color: #8888ee !important}
span[style$="color:#0e091c"] {color: #ee00cc !important}
[style="color:#666600"] {color: #FFFF00 !important;}
[style="color:#330000"] {color: #CC3333 !important;}
[style="color:#004A83"] {color: #3366CC !important;}
[style="color:#330033"] {color: #CC33CC !important;}
.navigation span	{padding: 3px 6px!important;box-shadow: 0 0 1px #FF0, 0 0 4px #FF3;border: solid 1px #FF3 ;border-radius: 4px;color: yellow;text-shadow: 0 0 2px #EE3}
.navigation span:first-child,
.navigation span:last-child	{padding: 3px 6px!important;text-shadow:0 0 1px #E55, 0 0 4px #C55, 0 0 8px #855!important;color:#200!important;background:#333 !important;border: 1px solid #555!important;box-shadow: 0 0 0 0 !important}
.forum_navigation a,
.navigation a, .text_spoiler a,
.navigation a:link,
.navigation a:visited,
fieldset > ul > li > a {border-radius: 4px;font:bold medium "Roboto Condensed","DejaVu Sans Condensed","Arial Narrow",Sans;color: #3E3!important;transition: linear all 3s 1s;}
.text_spoiler a,
fieldset > ul > li > a:link,
fieldset > ul > li > a:visited {padding-left: 6px; padding-right: 6px;}
.navigation a:link,
.navigation a:visited {padding: 3px 6px!important}
.navigation a:active,
.navigation a:hover,
div[class="text_spoiler"] > a:hover,
div[class="text_spoiler"] > a:active,
fieldset > ul > li > a:hover,
fieldset > ul > li > a:active {color:lime!important;text-shadow: 0 0 3px lime!important;box-shadow: 0 0 2px #3F3, 0 0 5px #3E3 !important;transition: 100ms ease-in}
div[id^="news-id-"],
.content-block > center:nth-child(3) > table > tbody > tr:nth-child(2) > td:nth-child(1),
td {font: 125% "Roboto Condensed" !important}
img {transition: ease 3s 7s, -webkit-filter 3s 7s, opacity 3s 7s;}
img:hover,img.highslide-image{-webkit-filter:grayscale(0);filter:grayscale(0); opacity:.99;transition:ease-out 1s 0ms, -webkit-filter 1s 0ms, opacity 1s 0ms;}
fieldset{border-radius:4px 16px !important}
input {border-radius:8px 0 !important}
div#content::before {background: #222 !important}
a.highslide > img {cursor: url('http://www.animespirit.ru/images/cur/zoom.gif'), zoom-in  !important}
h1>a, h2>a, h3>a {color:#F83 !important}
/* -=End Modificatin=- */
/* -= Added by ReColManiac =- */
*, html, body, div, span {font: normal medium 'Roboto Condensed', 'DejaVu Sans Condensed', sans !important;}
h1>a:hover, h2>a:hover, h3>a:hover {color:#FE3 !important;text-shadow: 0 0 2px #EC3,0 0 6px #EB3}
.content-block-title h2 a, .block-title h3 {font-size:150% !important;}
div{background-color: transparent !important}
#CollapsiblePanel_LTBanner_NOHIDE {display:none !important}
*[style="color: black"],.search, span[style="color:black"] {color:unset !important}
.rating {background-color: #000 !important; color: #fff !important}
.textin {color: #fff !important;background-image: none !important;background-color: #000 !important}
.borderwrap, .maintitle {background-image: none !important;background-color: #222 !important; border: none !important}
#dropmenudiv, #dropmenudiv a {border: 1px solid #888 !important;background-color: #222 !important}
div[id*="spoiler"] {padding: 0px !important;border: none !important;background-color: transparent !important}
li.top > a > span {border: solid #CCC 1px;border-radius: 8px 0 8px 0;padding: 0 5px 0 5px !important}
li.top a > span:hover {border: solid #FFF 1px;border-radius: 8px 0 8px 0;text-align: center !important;padding: 0 5px 0 5px !important}
tr[bgcolor="#FFFFFF"] {background: #444}
tr[bgcolor="#F2F2F2"] {background: #333}
#alltracks > param:nth-child(1), object#alltracks {background: transparent !important}
div[id^="track"][style="height: 24px"], table.tracklist tr td {vertical-align: top}
table.tracklist {width: 100%}
table.tracklist th {padding: 0 !important}
table.tracklist tr td {white-space: nowrap; text-align: left}
table.tracklist th, table.tracklist tr, table.tracklist td {border: none !important}
table.tracklist th {background: #F33 !important}
cadetblue table.tracklist td {background: transparent !important}
.unit-rating li a {transition: all 0ms !important}
.post2, .bg3, .dle_forum > div[style="font-size:10px; background-color:rgb(240,245,250); padding:7px; border-width:1px; border-color:rgb(194,207,223); border-style:solid;"] {background-color: #000 !important; border: none !important}
.forum_navigation a:link, .forum_navigation a:visited, .forum_navigation a:active, table.ipbtable, tr.ipbtable, td.ipbtable, .row1, .row2 {color: #FFF !important; background-color: #000 !important}
.dle_forum > table[style="border-color:rgb(194,207,223)"], .dle_forum > table[style="border-color:rgb(194,207,223)"] > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) {background-color: rgba(240, 245, 250, 0.36) !important; border: none !important; color: #FFF !important}
.dle_forum a:link, .dle_forum a:active, .dle_forum a:visited {color: #33F8EE !important}
.dle_forum a:hover {color: #8EF !important}
.catend { background-color: #000 !important; color: #fff !important}
.title_quote {background-color: rgba(136, 102, 0, .8) !important;filter: opacity(80%);border-left: none;color: #F80 !important}
b, strong {font: bold medium 'Roboto Condensed', 'DejaVu Sans Condensed', sans !important;}
*[id^="comm-id-"] {font-size: 125% !important; font-family: 'Roboto Condensed', 'DejaVu Sans Condensed', sans !important;}
[id^="comm-id-"] > * {font-size: 100% !important; font-family: 'Roboto Condensed', 'DejaVu Sans Condensed', sans !important;}
.quote {background-color: rgba(255, 153, 0, .8) !important; color: #FFF !important}
td.formbuttonrow, .borderwrap p.formbuttonrow, .borderwrap p.formbuttonrow1 {background-color: #000 !important; border: none !important}
.ctitle a {color: limegreen}
.vjs-black-poster, div.vjs-vpaid-ad.vjs-vpaid-flash-ad div.VPAID-container {background-color: unset !important}
#vk_like, *[id^="vkwidget"], #fb-root, .fb_iframe_widget, .content-block > center:nth-child(3) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(1) > div:nth-child(1) > script {display: none;}
.tShortDesc, .tShortDesc > * {color: #FFFFFF !important;}
.borderwrap table th, .polltitle, .dle_forum > table[style="font-size:10px; background-color:rgb(240,245,250); padding:7px; border-width:1px; border-color:rgb(194,207,223); border-style:solid;"] {filter: invert(100%);}
/* -=End Add=- */
}
