css:
@-moz-document domain("madleets.com") {
    body {
        background-image: url(http://i.hizliresim.com/aLDOg2.png);
    }
    main{
         background-color: rgba(25, 25, 31, 0.9);
    }
    header .header {
        background-image: none !important;
        border: none !important;;
    }
    header .header img {
        display: block;
        margin: 0 auto;
    }
    header .header img:hover {
        opacity: 0.6 !important;
        transition: opacity .2s ease-in-out;
        -moz-transition: opacity .2s ease-in-out;
        -webkit-transition: opacity .2s ease-in-out;
        -o-transition: opacity .2s ease-in-out;
    }
    header .header .header_search form {
        margin-top: 35px;
        margin-right: 8px;
        z-index: 99;
    }
   
    
    .top_panel a {
        /* color: white !important;*/
        position: relative;
        color: white;
        text-decoration: none;
    }
    .top_panel a:hover {
        color: white;
    }
    .top_panel a:before {
        content: "";
        position: absolute;
        width: 100%;
        height: 2px;
        bottom: 0;
        left: 0;
        background-color: white;
        visibility: hidden;
        -webkit-transform: scaleX(0);
        transform: scaleX(0);
        -webkit-transition: all 0.3s ease-in-out 0s;
        transition: all 0.3s ease-in-out 0s;
    }
    .top_panel a:hover:before {
        visibility: visible;
        -webkit-transform: scaleX(1);
        transform: scaleX(1);
    }
    .navigation_menu li:hover{
         background-color: rgba(255, 255, 255, 0.69);
        
    }
    .navigation_menu a:hover{
        color: black !important;
    }
    #shoutbox input.text{
            margin: 0;
             padding: 10px 8px;
    width: 100%;
    box-sizing: border-box;
    border: 1.4px solid;
    box-shadow: inset 0 2px 4px rgba(0,0,0,0.08);
    font-family: Arial, sans-serif;
    font-size: 12px;
    color: #D5D5D5;
    border-color: #5c5a5a;
    background-color: #151d2699;
    }
    
    .forum_legend li:nth-child(1){
        display: none;
    }
    .forum_legend li:nth-child(2){
        display: none;
    }
    .breadcrumb.clearfix a{
        color: white;
    }
}
