css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blenderartists.org") {
body{
background : #000000 !important;
color: #ffffff !important;
}
.baorgvert{
display : none !important;
}
#baorgheader{
display : none !important;
}
.baorgpost .details {
background-color: #000000 !important;
color: #ffffff;
}

table.page {


margin-left: auto !important; margin-right: auto;

}

.baorgpost {
  background-color: #000000 !important;
}
.vbmenu_control{
background: lightgrey !important;
}
.profile{
background: lightgrey !important;
}


/* postbit stuff */
.baorgpost {
  background-color: #303030;
}

.baorgpost .title {
  color: #ffffff !important;
  background-color: #101010 !important;
  font-size: 16px;
  line-height: 18px;
  text-decoration: none;
  letter-spacing: 1px;
  padding: 0px 0px 5px 0px;
}

.baorgpost .post-bar {
  padding:0px;
  margin:0px;
  background-image: none !important;
  background-color: #303030 !important;
  color: #fefefe !important;
}

.baorgpost .alt1 {
  background-color: #202020 !important;
 color: #fefefe !important;
}



.baorgpost .profile {
  background-color: #8A7B2B !important;
  color: #000000;
  font-size: 10px;
  line-height: 12px;
  padding-left: 10px;
  padding-top: 35px;
  padding-right: 10px;
  padding-bottom: 10px;  
}

.baorgpost .alt1 a, .baorgpost .alt1 a:hover, .baorgpost .alt1 a:visited {
  font-weight: bold;
  color: #FF9900 !important;
}


a:link, body_alink
{
	color: #FFCC33 !important;
	text-decoration: none;
}
a:visited, body_avisited
{
	color: #FFCC33 !important;
	text-decoration: none;
}
a:hover, a:active, body_ahover
{
	color: #FFCC33 !important;
	text-decoration: none;
}
.page
{
	background: #101010 !important;
	color: #fefefe;
}
.page a:link, .page_alink
{
	color: #000;
}
.page a:visited, .page_avisited
{
	color: #333;
}
.page a:hover, .page a:active, .page_ahover
{
	background: #101010 !important;
	color: #fff !important;
}





td, th, p, li
{
	background-color: #8A7B2B !important;
        color: #ffffff !important;
	font: 12px Arial, Helvetica, sans-serif;
	padding: 6px 6px 6px 6px;
}
.tborder
{
	background: #fff;
	color: #000000;
	border: 0px;
}
.tcat
{
	background: #8A7B2B !important;
	color: #999;
	font: 14px Arial, Helvetica, sans-serif;
	background-image: none !important; 
	background-repeat: no-repeat;
	height: 60px;
}
.tcat a:link, .tcat_alink
{
	color: #999;
	text-decoration: none;
}
.tcat a:visited, .tcat_avisited
{
	color: #999;
	text-decoration: none;
}
.tcat a:hover, .tcat a:active, .tcat_ahover
{
	color: #999;
	text-decoration: underline;
}
.thead
{
	background: #fff;
	color: #333;
	font: 12px Arial, Helvetica, sans-serif;
}
.thead a:link, .thead_alink
{
	color: #ffffff !important;
}
.thead a:visited, .thead_avisited
{
	color: #ffffff !important;
}
.thead a:hover, .thead a:active, .thead_ahover
{
	color: #ffffff !important;
}
.tfoot
{
	background: #8A7B2B !important;
	color: #fefefe !important;
	border-top: 1px solid #e4e4e4;
	padding: 2px 10px 0 10px;
	height: 22px;
	font-size: 10px;
	line-height: 12px;
	margin-bottom: 20px;
}
.alt1, .alt1Active
{
	background: #101010 !important;
	color: #ffffff !important;
}
.alt2, .alt2Active
{
	background: #101010 !important;
	color: #ffffff !important;
}
td.inlinemod
{
	background: #202020 !important;
	color: #ffffff !important;
}
.wysiwyg
{
	background: #101010 !important;
	color: #FFFFFF !important;
	font: 10pt Arial, Helvetica, sans-serif;
}
textarea, .bginput
{
	color: #333;
	font: 10pt Arial, Helvetica, sans-serif;
}
.button
{
	background-color: #101010 !important;
        border: 1px solid #ffffff !important;
        color: #ffffff !important;
font: 11px Arial, Helvetica, sans-serif;
}
select
{
	background-color: #101010 !important;
        color: #ffffff !important;
	font: 11px Arial, Helvetica, sans-serif;
        border: 1px solid #ffffff !important;
}
option, optgroup
{
	background-color: #101010 !important;
        color: #ffffff !important;
        border: 1px solid #ffffff !important;
	font-size: 11px;
	font-family: Arial, Helvetica, sans-serif;
}
.smallfont
{
	font: 11px Arial, Helvetica, sans-serif;
}
.time
{
	color: #000;
}
.navbar
{
	background-color: #202020 !important;
color: #ffffff !important;

font: 11px Arial, Helvetica, sans-serif;
}
.highlight
{
	color: #ff9b3f;
	font-weight: bold;
}
.fjsel
{
	background: #fff;
	color: #333;
}
.fjdpth0
{
	background: #fff;
	color: #333;
}
.panel
{
	background: #101010 !important;
	color: #ffffff !important;
	padding: 10px;
}
.panelsurround
{
	background: #202020 !important;
	color: #ffffff !important;
}
legend
{
	color: #000000 !important;
	font: bold 11px Arial, Helvetica, sans-serif;
}
.vbmenu_control
{
	

color: #000000 !important;
background-color: #8A7B2B !important;
	font: 11px Arial, Helvetica, sans-serif;
	padding: 3px 6px 3px 6px;
	white-space: nowrap;
}
.vbmenu_popup
{
	background: #101010 !important;
	color: #ffffff !important;
	border: 1px solid #999;
}
.vbmenu_option
{
	background: #fafafa;
	color: #333;
	font: 11px Arial, Helvetica, sans-serif;
	white-space: nowrap;
	cursor: pointer;
}
.vbmenu_hilite
{
	background: #101010  !important;
	color: #FFFFFF;
	font: 11px Arial, Helvetica, sans-serif;
	white-space: nowrap;
	cursor: pointer;
}
.vbmenu_hilite a:link, .vbmenu_hilite_alink
{
	color: #000000 !important;
	text-decoration: none;
}
.vbmenu_hilite a:visited, .vbmenu_hilite_avisited
{
	color: #000000 !important;
	text-decoration: none;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active, .vbmenu_hilite_ahover
{
	color: #000000 !important;
	text-decoration: none;
}
/* ***** styling for 'big' usernames on postbit etc. ***** */
.bigusername { font-size: 14pt; }

/* ***** small padding on 'thead' elements ***** */
td.thead, div.thead { padding: 4px; }

/* ***** basic styles for multi-page nav elements */
.pagenav a { text-decoration: none; }
.pagenav td { padding: 2px 4px 2px 4px; }

/* ***** define margin and font-size for elements inside panels ***** */
.fieldset { margin-bottom: 6px; }
.fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px; }


div.vbmenu_popup td.thead {
  background-color: #000000 !important;
  color: #ffffff !important;
}

#breadcrumb td.alt1 {
  background-color: #000000 !important;
}


.baorg-code {
  border: 1px solid #eee;
  border-left: 2px solid #666;
  background-color: #505050;
  color: #000000;
  padding-left: 20px;
  padding-top: 5px;
  margin-top: 0px;
}

td.inlinemod
{
	background: #8A7B2B !important;
	color: #000000 !important;
}


textarea, .bginput

{

	color: #ffffff !important;
        background-color: #101010 !important;
        border: solid 1px #ffffff !important;
    


	font: 10pt Arial, Helvetica, sans-serif;

}

/* code block */

.baorg-code {

  border: 1px solid #eee;

  border-left: 2px solid #666;

  background-color: #505050 !important;

  color: #FFFFFF !important;

  padding-left: 20px;

  padding-top: 5px;

  margin-top: 0px;

}


}
