css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("hipertextual.com") {

  .listAnalisis, .sideBar, .wrapperBanner, .aside, .roba_ATF_abp, .roba_ATF, .width--sm--sideBar, .destacados--sidebar
  {
    display:none !important;
  }
  
  main, .wrapperMain {
    background-color: #959375;
}

.width--smLarge--fullParent--OutAside
{
    width:-webkit-calc(100% - 30px);
    width:-moz-calc(100% - 30px);
    width:calc(100% - 30px)
}
  
.width--smLarge--fullWindow
{
	width:calc(100vw - 15px)
}

.aboutNav__item__link {
    color: #393535;
}

.post--extended:after {
    background: linear-gradient(to bottom, rgba(255,255,255,0) 0%,#959375 100%);
}

.post--b:after, .post--c:after, .post:after, .post.primero:after {
    background: linear-gradient(to bottom, rgba(255,255,255,0) 0%,#959375 100%); 
}
  
}
