css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lestream.fr")
  {

body, .ls-news-results, .ls-cms-content {
  background: #222222 !important;
  counter-reset: section;
}

.ls-header-nav-site-item > a, h2, strong, .ls-best-viewers-meta, .ls-badge-name, p, .ls-stats-badges, .ls-footer-nav li, .ls-footer-nav a, .ls-hmodule, li.ls-suggestions-nothing, .ls-popin-badge-meta, .ls-filter .nqdropdownmenu-toggler:hover, .ls-filter .nqdropdownmenu-toggler:focus, .ls-success-tabs-text, .ls-profile-params-section .ls-form-radio label, .ls-stats-chat, .ls-stats-auction, .ls-profile-tabs .nqtabs-list-item-active, .ls-profile-near-success-nothing, .ls-heros-listing-name, .ls-cms-rte a, li {
  color: #9A9A9A !important;
}

input[type="email"], input[type="number"], input[type="password"], input[type="text"], textarea, select, .ls-success-other-users .ls-listing-table, .ls-popin-badge-main, .ls-success-last-obtained, .ls-success-tabs .nqtabs-list-item-active, .ls-header-nav, .ls-profile-params-left form, .ls-profile-history li:nth-child(2n), .ls-best-viewers-content li > a:hover, .ls-heros-suggestions, .ls-btn-page, .ls-success-tabs .ls-success-cat-all:hover {
  background: #181818;
}

.ls-header-search button {
  background: #4e4e4e;
}

.ls-btn {
  background: #6b6b6b;
}

.ls-pages ol {
  background: #191919;
}

.ls-streamer-live-meta-origin {
  line-height: auto;
}

.ls-streamer-live-from {
  left: 20px;
  position: relative;
  text-transform: capitalize;
}

.ls-success-other-users .ls-listing-table {
  border: 1px solid #5c5c63;
}

.ls-profile-params-section .ls-form-text input, .ls-profile-params-section .ls-form-text textarea, .ls-profile-params-section .ls-form-select select {
  border: 1px solid #343434;
  color: #666666;
}

.ls-stats-time-2 {
  color: #6b6b6b !important;
}

.ls-stats-time-1 {
  color: #4d4d4d;
}

.ls-best-viewers-content::after {
  background: -webkit-linear-gradient(to right, #d4d4d4 0%, #16161d 60%) !important;
}

.ls-listing-tabl:hover {
  background: orange !important;
}

.ls-heros-suggestions .ls-suggestions-more .ls-btn {
  background: #393945;
  color: #9a9a9a;
}

.ls-heros-first {
  -webkit-filter: contrast(81%) brightness(53%) saturate(90%) sepia(20%);
  filter: contrast(81%) brightness(53%) saturate(90%) sepia(20%);
}

.ls-heros-second {
  background: #f2f2e6;
}

.ls-footer-nav a:hover {
  text-decoration: underline;
}

.ls-success-other-users .ls-user-name:hoverr {
  color: #5c5c63;
  text-decoration: underline white;
}

.ls-cms-main-wrapper {
  background: #1e1e1e;
}

.ls-streamer-info-redifs:hover, .ls-chat-toolbar .nqtabs-list-item-active {
  background: #5c5c63;
  color: #aeaeae;
}

.ls-cms-rte ol {
  list-style-type: none;
}

.sprite-question-mark:before {
  content: '(?)';
  font-weight: bold;
  font-size: 13px;
}

.ls-btn-page {
  top: 1px;
}

.ls-pages ol {
  padding: 3px 15px;
  bottom: 3px;
}

.ls-s77 img {
  opacity: 0.8;
}

.ls-cms-rte li::before {
  content: counter(section);
  counter-increment: section;
  margin-right: 5px;
  font-size: 80%;
  background-color: #2f2f2f;
  color: #9a9a9a;
  font-weight: bold;
  padding: 3px 8px;
  border-radius: 3px;
}

ul[style*="list-style-type:square"] {
  display: none;
}

.ls-heros-listing li a:hover .ls-heros-listing-profile {
  color: #9a9a9a;
  text-decoration: underline white;
}

.ls-streamer-info-redifs, .ls-popin-badge-success {
  color: #d6d6d6;
}

.ls-streamer-my-videos .ls-suggestions-more .ls-btn {
  color: #d6d6d6;
  background: #F9433A;
  border-color: #F9433A;
}

.ls-chat-wrapper .nqtabs-viewport, .ls-btn-user, .ls-btn-contact-page {
  border: none;
}

.ls-filter .nqdropdownmenu li {
 z-index: 999;
}

.ls-profile-params-section .ls-form-radio label {
  position: relative;
  left: 4px;
  top: 2px;
}

.ls-heros-listing li a:hover {
  background: #0e0e0e;
}

.ls-heros-listing li:nth-child(2n+1) a {
  background: #383838;
}

.ls-heros-listing li:nth-child(2n+1) a:hover {
  background: #0e0e0e;
}

.ls-heros-user-position .ls-listing-table-cell {
  background: #393945;
}

.ls-btn-stack-end .ls-btn, .ls-heros-listing-pagination .ls-btn:last-child {
  background: #393945;
  color: #9a9a9a;
}

.ls-btn-stack-end .ls-btn:hover {
  color: #ffc000;
}

.ls-profile-history-avatar {
  position: relative;
  left: 8px;
}

.ls-streamer-live-meta {
  background: #3d3d3d;
}

.ls-success-tabs .nqtabs-list-item-active:hover {
  background: #212128;
}

.ls-stats-success-how::after {
  border-color: transparent #e5e5e5 transparent transparent;
}

.ls-profile-tabs .nqtabs-list-item-active {
  background: #212128;
  border: none;
}

.ls-profile-tabs .nqtabs-list-item:hover, .ls-profile-tabs .nqtabs-list-item:focus, .ls-success-tabs .nqtabs-list-item-active:focus {
  background: #1d1d23;
  color: #9a9a9a;
}

.ls-profile-tabs .nqtabs-list-item-active::after, .ls-best-viewers-content li::before {
  background: #9a9a9a;
}

.ls-filter .nqdropdownmenu-toggler:hover .ls-btn-arrow-bottom::before, .ls-filter .nqdropdownmenu-toggler:hover .ls-btn-arrow-bottom::after, .ls-filter .nqdropdownmenu-toggler:focus .ls-btn-arrow-bottom::before, .ls-filter .nqdropdownmenu-toggler:focus .ls-btn-arrow-bottom::after {
  background: #8a8a8e;
}

.ls-streamer-live-name {
  position: relative;
  left: 10px;
}

img[src*="https://lestream.fr/asset/image/c/1920x500/model/viewer/background.default.jpg"], img[src*="https://lestream.fr/asset/image/c/x200/model/viewer/background.default.jpg"] {
  -webkit-filter: contrast(109%) brightness(52%) saturate(102%) sepia(26%) grayscale(29%);
  filter: contrast(109%) brightness(52%) saturate(102%) sepia(26%) grayscale(29%);  
}

.ls-streamer-live-social {
  bottom: 5px;
  right: 5px;
}

.sprite-header-nav-facebook {
  background: url(https://puu.sh/uA1X3/d12402aa44.png) no-repeat;
  position: relative;
  top: 15px;
  left: 5px;
}

a:hover .sprite-header-nav-twitter, a:hover .sprite-header-nav-youtube, a:hover .sprite-header-nav-facebook, a:hover .sprite-header-nav-instagram {
  background-position: 0 0 !important;
}

.sprite-header-nav-twitter {
  background: url(https://puu.sh/uA2va/9d1e0364c6.png) no-repeat;
  position: relative;
  top: 12px;
}

.sprite-header-nav-twitch {
  right: 6px;
}

.sprite-header-nav-youtube {
  background: url(https://puu.sh/uA41P/521dd34c5b.png) no-repeat;
  position: relative;
  top: 16px;
  right: 5px;
}

.sprite-header-nav-instagram {
  background: url(https://puu.sh/uA4us/b81316e08d.png) no-repeat;
  position: relative;
  top: 12px;
  right: 2px;
}

.ls-header-nav-social .ls-header-nav-social-twitch:hover .sprite-header-nav-twitch {
  background: url(https://lestream.fr/dist/img/logo_twitch_purple.png) no-repeat;
  position: relative;
  top: 17px;
  left: -2px;
}

}
