css:
@-moz-document url-prefix("https://www.amazon.com/gp/dmusic"), regexp("https?:\\/\\/music\\.amazon\\..*") {
*, .ui-layout-content, #page-wrapper,.sharingContainer, #optionPanel ul {
    background-color: #1e212b !important;
}

#left-content *, #header *:not(.button):not(.suggestions-list), .icon-play, .albumArtWrapper *, #contextMenuContainer, .carousel * {
    background-color: unset !important;
}

#dragonflyTransport .playbackControlsView .scrubberBackgroundContainer .scrubberBackground {
    background-color: #fff !important
}

#dragonflyTransport .playbackControlsView .scrubberBackgroundContainer .scrubberRightLine {
    background-color: #53aef5 !important;
}

*, .listViewDurationContextButton .listViewContextButton.playerIconDotMenu, .optionSprite:before, .pausedButton, .playingButton {
    color: white !important;
}

.disabledOptionPanelLink {
    color: #949db3 !important
}

.button, .button *, .playListHeaderContextMenu {
    background-color: #252d40 !important;
    border-color: #151925 !important;
}

.trackPopularityContainer .unhighlighted {
    background-color: #121620 !important;
}

.playlistDetailsListItem:hover {
    background-color: #232a3b !important;
}

.playlistDetailsListItem:hover:not(.emptyRow):not(.header) *:not(.highlighted):not(.unhighlighted), .selectable.smartList:hover * {
    background-color: #252d40 !important;
    transition: none !important
}

.playingButton, .pausedButton, .duration .listViewContextButton.playerIconDotMenu, .libraryButton *, .libraryStatus *  {
    color: #fff !important;
}

.bottomNav:after {
    content: "Themed by Michael Tannenbaum";
    font-size: xx-small;
    bottom: 0;
    position: absolute;
    margin-left: 5px !important;
}

.buttonCenter, .decoration, a.btnText {
    border-color: white !important;
    background-color: #242c40 !important;
}

#contextMenu * {
    color: white !important;
}

.highlighted {
    background-color: #53AEF5 !important;
}

.shoveler .playerIconChevronRight, .shoveler .playerIconChevronLeft {
    border: 1px solid #fff;
}

.ap_popover {
    border: 1px solid #fff;
}

span.buttonText {
    margin-left: 1rem !important;
    padding: 0 !important;
}
}
