css:
@-moz-document domain("www.douyu.com") {
@media (min-width: 992px) {
    /* 加长弹幕框 */
    
    div#js-chat-cont {
        height: 620px !important;
    }
    /* 调整主播动态 */
    
    .live-room-normal-equal-right-item {
        margin-left: -255%;
    }
}
@media (min-width: 1600px) {
    /* 加长弹幕框 */
    
    div#js-chat-cont {
        height: 828px !important;
    }
    /* 调整主播动态 */
    
    .live-room-normal-equal-right-item {
        margin-left: -366%;
    }
}
.column-body {
    min-height: 0px !important;
}
a.comein-yuba-btn {
    display: none !important;
}
}

@-moz-document domain("www.douyu.com") {
/* 屏蔽大量无用元素 */
DIV[id="js-fans-rank"][class="fans-rank"],
DIV[id="js-live-room-recommend"][class="js-live-room-recommend"],
DIV[class="action-list fl"],
DIV[class="nobility-privilege"],
DIV[class="box5 task clearfix"],
DIV[class="column rec"],
.row,
.starshow,
#footer,
DIV[id="js-live-room-normal-equal-left"][class="live-room-normal-equal-left"] {
    display: none;
}
}
