css:
@-moz-document domain("wololo.net") {
	.col-2cr .main-inner {
		background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAVQAAAACCAMAAAAw7NTZAAAAA1BMVEUYGBgkOM8dAAAADklEQVR4AWMYEmAUjAIAAqoAAUwnAscAAAAASUVORK5CYII=) repeat-y left 0;
	}

	.col-2cl .main-inner {
		background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAVQAAAACCAMAAAAw7NTZAAAAA1BMVEUYGBgkOM8dAAAADklEQVR4AWMYEmAUjAIAAqoAAUwnAscAAAAASUVORK5CYII=) repeat-y right 0;
	}
	
	.s1 .sidebar-top, .s1 .sidebar-toggle, #nav-footer .nav > li > a:hover, #nav-footer .nav > li:hover > a, #footer-bottom #back-to-top, .comment-tabs li span, div.popular {
		background: #333 !important;
	}
	
	#dokuwiki__pagetools ul li a:hover {
		background-color: #333;
	}
	
	#mainmenu {
		background: #222 repeat-x left top;
	}
	
	.page-title, .sharrre .count, #commentform, .widget select, #nav-footer.nav-container, hr, .hr, .post-tags a, div.allpopular, ul.yiiPager .selected a, .portlet-decoration, .operations li a:hover, div.entry.themeform ul li a, div.entry.themeform p a, #dokuwiki__pagetools:hover ul, #dokuwiki__pagetools ul li a:focus, #dokuwiki__pagetools ul li a:active, .dokuwiki table.inline tr:hover th, .dokuwiki table.inline tr:hover td, #ez-side-nav ul li .ez-submenu ul li.ezCSS, .entry-inner h3 a {
		background: #222 !important;
	}
	
	body, #header, .themeform input[type="text"], .themeform input[type="password"], .themeform input[type="email"], .themeform textarea, #footer-bottom, .portlet-content, .entry .wp-caption, .author-bio, #dw__toc, th, #ez-sidebar.ezCSS, #ez-side-nav ul li a.ezCSS, #ez-header-wrapper {
		background: #111 !important;
	}
	
	.main, #page, #nav-header.nav-container, .dokuwiki div.page, .dokuwiki .pageId span, .dokuwiki div.breadcrumbs, input, textarea, button, select, optgroup, option, keygen, output, meter, progress, #ez-container.ezCSS, .gsc-input-box, #stylesheet_body {
		background: #000 !important;
	}

	#mainmenu ul li a:hover, #mainmenu ul li.active a, div.entry.themeform p em a {
		background: transparent !important;
	}

	
	/* colors */
	div.entry-inner p a, div.entry.themeform ul li a, div.entry.themeform p a, div.rxRWuDpxIamP p strong a  {
		color: #6c81db !important;
	}
		
	div.entry-inner p a:hover, div.entry.themeform ul li a:hover, div.entry.themeform p a:hover, div.rxRWuDpxIamP p strong a:hover {
		color: #404c80 !important;
	}
	
	a:hover {
		color: #888;
	}
	
	#stylesheet_body {
		color: #777;
	}
	
	.widget a:hover, .comment-tabs li a, .entry-inner h3 a {
		color: #666;
	}
	
	a, .author-bio .bio-name, .commentlist .comment-body p, .post-tags a, ul.yiiPager .hidden a, ul.yiiPager .selected a, ul.yiiPager .selected a, ul.yiiPager a:link, ul.yiiPager a:visited, .portlet-content, .operations li a {
		color: #aaa;
	}

	h1, h2, h3, h4, h5, h6, .widget > h3 a, .widget > h3 a:hover, .post-hover:hover .post-title a, .author-bio .bio-desc, .themeform label, .comment-tabs li.active a, #mainmenu ul li a:hover, #mainmenu ul li.active a, .dokuwiki .pageId span, .dokuwiki .page li .li, .dokuwiki .aside li .li {
		color: #bbb;
	}
	
	body, .alx-tabs-nav li.active a, #footer .alx-tabs-nav li.active a, .widget select, #nav-footer .nav li > a:hover, #nav-footer .nav li:hover > a, #nav-footer .nav li.current_page_item > a, #nav-footer .nav li.current-menu-item > a, #nav-footer .nav li.current-menu-ancestor > a, #nav-footer .nav li.current-post-parent > a, .post-tags a:hover, .portlet-content, .portlet-title, #dokuwiki__header h1 a, input, textarea, button, select, optgroup, option, keygen, output, meter, progress {
		color: #ccc;
	}

	.post-title a, .post-title, .alx-tab .tab-item-title a, .alx-tab .tab-item-comment a, .alx-posts .post-item-title a, .themeform input[type="text"], .themeform input[type="password"], .themeform input[type="email"], .themeform textarea, .themeform input[type="text"]:focus, .themeform input[type="password"]:focus, .themeform input[type="email"]:focus, .themeform textarea:focus, .operations li a:visited {
		color: #ddd;
	}
	
	
	/* borders */
	.alx-tabs-nav li a:hover, .comment-tabs li a {
		border-color: #666;
	}
	
	#nav-footer.nav-container {
		border-color: #444;
	}
	
	.alx-tabs-nav, .themeform input[type="text"]:focus, .themeform input[type="password"]:focus, .themeform input[type="email"]:focus, .themeform textarea:focus, div.view, ul.yiiPager .hidden a, #footer, .portlet-decoration, #header, .widget_archive ul li, .widget_categories ul li, .widget_links ul li, .widget_meta ul li, .widget_recent_comments ul li, .widget_recent_entries ul li, .widget_rss ul li, .widget_pages ul li a, .widget_nav_menu ul li a, .alx-tab li, .alx-posts li, .author-bio, .comment-tabs, th, td {
		border-color: #333 !important;
	}
	
	.author-bio:before, .author-bio:after {
		border-bottom-color: #333;
	}
	
	.sharrre .count:after, .widget select, #page, ul.yiiPager a:link, ul.yiiPager a:visited, .post-list .post-row, hr, .hr, #dw__toc, #dokuwiki__pagetools:hover ul, #dokuwiki__pagetools ul li a:focus, #dokuwiki__pagetools ul li a:active, input:active, input:focus, textarea:active, textarea:focus, select:active, select:focus, keygen:active, keygen:focus, #ez-sidebar #ez-search div.ez-google-box table.gsc-search-box td.gsc-input div.gsc-input-box, #ez-sidebar #ez-search div.ez-google-box table.gsc-search-box td.gsc-search-button input.gsc-search-button, #ez-side-nav ul li a.ezCSS, #ez-side-nav ul li .ez-submenu ul li a.ezCSS, .ezoic-wrapper .post-list .post-row, .ezoic-wrapper .themeform input[type="text"], .ezoic-wrapper .themeform input[type="password"], .ezoic-wrapper .themeform input[type="email"], .ezoic-wrapper .themeform textarea {
		border-color: #222;
	}

	.page-title, .themeform input[type="text"], .themeform input[type="password"], .themeform input[type="email"], .themeform textarea, #ezoic-content.ezCSS, .ezoic-wrapper hr, .ezoic-wrapper .hr {
		border-color: #111;
	}
	
	.dokuwiki div.page, .dokuwiki div.breadcrumbs, .dokuwiki div.breadcrumbs div:last-child, .dokuwiki div.breadcrumbs div:first-child, .dokuwiki div.breadcrumbs div:only-child, .dokuwiki .pageId span, input, textarea, button, select, optgroup, option, keygen, output, meter, progress, #ez-sidebar.ezCSS, #ez-sidebar div.social .box.ezCSS {
		border-color: #000;
	}
	
	.s1 .alx-tabs-nav li.active a, #footer .alx-tabs-nav li.active a, .comment-tabs li.active a {
		border-color: #ccc;
	}
	
	
	/* box shadows */
	.dokuwiki div.page, .dokuwiki div.breadcrumbs, .dokuwiki .pageId span {
		box-shadow: 0 0 .5em #000;
	}

  input, textarea, button, select, optgroup, option, keygen, output, meter, progress {
	  box-shadow: inset 0 0 1px #000;
  }
	
	#ez-sidebar.ezCSS {
		box-shadow: #000 4px 2px 6px -2px inset;
	}
	
	#dokuwiki__pagetools:hover ul, #dokuwiki__pagetools ul li a:focus, #dokuwiki__pagetools ul li a:active {
		box-shadow: 2px 2px 2px #222;
	}
	
	
	/* transition */
	.entry img:not(.emoji), .dokuwiki img.media {
		filter: brightness(25%);
		transition: cubic-bezier(.46,.03,.52,.96) 0.3s;
	}
	
	.entry img:not(.emoji):hover, .dokuwiki img.media:hover {
		filter: brightness(100%);
		transition: cubic-bezier(.46,.03,.52,.96) 0.3s;
	}
	
	.post-thumbnail img {
		filter: brightness(50%);
		transition: cubic-bezier(.46,.03,.52,.96) 0.3s;
	}

	.post-thumbnail img:hover {
		filter: brightness(105%);
		transition: cubic-bezier(.46,.03,.52,.96) 0.3s;
	}
}
