css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.halomaps.org") {
  /*/ Site Background /*/
  body, td, .LIGHT {
    color: rgb(200, 200, 200) !important;
    background-color: rgb(35, 35, 35) !important;
  }
  
  /*/ Google Translate gadget /*/
  .goog-te-gadget-simple {
    margin-right: 130px;
  }
  
  .goog-te-gadget-simple, .goog-te-gadget-simple goog-te-menu-value {
    color: inherit;
    background-color: inherit;
    border-color: rgb(25, 25, 25);
  }
  
  .goog-te-gadget-simple .goog-te-gadget-icon {
    background-size: 18px;
    background-position: 0px 0px !important;
    background-image: url("http://diylogodesigns.com/blog/wp-content/uploads/2016/04/google-logo-icon-PNG-Transparent-Background.png") !important;
  }
  
  body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(4) {
    display: none;
  }
  
  /*/ Topic & Forum Headers /*/
  .LIGHTHEADER:not(:nth-child(1)) {
    display: none;
  }
  
  tbody tr .HEADER {
    background-image: none;
    border: 1px;
    border-style: solid;
    border-color: black;
  }
  
  td.LIGHTHEADER:nth-child(1) {
    background-image: none;
  }
  
  td.LIGHTHEADER:nth-child(2) {
    display: inline-flex;
    background-image: none;
  }
  
  /*/ Message Profile /*/
  td.LIGHT > br:nth-child(2) { /* br (above profile name) */
    display: none;
  }
  
  body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) > img:nth-child(1) { /* empty .gif */
    display: none;
  }
  
  tr:nth-child(odd) td.LIGHT { /* Profile Area */
    padding-top: 5px;
    padding-left: 5px;
  }
  
  td.LIGHT span.SMALL { /* "Joined:" text */
    color: rgb(200, 200, 200) !important;
  }
  
  td.LIGHT > img:nth-child(7) { /* Avatar (not the .avatar class) */
    margin-top: 5px;
    border: 2px solid rgba(0, 0, 0, 0.3);
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.3);
  }
  
  body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) {
    background-color: rgb(50, 50, 50) !important;
  }
  
  /*/ Message Text Area /*/
  #messagearea {
    color: inherit;
    background-color: inherit;
  }
  
  td.LIGHT div.MID { /* Message Bar Header */
    color: rgb(200, 200, 200) !important;
    background-color: rgb(25, 25, 25) !important;
    border-color: rgb(200, 200, 200);
    border-width: 1px;
    border-radius: 0px;
    border-bottom-style: solid;
    border-top-style: solid;
  }
  
  .QUOTE {
    background-color: inherit;
    font-size: inherit;
    border-style: none;
    box-shadow: 0 0 6px 0 rgba(0, 0, 0, 0.75);
  }
  
  .QUOTE b {
    text-shadow: 0 0 2px rgba(0, 0, 0, 1);
  }
  
  .CODE {
    color: inherit;
    background-color: inherit;
    font-size: inherit;
    border-style: none;
    box-shadow: 0 0 6px 0 rgba(0, 0, 0, 0.75);
  }
  
  /*/ Site Buttons /*/
  td.LIGHT div.replybuttons
  {
    width: initial;
  }
  
  td.LIGHT div.replybuttons a:after, tbody tr:nth-child(1) > td:nth-child(2) > a:after { /* CSS Buttons */
    display: inline-block;
    float: right;
    color: rgb(200, 200, 200);
    background-color: rgb(50, 150, 200);
    padding: 8px 25px;
    margin: 4px;
    box-shadow: 0 0 4px 0 rgba(0, 0, 0, 0.75);
    text-decoration: none;
    text-align: center;
  }
  
  td.LIGHT div.replybuttons a:hover:after, 
  tr:nth-child(1) > td:nth-child(2) > a:hover:after { /* CSS Buttons Hover */
    background-color: rgb(70, 170, 220);
  }
  
  tr:nth-child(1) > td:nth-child(2) > a:nth-child(1):after { /* New Topic Button Text */
    content: "New Topic";
  }
  
  tr:nth-child(1) > td:nth-child(2) > a:nth-child(2):after {
    content: "New Post";
  }
  
  td.LIGHT div.replybuttons a:nth-child(1):after { /* Post Reply Button Text */
    content: "Post Reply";
  }
  
  td.LIGHT div.replybuttons a:nth-child(2):after { /* Quote Reply Button Text */
    content: "Quote Post";
  }
  
  td.LIGHT div.replybuttons a:nth-child(3):after { /* Edit Post Button Text */
    content: "Edit Post";
  }
  
  td.LIGHT div.replybuttons a > img, 
  tr:nth-child(1) > td:nth-child(2) > a > img { /* Default Site Buttons */
    display: none;
  }
  
  /*/ Links /*/
  A {
    color: rgb(50, 150, 200);
    text-decoration: underline;
  }
  
  A:hover {
    color: rgb(100, 200, 200);
  }
  
  A:visited {
    color: rgb(150, 100, 200);
  }
  
  A:visited:hover {
    color: rgb(200, 150, 200);
  }
  
  body {
    box-sizing: border-box;
  }
}
