/* 4chan Improve2 */
/* Based on 4chan Improvement with my tweaks. */

/* by: jerieljan */

/* New Features */
/* - Navigation is fixed on top.
   - Page Navigation follows suit.
   - a few other tweaks */

/* I suggest you take a look at the bottom part for extras. */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {
/* 4chan Improvement */

/* sets font */
body, span#navtop, td.replyhl, td.reply, a, a:hover, a.quotejs, a.quotejs:hover, a.quotelink, span.filesize {font:13px Trebuchet MS !important;} 

/* hides ads */
div.logo img{display:none !important;}
iframe{display:none !important;}
td.rules {display:none}

/* hr */
hr{display:none !important;}
form hr{display:block !important;}
hr{height:1px !important;border:none !important;background:#999 !important;}

/* rounded corners */
td.replyhl, td.reply{-moz-border-radius:4px !important;}

/* links */
a{text-decoration:none !important;}
a.quotejs:hover, a:hover{font-weight:normal !important;text-decoration:underline !important;}

/* removes ugly borders on page overview [0,1,2...] */
table,td{border:none !important;}


/* logo */
img + br {display:none !important;}



/* post area 
input,textarea{border:#aaa 1px solid !important;margin-right:2px !important;}
textarea{height:200px !important;}
div.postarea{position:fixed!important;width:300px !important;height:0px !important;opacity:0 !important;right:200px !important;}
div.postarea:hover{opacity:1 !important;}
td.postblock{vertical-align:top !important;font-size:10px !important;} */
}

/* blurry thumbnails fix */
img[md5] {
height: auto !important;
width: auto !important;
}

/* Improve 2.0 */

/* Taskbar (bottom) */
#navtop {
 position:fixed;
 border:#CCCCCC 1px solid;
 background:#CCCCFF;
 top:0px;
 right:0px;
 z-index:0;
 height:26px
}

#navtop:hover {
 opacity:1;
 background:#FFCCCC;
 z-index:2;

}
table.pages {position:fixed; top:26px; right:35%; z-index:1}
table.pages:hover {background:#FFCCCC !important}

/* Remove bottom navigation bar */
#navbot {display:none;}

/* Change formatting (optional) */
/* Quote Colors (default here is purple)
font.unkfunc {color:#CC33CC !important}
*/

/* Hovered Spoiler Tag Colors (default is inverse, white bg + black text)
span.spoiler:hover {background:#FFFFFF !important; color:#000000 !important}
*/
