css:
@-moz-document domain("www.pandora.com") {
body {
    background-image: none;
    background-repeat: no-repeat;
    background-position: 50% 50%;
    background: linear-gradient(328deg, #26ab89, #26ab34, #19bfd6, #25a58d, #2579a5, #246cc9, #23acb1, #23b171, #5fb123, #34a237);
    background-size: 2000% 2000%;
    -webkit-animation: Back 30s ease infinite;
    animation: Back 30s ease infinite;
    background-color: #0E77CC;
    background-attachment: fixed;
    overflow: hidden;
}
@-webkit-keyframes Back {
    0% {
        background-position: 0% 58%
    }
    50% {
        background-position: 100% 43%
    }
    100% {
        background-position: 0% 58%
    }
}
@keyframes Back {
    0% {
        background-position: 0% 58%
    }
    50% {
        background-position: 100% 43%
    }
    100% {
        background-position: 0% 58%
    }
}
#topnav {
    height: 100%;
}
#playerBar {
    background-color: rgba(0, 0, 0, 0.25);
    border-color: transparent;
    min-width: 0;
    height: 100%;
}
#playerBar .columns {
    display: table;
    margin: 0 auto;
    width: 100%;
}
#playerBar .rightcolumn {
    display: table-header-group;
    float: none;
    padding: 0;
    text-align: center;
    width: 100%;
}
#playerBar .middlecolumn {
    display: table-footer-group;
}
#playerBar .middlecolumn .buttons {
    width: 430px;
}
#playerBar .progress {
    width: auto;
    max-width: calc(100vh - 180px);
}
#playerBar .progress .remainingTime {
    padding-right: 6px;
}
#playerBar .progress .progressBar {
    margin: 0 auto;
}
#playerBar .thumbDownButton {
    margin: 6px 19px 0 100px;
}
#playerBar .nowplaying {
    display: table !important;
    margin: 12px auto;
    text-align: center;
    width: 100%;
    max-width: 800px;
}
#playerBar .rightcolumn .albumArt {
    margin: 0 auto;
    width: calc(100vh - 180px);
    height: calc(100vh - 180px);
    max-width: 100vw;
    max-height: 100vw;
}
#playerBar .rightcolumn .cd_icon {
    float: none;
    margin: 0;
    max-width: 100vw;
    max-height: 100vw;
}
#playerBar .playerBarArt {
    box-shadow: 0px 0px 15px 5px rgba(0, 0, 0, 0.25);
    position: relative;
    height: 100%;
    width: 100%;
}
#playerBar .rightcolumn .info {
    display: table-header-group;
    float: none;
    text-align: center;
    width: auto;
    text-shadow: 0px 0px 15px rgba(0, 0, 0, 0.75);
}
#playerBar .rightcolumn .info div:last-child {
    padding-bottom: 16px;
}
#playerBar .rightcolumn .info div {
    height: 24px;
}
#playerBar .rightcolumn .info a {
    font-size: 20px;
}
.dropdownmenu,
.leftcolumn,
.volumePosition,
#brandingBar,
#footer,
.playerByText,
.playerOnText,
#ad_container,
.contentnav,
.homeRightSide,
#promobox,
.platformPromo {
    display: none !important;
}
::-webkit-scrollbar {
    display: none;
}
.contentContainer {
    width: 100%;
    height: 100%;
}
#adLayout {
    margin: 0px;
    position: fixed;
    top: 0px;
    left: 0px;
    height: 100%;
}
#mainContentContainer {
    width: 190px;
    background-color: rgba(0, 0, 0, 0);
}
#topnav {
    pointer-events: none;
}
.buttons {
    pointer-events: auto !important;
}
.inactiveHoverArrow {
    display: none !important;
}
.options,
.promotedStation {
    display: none !important;
}
.stationListItem.selected {
    background-color: rgba(255, 255, 255, 0.5) !important;
    background-image: none !important;
}
.stationNameText,
#shuffleIcon,
.playerBarSong {
    color: #fff !important;
    font-family: arial !important;
    text-shadow: none;
}
.playerBarArtist,
.playerBarAlbum {
    color: #ccc !important;
    font-family: arial !important;
    text-shadow: none;
}
.stationContent,
.top,
.stationListHolder,
.jspContainer,
.jspPane,
#stationList,
.stationListItem,
#shuffleContainer,
.stationName,
#adLayout {
    background-color: rgba(10, 10, 10, 0) !important;
}
#mainContent,
.content {
    background-color: rgba(10, 10, 10, 0.25) !important;
    height: 542px !important;
}
.stationName_hover {
    background-color: rgba(255, 255, 255, 0.25) !important;
    -o-transition: .5s;
    -ms-transition: .5s;
    -moz-transition: .5s;
    -webkit-transition: .5s;
    transition: .5s;
}
.stationName {
    -o-transition: .5s;
    -ms-transition: .5s;
    -moz-transition: .5s;
    -webkit-transition: .5s;
    transition: .5s;
}
.fxCol-cont-1 {
    background: none;
}
.contentContainer {
    opacity: 0;
    -o-transition: .5s;
    -ms-transition: .5s;
    -moz-transition: .5s;
    -webkit-transition: .5s;
    transition: .5s;
}
.contentContainer:hover,
#mainContentContainer:hover {
    opacity: 1 !important;
}
.top {
    display: none;
}
.content,
#mainContent,
.home,
.stationContent {
    height: 100% !important;
}
.stationListHolder {
    height: calc(100% - 30px) !important;
}
.btn_bg_sort {
    background-color: rgba(50, 50, 50, 0.5) !important;
    background-image: none !important;
    color: #fff !important;
    text-shadow: none !important;
    -o-transition: .5s;
    -ms-transition: .5s;
    -moz-transition: .5s;
    -webkit-transition: .5s;
    transition: .5s;
}
.btn_bg_sort:hover {
    background-color: rgba(255, 255, 255, 0.5) !important;
    -o-transition: .5s;
    -ms-transition: .5s;
    -moz-transition: .5s;
    -webkit-transition: .5s;
    transition: .5s;
}
.btn_bg_sort.selected {
    background-color: rgba(255, 255, 255, 0.5) !important;
}
.jspDrag {
    background-color: rgba(255, 255, 255, 0.25) !important;
}
.jspDrag:hover {
    background-color: rgba(255, 255, 255, 0.5) !important;
}
.remainingTime,
.elapsedTime {
    display: none;
}
.progressMiddle {
    filter: brightness(0) invert(1) !important;
}
.progressBar {
    background-color: #444 !important;
}
.videoPlayer {
    display: none !important;
}
.jspContainer,
.jspPane,
#stationList,
.jspTrack {
    height: 100% !important;
}
}
