css:
@-moz-document domain("www.gamezer.com") {
#auth_form {
    width: 100%;
    max-width: 400px;
    min-width: 320px;
    min-height: 580px;
    margin-top: 0px;
    padding: 0px 0px;
}

#auth {
    top: 0;
    right: 0;
    min-height: 702px;
    max-height: 2000px;
    background-image: url("https://e.top4top.net/p_818cy00n1.png");
    background-size: cover;
}

#auth_logo {
    opacity: 0.9;
}
#auth_geo_content {
    color: #909090;
}

body {
    background-color: #101010;
    background-image: url(https://f.top4top.net/p_81867ixb2.png);
}

.size2 .centerBlock {
    background-color: rgba(60, 40, 171, 0);
}
#BLAZE_PreloaderBase {
    background-color: rgba(0, 0, 0, 0.3);
    ;
}
#BLAZE_PreloaderBar {
    background-color: #bfaf00;
}
.standby_overlay {
    background-color: #101010;
    background-image: url(https://f.top4top.net/p_81867ixb2.png);

}

.woody {
    background-color: rgba(25, 25, 25,0.3);
    background-size: 0px 0px;
    box-shadow: rgba(110, 110, 110, 0.8) 1px 1px 1px, rgba(110, 110, 110, 0.8) 1px 1px 1px inset;
    border-width: 0px;
}

#sign {
    color: #808080;
    background-color: rgba(99, 99, 99, 0.2);
}

#sign_login {
    background-color: #565656;
}
.switched #sign_login .sign_button_text, #sign_reg .sign_button_text {
    color: #fff;
}

.switched #sign_login {
    background-color: rgba(58, 58, 58, 0.5);
}

#sign_reg {
    background-color: rgba(58, 58, 58, 0.5);
}
.switched #sign_reg {
    background-color: #565656;
}
#sign_stats {
    color: #bdbdbd;
}
#sign_login .sign_button_text,
.switched #sign_reg .sign_button_text {
    color: #fff;
}

.authform .input {
    color: #c5c5c5;
    border: 1px solid #999;
}
.input_block.authform div {
    color: #797979;
}
#login_access_code_text {
    color: #353535;
    background-color: #eaeaea;
}
#sign a {
    color: #4c4c4c;
    background-color: #c5c5c5;
}
.desktop #sign a:hover {
    color: #7c7c7c;
    background-color: #d2d2d2;
}
input[type=checkbox] + label.signlabel {
    border: 1px solid #999;
    background-color: #dadada;
}
.desktop .button.ui:hover,
.desktop .button_block.ui:hover {
    background-color: #2b2b2b;
}
.button.ui,
.button_block.ui {
    background-color: #484848;
}
#sign_footer_info {
    color: #828282;
}
#sign_footer_info a {
    color: #828282;
}
.desktop #sign_footer_info a:hover {
    color: #bdbdbd;
}
#sign_standby .standby_container {
    border-color: #777;
}
#sign_standby .standby {
    border-top: 4px solid #777;
}
body.desktop::-webkit-scrollbar-thumb {
    background-color: #565656;
}
body.desktop::-webkit-scrollbar-track {
    background-color: #171717;
}
body.desktop::-webkit-scrollbar-track-piece {
    background-color: #171717;
}

/*====================|| Center Block ||====================*/
.DashItem {
    padding: 57px 40px 15px;
}
.DashItemBlock {
    color: #e0dfa2;
}
.centerBlock {
    background-size: cover;
    background-image: url(https://e.top4top.net/p_818ghwfe7.png);
    border-radius: 6px;
    background-position: center center;
    background-repeat: no-repeat;
}
.size3 .centerBlock {
    background-color: #151515;
}
.gamelist #DashSection_Background {
    opacity: 0.2;
}
#Block_FHeader {
    height: 40px;
    padding: 5px;
    display: none;
    z-index: 10;
    border-radius: 5px 5px 0 0;
    overflow: hidden;
    background: none;
    background-color: rgb(33, 33, 33);
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.4);
    -moz-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.4);
    -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.59);
}
.desktop .scroller::-webkit-scrollbar {
    display: none;
}
/*---<StartGame>---*/
.StartGame.InvitationGame {
    background-color: rgb(22,22,22);
    box-shadow: rgba(0, 0, 0, 0.4) 0px 1px 4px;
    border-color: #222;
}
.InvitationGame {
	background-color: #171717;
    color: #b7b7b7;
    font-size: 16px;
    border: 1px solid #000;
}
.InvitationGame:hover {
    border: 1px solid #444;
}
.InvitationContent select {
    background-color: rgb(25, 25, 25);
    color: rgb(230, 230, 230);
    background-image: url(https://f.top4top.net/p_818lxe211.png);
    border-color: rgb(0, 0, 0) !important;
}
.InvitationContent option {
    color: rgb(230, 230, 230);
    background-color: rgb(25, 25, 25);
}
.Dash_StartBlock_SinglePlayer,
.multiplayer .Dash_StartBlock_Multiplayer {
    background-color: rgba(68, 68, 68, 0.4);
    border: solid 1px #464646;
}
.Dash_StartBlock_Setup select {
    background-color: rgb(25, 25, 25);
    color: rgb(230, 230, 230);
    background-image: url(https://f.top4top.net/p_818lxe211.png);
    border-color: rgb(0, 0, 0) !important;
}
.Dash_StartBlock_Setup option {
    color: rgb(230, 230, 230);
    background-color: rgb(25, 25, 25);
}
.SButton {
    color: #afafaf;
    border: solid 1px #2c2c2c;
    background: rgba(63, 73, 131, 0.7);
    background: -webkit-linear-gradient(rgba(31, 31, 31, 0.7), rgba(58, 58, 58, 0.7), rgba(31, 31, 31, 0.7));
    background: -o-linear-gradient(rgba(31, 31, 31, 0.7), rgba(58, 58, 58, 0.7), rgba(31, 31, 31, 0.7));
    background: -moz-linear-gradient(rgba(31, 31, 31, 0.7), rgba(58, 58, 58, 0.7), rgba(31, 31, 31, 0.7));
    background: linear-gradient(rgba(31, 31, 31, 0.7), rgba(58, 58, 58, 0.7), rgba(31, 31, 31, 0.7));
}
.SButton:hover {
    color: #fff;
}
.BotOption div {
    background-color: rgba(66, 66, 66, 0.3);
    border-color: rgb(33, 33, 33);
}
.BotOption:hover div {
    border-color: rgb(55, 55, 55);
}
.DashSwitchBlockGummy {
    background-color: rgba(83, 82, 84, 0.2);
    border: solid 1px #353535;
}
/*---<Settings>---*/
.Dash_StartBlock_Title.setup {
    color: rgb(220, 220, 220);
    box-shadow: rgba(0, 0, 0, 0.4) 0px 1px 4px;
    background-color: rgb(30, 30, 31);
    border-color: rgb(78, 78, 78);
}
.DashSect {
    background-color: rgba(39, 39, 39, 0.75);
    border: 1px solid #444;
    box-shadow: 0px 2px 10px rgba(66, 66, 66, 0.4);
    -moz-box-shadow: 0px 2px 10px rgba(66, 66, 66, 0.4);
    -webkit-box-shadow: 0px 2px 10px rgba(66, 66, 66, 0.4);
}
.DashSect select {
    background-color: rgb(25, 25, 25);
    color: rgb(230, 230, 230);
    background-image: url(https://f.top4top.net/p_818lxe211.png);
}
.DashSect .border {
    border-color: rgb(0, 0, 0);
}
.DashSect option {
    color: rgb(230, 230, 230);
    background-color: rgb(25, 25, 25);
}

/*---<History>---*/

.HistoryItemContainer {
    border: 1px solid #4a4a4a;
    background-color: rgba(70, 69, 54, 0.2);
}

/*---<Account>---*/
.DA_Block {
    background-color: rgba(62, 62, 62, 0.48);
    color: rgb(226, 226, 226);
    border-color: rgb(82, 82, 82);
}
.DashSectionAccount .input {
    background-color: rgb(35, 35, 35);
    color: rgb(226, 226, 226);
    border-color: rgb(84, 84, 84);
}
.DashSectionAccount select {
    background-color: rgb(25, 25, 25);
    color: rgb(230, 230, 230);
    background-image: url(https://f.top4top.net/p_818lxe211.png);
    border-color: rgb(0, 0, 0);
}
.DashSectionAccount option {
    color: rgb(230, 230, 230);
    background-color: rgb(25, 25, 25);
}

/*---<RecordedGames>---*/

.input {
    color: #cccccc;
    background-color: rgba(101, 101, 101, 0.2);
}
/*---<Inventory>---*/

.InventoryItemContainer {
    border: 1px solid #4a4a4a;
    background-color: rgba(25, 25, 25, 0.7);
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.4);
    -moz-box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.4);
    -webkit-box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.4);
}
.IVContent b {
    color: #bfbb7f;
}
.IVExists {
    left: -1px;
    bottom: -1px;
    background-color: rgb(22, 22, 22);
}
.IVAct {
    background-color: #101010
}
.IVConfirmButton,
.IVCloseButton {
    background-color: #1d1d1d;
}
.IVConfirmButton {
    color: #337b3b;
}
.IVCloseButton {
    color: #d0d0d0;
}
/*---<Store>---*/

.StoreItemBlock {
    background-color: rgba(144, 144, 144, 0.2);
    color: rgb(212, 212, 212);
    border-color: rgb(51, 51, 51);
}
.StoreItem.Option .StoreItemBlock {
    background-color: rgba(144, 144, 144, 0.2);
    color: rgb(212, 212, 212);
    border-color: rgb(51, 51, 51);
}
.StoreItemBlockCompact {
    background-color: rgba(144, 144, 144, 0.2);
    color: rgb(212, 212, 212);
    border-color: rgb(51, 51, 51);
}
.DashStoreRedirect, .DashStoreGMPay {
	background-color: rgba(144, 144, 144, 0.2);
    color: rgb(212, 212, 212);
    border-color: rgb(51, 51, 51);
}

.StoreSummary {
    color: #67b17e;
}
.StoreOrderNum {
    color: #dcdcdc;
}
.order_warning {
    color: #daa458;
}
.StoreOrder .StoreItemBlock {
    color: #b3b3b3;
}
.clabel {
    background-color: rgba(00, 00, 00, 0.2);
    border-color: rgb(22, 22, 22) !important;
}
.clabel:hover {
    border-color: rgb(66, 66, 66) !important;
}
.StoreOrderGift .input {
    border: 1px solid #1b1b1b;
    color: #c5c5c5;
    background-color: rgba(22, 22, 22, 0.5);
}
#GMOrder_Confirm .input {
    border: 1px solid #1f1f1f;
    background-color: rgba(0, 0, 0, 0.19);
}
.CancelOrderButton {
    background-color: #34344c !important;
}

.SubmitOrderButton {
    background-color: #225029 !important;
}
.SubmitOrderInfo {
    color: #cacaca;
}
.button {
    color: #c7c7c7;
}
/*---<Help>---*/

#Dash_Section.help {
    background-color: rgba(99, 99, 99, 0.3);
}
.DashHelpContainer {
    color: #afafaf;
}
.DashSectionHelp .standby_container {
    border: 2px solid #a0a0a0;
}
.DashSectionHelp .standby {
    border-top: 4px solid #a0a0a0;
}

/*---<Forum>---*/
.ForumSection, .ForumTopic, .ForumPost {
    background-color: rgba(23, 23, 23, 0.7);
    border: 1px solid #3a3a3a;
}
.ForumPost {
    color: #dcdcdc;
    background-color: rgba(23, 23, 23, 0.7);
}
.ForumTopic {
    color: rgb(197, 197, 197);
}
.UserName {
    color: #949494;
}
.ForumInput_Title, .ForumInput_Post {
    border: 1px solid #313131;
    background-color: rgba(31, 31, 31, 0.5);
}
.ForumTimestamp {
    color: rgb(138, 138, 138);
}
.DashItemStar {
    top: 35px;
    right: 34px;
}
/*====================|| Left Block ||====================*/

/*<mainManu>*/

.leftBlock {
    border-radius: 0px;
    background-color: rgba(34, 34, 34, 0.45);
}
.RoomCategoryIcon {
    background-image: url(https://5.top4top.net/p_1270kqn621.png);
}
.RoomCategoryItem {
    background-color: #333;
	color: #e2e2e2;
    border-radius: 3px;
    margin-bottom: 3px;
}
.desktop .RoomCategoryItem:hover {
    background-color: #666 !important;
    border-radius: 3px;
}
#ExtraBlock_Best {
    background-color: #2c3152;
    border-radius: 3px;
    box-shadow: 0px 0px 3px rgba(188, 230, 255, 0.81);
}
.ExtraBlock_Title {
    color: #a8acc7;
    text-shadow: 0px 0px 5px rgba(0, 0, 0, 0.9);
}
#ExtraBlock_Switcher {
    background-color: #444444;
    color: #999;
    border-radius: 3px;
    text-shadow: 0px 1px 3px rgb(0, 0, 0);
}
#ExtraBlock_Top {
    background-color: #2c5246;
    border-radius: 3px;
}
.desktop .list_scroller::-webkit-scrollbar-track-piece {
    background-color: #111;
}
.desktop .list_scroller::-webkit-scrollbar-thumb {
    background-color: #333;
}
.desktop .list_scroller::-webkit-scrollbar {
    background-color: #080808;
}
#RoomMenuContainer {
        background-image: linear-gradient(rgba(41, 67, 201, 0), rgba(40, 44, 172, 0), rgba(15, 96, 196, 0));
    border-radius: 5px;
    color: #9c9c9c;
    background-color: #1d1d1d;
    border-color: #1d1d1d;
}
/*<SubManu>*/
.RoomItem {
    border-radius: 3px;
    background-color: #333;
}
.RoomItem_name {
    color: rgb(197, 197, 197);
}
.RoomItem_users {
    border-radius: 100px;
    color: #6b695a;
    text-shadow: 0px 1px 2px rgba(0, 0, 0, 0.5);
    background-color: #262626;
    border: solid 1px rgba(0, 0, 0, 0.3);
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.5);
}
.RoomItem_icon {
    opacity: 0.7;
}
.desktop .RoomItem:hover {
    background-color: #252525;
    box-shadow: 0px 0px 2px rgba(255, 255, 255, 0.5);
}
#CategoryBack {
    background-image: linear-gradient(rgb(217, 51, 212), rgb(109, 0, 96), rgb(217, 51, 212));
}
#CategoryBack:hover {
    background-color: #999 !important;
    border-radius: 5px 0px 0px 5px;
}
/*<UsersList>*/

#RoomQuit {
    background-color: #bf404c;
}
#RoomQuit:hover {
    background-color: #da4452 !important;
}
#RoomUserNum {
    width: 39px;
    height: 39px;
    color: #737373;
    background-color: #151515;
    border-radius: 40px;
    font-size: 15px;
}
.UserItem {
    background-image: url(https://d.top4top.net/p_818pz1wa1.png);
    background-color: #101010;
    border-radius: 0px;
    margin-bottom: 3px;
}
.UserBG {
    border-radius: 0px;
    background-color: #202020;
    background-blend-mode: luminosity;
    border: solid 1px rgba(0, 0, 0, 0.5);
}

.Info_TopPlayer {
    background-color: #202020;
    background-blend-mode: luminosity;
    color: #fff;
}
.UserAvatar {
    background-color: #151515;
}
.UserAvatarContainer {
    border-radius: 2px;
}
.UserGroup,.UserNick {
    float:left;
    position:relative;
    left: 85px;
}
.UserNick {
    width:180px;
    overflow:hidden;
}
.UserNick:before{
    content: url(https://a.top4top.net/p_818c760n9.png);
}
.UserFlag {
    z-index: 1;
}
.UserName {
    top: 30px;
    left: 92px;
}
.UserScore {
    color: #4ca76a;
    font-weight: 900;
}
.UserItemSB {
	background-image: url(https://d.top4top.net/p_818at0vz6.gif);
    border-radius: 0px;
}
.UserActSB {
    width:27px;
    height:27px;
    transition-property: transform;
    transition-duration: 0.1s;
     animation-name: rotate; 
    animation-duration: 2s; 
    animation-iteration-count: infinite;
    animation-timing-function: linear;
 }
@keyframes rotate {
    from {transform: rotateZ(0deg);}
    to {transform: rotateZ(360deg);}
}
.Switch_Room #RoomMenuContainer {
    color: #8e8e8e;
    background-color: rgb(33, 33, 33);
}
.desktop .Switch_Room #RoomMenuContainer:hover {
    color: #8e8e8e;
    background-color: rgb(55,55,55);
}

/*====================|| Right Block ||====================*/

.rightBlock {
    border-radius: 0px;
    background-color: #4a4a4a;
}
#TabContainer {
    background-color: #282828;
    border-radius: 0px;
}
#PanelRight {
    -moz-box-shadow: 0px 0px 0px 0px #d4ccff, 0px 0px 0px rgba(0, 0, 0, 0.5);
    -webkit-box-shadow: 0px 0px 0px 0px #d4ccff, 0px 0px 0px rgba(0, 0, 0, 0.5);
}
.Tab_Home #MenuTab_Home,
.Tab_Chat #MenuTab_Chat,
.Tab_Profile #MenuTab_Profile,
.Tab_Settings #MenuTab_Settings {
    background-color: #4a4a4a !important;
}
/*---<ProfileBG>---*/

.Profile_BGImage {
    background-color: #141414;
    background-blend-mode: luminosity;
}
.Profile_BGInfo {
    background-color: #141414;
}
/*---<Profile>---*/

#ProfileContent .SectionArea {
    background-color: #141414;
    background-blend-mode: luminosity;
}
.Profile_MottoInput input {
    background-color: rgba(36, 21, 78, 0);
    border-bottom: 1px solid rgb(68, 68, 68);
}
.Profile_StatusHighlighter {
    background-color: #434246;
}
.desktop .sec_scroller::-webkit-scrollbar {
    display: none;
}
.bg_select {
    color: #b5b5b5;
    background-color: rgb(22, 22, 22);
    background-image: url(https://f.top4top.net/p_818lxe211.png);
}
.bg_select option {
    background-color: rgb(22, 22, 22);
}

.Profile_ZPGroup {
    background-color: rgb(34, 96, 25);
    background-image: linear-gradient(rgb(69, 112, 73), rgb(34, 96, 25), rgb(74, 130, 51));
    border-color: rgb(81, 194, 67);
}
.Profile_PremiumGroup {
    color: #eed4ff;
    background-color: #4d1b6d;
    border-color: rgb(126, 60, 169);
}

.Info_StatsLeft {
    background-color: #1f7334;
    border-left: 4px solid #3c6f3a;
    border-right: 2px solid #3c6f3a;
}
.Info_StatsRight {
    background-color: #de9624;
    border-right: 4px solid #e69c26;
    border-left: 2px solid #e69c26;
}
/*---<Options>---*/
.SettingsButton {
    background-color: rgb(195, 125, 11);
}
.desktop .SettingsButton:hover {
    background-color: rgb(226, 144, 8);
}
.SectionArea {
    background-color: #222;
    color: #9e9e9e;
}
.SettingsRow select {
    background-color: rgb(25, 25, 25);
    color: rgb(230, 230, 230);
    background-image: url(https://f.top4top.net/p_818lxe211.png);
    border-color: rgb(0, 0, 0);
}
.SettingsRow option {
    color: rgb(230, 230, 230);
    background-color: rgb(25, 25, 25);
}
/*---<Chat>---*/

#RoomChatUpperLine {
    height: 4px;
    background-color: #4a4a4a;
}
#Block_Chat {
    background-color: #171c18;
    background-image: url(https://a.top4top.net/p_818x7zko3.png);
    background-repeat: repeat;
    border-radius: 0px;
}
.chat_ava {
    background-color: #444;
}
.room_chat_item,
.game_chat_item {
    background-color: rgba(0, 0, 0, 0.2);
}
.chat_content {
    color: #e8e8e8;
}
.room_chat_item.inprivate,
.game_chat_item.inprivate {
    background-color: rgba(24, 41, 41, 0.71);
}
.room_chat_item.inprivate .chat_content,
.game_chat_item.inprivate .chat_content {
    color: #caeeff;
}
.room_chat_item.outprivate,
.game_chat_item.outprivate {
    background-color: rgba(45, 28, 44, 0.58);
}
.room_chat_item.outprivate .chat_content,
.game_chat_item.outprivate .chat_content {
    color: #fbcdf9;
}
.desktop .chat_scroller::-webkit-scrollbar-thumb {
    background-color: #4e4e4e;
}
.desktop .chat_scroller::-webkit-scrollbar-track {
    background-color: rgba(37, 37, 37, 0.5);
}
#RoomChatControls {
    background: #101023;
    background: -webkit-linear-gradient(left, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: -o-linear-gradient(right, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: -moz-linear-gradient(right, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: linear-gradient(to right, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
}
#RoomChatPanel {
    background: #101023;
    background: -webkit-linear-gradient(left, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: -o-linear-gradient(right, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: -moz-linear-gradient(right, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: linear-gradient(to right, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    -webkit-transform: translate3d(0, 0, 0);
    box-shadow: 0px -1px 2px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: 0px -1px 2px rgba(0, 0, 0, 0.2);
    -webkit-box-shadow: 0px -1px 2px rgba(0, 0, 0, 0.2);
}
#RoomChatInput {
    background-color: rgba(20, 14, 25, 0);
    border-top: 1px solid #222;
    border-radius: 0px;
}
#RoomChatButton {
    background-color: rgba(50, 34, 94, 0);
}
#RoomPMInput, #RoomVMInput, #RoomIMInput {
    color: #fffde7;
    background-color: rgba(11,11,11, 0) !important;
}
