css:
@-moz-document url(https://controller.groupe-efrei.fr/101/portal/) {
    body{
	background-image: none !important;
	background:#151515 !important;
        overflow:hidden !important;
}

#reserved_block{
	margin-left:180px !important;
}

#container {
	background: transparent !important;
}

#header {
	margin-top:20px !important;
	opacity:1 !important; 
	background-image: url('http://www.paulbouloc.com/efrei/header.png') !important;
z-index:100 !important;
}

#editor_img_Logo-EFREI-GRP {
	display:none !important;
}

#lang_block{
	position: fixed !important;
	top:calc(100% - 74px) !important;
	left:calc(100% - 100px) !important;
	margin:0 !important;
	padding:0 !important;
}

#editor_text_banner {
	opacity: 0 !important;
}

#editor_img_slice {
	opacity: 0 !important;
}

#reserved_block{
	text-align: center !important;
	background: #202020 !important;
	border:none !important;
	-webkit-box-shadow: inset 0px 1px 0px 0px rgba(153,153,153,0.4) !important;
    -moz-box-shadow: inset 0px 1px 0px 0px rgba(153,153,153,0.4) !important;
    box-shadow: inset 0px 1px 0px 0px rgba(153,153,153,0.4) !important;
    color: rgba(255,255,255,0.7) !important;
}

#reserved_block a{
	color:rgba(255,255,255,0.4) !important;
	text-transform: uppercase !important;
}

#reserved_block button{
	background: rgb(50,50,50) !important;
	-webkit-box-shadow: inset 0px 1px 0px 0px rgba(153,153,153,0.4) !important;
    -moz-box-shadow: inset 0px 1px 0px 0px rgba(153,153,153,0.4) !important;
    box-shadow: inset 0px 1px 0px 0px rgba(153,153,153,0.4) !important;
    transition: 0.2s !important;
}

#reserved_block button:hover{
	background: rgb(40,40,40) !important;
}

#reserved_block input{
	padding:2px !important;
	margin-right: 75px !important;
	border-radius:3px !important;
	margin-left:10px !important;
	border:none;
	background:rgb(17,17,17) !important;
	-webkit-box-shadow: inset 0px -1px 0px 0px rgba(153,153,153,0.6) !important;
    -moz-box-shadow: inset 0px -1px 0px 0px rgba(153,153,153,0.6) !important;
    box-shadow: inset 0px -1px 0px 0px rgba(153,153,153,0.6) !important;
}

#reserved_block #logonForm_connect_button{
	margin-left: 35px !important;
}
}
