css:
@-moz-document url-prefix("https://github.com/") {
/* Octotree Sidebar Extension */

.octotree_sidebar,
.ui-resizable.octotree_github_sidebar {
  z-index: 150;
}
.octotree_toggle.btn {
  top: 12px !important;
  right: -43px !important;
}



/* Fixed Navbar */

.js-header-wrapper {
  z-index: 101;
  position: fixed !important;
  top: 0;
  left: 0;
  right: 0;
  box-shadow: 3px 2px 4px rgba(0, 0, 0, .3);
}
div[role="main"] {
  padding-top: 60px;
}


/* Profile Page */

.user-profile-mini-vcard.d-table,
.user-profile-nav.js-sticky.is-stuck,
.user-profile-sticky-bar.js-user-profile-sticky-bar.is-stuck::after {
	top: 53px !important;
}

.user-profile-sticky-bar.js-user-profile-sticky-bar.is-stuck::after {
	box-shadow: 0px 0px 5px 1px rgba(45, 45, 45, .2);
}

/* Code */

.highlight pre,
.blob-wrapper {
  background: #050505 !important;
  color: #fff !important;
}
.blob-num,
.pl-s1 .pl-s2,
.pl-smi,
.pl-smp,
.pl-stj,
.pl-vo,
.pl-vpf,
.blob-code,
.pl-mo,
.pl-v {
  color: #fff !important;
}
.pl-e,
.pl-en {
  color: #e57232 !important;
}
.pl-k,
.pl-s,
.pl-st {
  color: yellow !important;
}
.pl-pds,
.pl-s1,
.pl-s1 .pl-pse .pl-s2,
.pl-sr,
.pl-sr .pl-cce,
.pl-sr .pl-sra,
.pl-sr .pl-sre,
.pl-src {
  color: #32CD32 !important;
}
.pl-s3 {
  color: #56A0D3 !important;
}



/* Firacode Font */

@font-face {
  font-family: FiraCode;
  font-style: normal;
  font-weight: normal;
  src: url("https://github.com/tonsky/FiraCode/blob/master/distr/otf/FiraCode-Regular.otf?raw=true") format("opentype");
}
.blob-code-inner,
pre {
  font-family: FiraCode, Consolas, "Liberation Mono", Menlo, Courier, monospace;
}
}
