css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("craigslist.org"), domain("craigslist.co.uk") {

*{font-family:"calibri" !important}

body {background:#093155 !important;

color:#CFCBC2 !important;

background-position:center top !important;

background-repeat:repeat !important;

}



table{background-color:#093155 !important;

color:#6EBCFF !important;

width:100% !important;

}



.wwl td a, #leftbar, #postlks li, #leftbar li {background-color:#093155!important;

background-repeat:no-repeat!important;

border:1px solid #6EBCFF!important;

color:#000000!important;

font-size:10px!important;

padding:5px 7px 5px 14px!important;

-moz-border-radius:3px !important
}



.www td a, table.city, .hp a, #leftbar td{

background:#072541!important;

background-repeat:no-repeat!important;

color: #6EBCFF !important;

-moz-border-radius:3px !important

}



.www td a:hover, .hp a:hover {

background:#000000 !important;

cursor:pointer !important;

}



.www b , .city b, .ban, #topban div, #logo{

background:#093155!important;

border:1px solid #1879C9!important;

color:#6EBCFF!important;

cursor:pointer!important;

font-weight:bold!important;

-moz-border-radius:3px !important

}



blockquote{

background-color:#093155 !important;

color:#6EBCFF !important;



margin:0px !important;

}



a {

color:#6EBCFF !important;

text-align:center !important;

}



.bchead {

background:#072541 !important;

border-color:#093155 !important;

}



.toc h4, #search div {

background:#072541!important;

border:1px solid #1879C9!important;

color:#1879C9!important;

font-weight:bold!important;

-moz-border-radius:3px !important

}









}
