css:
@namespace "http://www.w3.org/1999/xhtml";

@-moz-document domain("poe.trade") {

	body {
		padding: 20px 0 20px 0 !important;
		background: #223 !important;
		background: -webkit-linear-gradient(#223,#000) !important;
		color: #999 !important;
		
	}
  
	#main {
		background:#334!important;
		border-radius:12px!important;
		border:1px solid #555!important
	}

	img:hover {
		-webkit-filter:brightness(200%)!important;
		-webkit-transform:scale(1.1,1.1)!important
	}

	code {
		color:#000!important
	}

	a[class="chosen-single"] {
		color:#fff!important
	}

	a[class*="button"] {
		background:#636!important;
		border:none!important;
		border-radius:6px!important;
		margin:3px!important;
		box-shadow:2px 2px 2px #000!important
	}

	#topone {
		display:none!important
	}

	input {
		border-radius:6px!important;
		border:1px solid #555!important;
		background:#223!important
	}

	input[class="search button"] {
		background:#f90!important;
		border:none!important;
		color:#000!important;
		box-shadow:2px 2px 2px #000!important;
		width:300px!important;
		float:right!important
	}

	input[class="num"] {
		color:#f9f!important;
		text-align:center!important
	}

	input[class*="sockets"] {
		font-weight:700!important;
		text-align:center!important
	}

	input[class*="sockets-r"] {
		color:#f00!important
	}

	input[class*="sockets-g"] {
		color:#0f0!important
	}

	input[class*="sockets-b"] {
		color:#0ff!important
	}

	input[class*="sockets-w"] {
		color:#fff!important
	}

	input[class*="nonempty"] {
		background:#556!important;
		text-align:center!important;
		font-weight:700!important
	}	

	input[name="name"],input[placeholder="search"] {
		background:#fff!important;
		color:#000!important;
		text-align:left!important;
		font-weight:700!important
	}

	div[class*="currency-selectable"] {
		background:#223!important;
		height:60px!important;
		width:60px!important;
		border-radius:6px!important;
		border-color:#445!important;
		padding-top:8px!important;
		font-size:10px!important;
    text-align:center!important;
		overflow:hidden!important;
	}

	div[class*="currency-selected"] {
		background:#f90!important;
		color:#000!important;
	}
	
	td tr {
		background:#223!important;
		padding:6px!important
	}

	td[class="table-stats"],td[class="third-cell"] {
		background: #222 !important;
		width: 1% !important;
	}
	
	td[class="table-stats"] > table > tbody {
		background: #f90 !important;
	}
	
	th[colspan="2"] {
		background: #556 !important;
	}
	
	td[data-name] {
		background: #334 !important;
		color: #fff !important;
	}
	
	td[data-name]:hover {
		background: #f90 !important;
		color: #000 !important;
		border-radius: 6px !important;
	}
	
	ul[class="proplist"] {
		background:#334!important;
		padding:6px!important;
		border-radius:6px!important;
		border:1px solid #555!important
	}

	h3[class="title"] {
		background:#223!important;
		padding:3px 0 2px 6px !important;
		border:1px solid #555!important;
		border-radius:6px!important;
		box-shadow:2px 2px 2px #000!important;
	}

	a[href*="currency.poe.trade"],span[class="backtopoetrade"] {
		background:#f90!important;
		border-radius:0 6px 6px 0!important;
	}

	span[class="backtopoetrade"] {
		padding: 0px 6px 0px 6px !important;
	}
	
	a[href="http://poe.trade"] {
		color: #000 !important;
	}
	
	td[class="cell-empty"],th[class="cell-empty"] {
		display: none !important;
	}
	
	td[class="table-stats"] {
		padding: 8px !important;
	}
	
	td[class="table-stats"] > table {
		background: #555 !important;
	}
	
	div[class*="offer-desc"] {
		padding:10px!important;
		font-size:1em!important;
		font-weight:700!important
	}

	div[class="displayoffer-middle"] {
		margin-top:6px!important
	}

	h5 {
		color:#fff!important;
		background:#556!important;
		padding:3px 0 3px 10px !important;
		border-radius:6px!important;
		box-shadow:2px 2px 2px #000!important;
		text-shadow:2px 2px 2px #000!important
	}

	span[class="found-time-ago"],a[class="wiki-link"] {
		padding:2px 12px 2px 0 !important
	}

	hr {
		visibility:hidden!important
	}

	div[style*="height"] {
		display:none!important
	}

	div[class*="box"] {
		background:#b55!important;
		border:none!important;
		padding:10px!important;
		margin:10px 0!important
	}

	label {
		color:#999!important
	}
}
