css:
@-moz-document domain("kshowonline.com") {
body {
    color: #fff !important;
}
.post-message-container .publisher-anchor-color .post-message p {
    color: #fff !important;
}
.white {
    background-color: #0e0e0e !important;
}
header {
    background-color: #161616;
}
#wrap {
    background-color: #161616;
}
nav ul,
nav ul a {
    color: #fff;
}
.content-list {
    background-color: #0e0e0e !important;
}
.content-list h5 {
    font-size: 18px;
    margin-left: 12px;
    color: #fff;
}
a {
    color: #fff;
}
.menu-left {
    background-color: #0e0e0e !important;
}
.input-field input[type=search] {
    background-color: #161616;
}
nav div > form {
    width: 300px;
    height: 53px;
    border-left: none;
    border-right: none;
}
.grey.lighten-5 {
    background-color: #161616 !important;
}
.grey {
    background-color: #161616 !important;
}
.input-field input[type=search]:focus {
    background-color: #161616;
}
.grey.darken-3 {
    background-color: #0e0e0e !important;
}
.content-list h5 a {
    color: #fff;
}
footer {
    border-top: 2px solid #0070C3;
}
.content-info {
    background-color: #0e0e0e !important;
}
.content-info {
    padding-top: 20px !important;
}
.content-left {
    width: 70% !important;
}
.post-content .post-message p:last-child {
    margin: 0;
    color: #fff !important;
}
.post-content .post-message p:last-child {
    margin: 0;
    color: #fff !important;
}
.comment__footer .vote-down,
.comment__footer a,
.comment__header .vote-down,
.comment__header a {
    color: #fff !important;
}
.panel-episode {
    background-color: #161616;
    border-radius: 0px;
    margin: 0;
}
.panel-episode {
    margin-top: 15px;
    border: solid 1px #272727;
}
.relative-eps {
    height: 32px;
}
.nextep .thumb {
    width: 100px;
    height: 56px;
    border: solid 1px #272727 !important;
}
.info tbody tr:last-child {
    display: none;
}
select {
    background-color: #161616;
    border: solid 1px #272727;
}
select:focus {
    outline: none;
}
.features .btn {
    color: #252525 !important;
}
.jw-skin-bekle:not(.jw-flag-time-slider-above):not(.jw-flag-audio-player):not(.jw-breakpoint-0) .jw-controlbar {
    bottom: 0em;
    max-width: none;
    margin: none;
    left: 0%;
    right: 0%;
    width: 100%;
}
.jw-skin-bekle .jw-background-color {
    background-color: #161616 !important;
}
.jw-skin-bekle:not(.jw-breakpoint-0) .jw-controlbar {
    border-radius: 0em;
}
.menu-info b font {
    color: #fff
}
.menu-left h5 {
    color: #0070C3;
}
.content-list .pagination .active {
    background-color: #0070C3;
}
b,
strong {
    font-weight: bold;
    color: #0070C3;
}
.content-right {
    margin-top: -10px;
}
.dropdown-content {
    background-color: #161616;
}
#dropdown1 {
    top: 53px !important;
}
nav .dropdown-content li>a {
    color: #fff;
}
.features .btn {
    color: grey !important;
    ;
}
.features .btn {
    background-color: rgba(37, 37, 37, 0.67) !important;
    color: #8A8A8A;
    border: solid 1px #272727;
}
.features .btn:hover {
    border: solid 1px #0070C3;
}
.content-right .menu-left:nth-child(1) {
    display: none
}
.row-item {
    display: none;
}
.banner-top {
    display: none
}
}
