@namespace url(http://www.w3.org/1999/xhtml);

/**** 2017-03-18 ****/

@-moz-document domain("twitch.tv") {
	/**** NOTIFICATION BETA ****/
	.warp__item--anchor-beta {
		background: #282828;
	}

	.notification-center__core--empty {
		background: #141414;
	}

	.notification-center, .notification-center__footer {
		background-color: #1E1E1E;
	}

	.notification-center__header, .notification-center__footer {
		border-bottom-color: #141414 !important;
		border-top-color: #141414 !important;
	}

	.theme--light .button--text {
		color: #C0A1FF;
	}

	.notification-center__header-text {
		color: #CFC3E4;
	}

	/**** Bordure Ã  gauche du chat ****/
	.chat-container {
		border-left: 0;
	}
}
