css:
@-moz-document domain("niebezpiecznik.pl") {
body { background-color: #fafafa;color:#666; }
body strong, body div.strong { color:#000; }
body div#page_wrap { background-position:130px 0 !important;background-image:none; }
body div#page_wrap:before { background: url(https://niebezpiecznik.pl/wp-content/themes/pyrmont-v2/images/bg.gif) 130px 0 no-repeat;content: "";display: block;z-index:-1;height:153px;width:1090px;position:absolute;-webkit-filter:brightness(350%);-moz-filter:brightness(350%);-o-filter:brightness(350%);-ms-filter:brightness(350%);filter:brightness(350%); }

body div#main_navi { background-color:#EEE;background-image: none;border: 1px solid #aaa;border-top: none;width: 958px; }
body div#main_navi ul.right li a,body div#main_navi ul.right li span  { color:#555; }
body div#main_navi ul.right li a img, body div#main div#comments h3 a img { background:#555;padding:2px;margin-top:0 !important;border-radius:50%;display:inline-block;vertical-align:text-top; } 
body div#main_navi+div { display:none; }

body div#container { margin-left:120px; }
body div#container #main { width:672px; }

body div#main div.post { background-color: #f0f0f0;border-color: #aaa;color:#444; }
body div#main div.post div.date { background-image:none;border: 1px solid #aaa;background: #fafafa;color: #666;height: 32px; }
body div#main div.post div.title h2 a, body div#main div.post h3 { color:#444; }
body div#main div.post div.entry a { color:#006dff; }
body div#main div.post div.entry p strong { color:#000; }
body div#main div.post div.entry .announcement-post>h4 { color:#000 !important; }
body div#main div.post div.entry blockquote { background: #fff;border-color: #bbb;padding: 15px;border-left: 15px solid #bbb;border-right: 1px solid #bbb;color: #666; }
body div#main div.post div.entry ul.similar-posts { margin-left:20px;margin-right:10px; }
body div#main div.post div.entry ul li, body div#main div.post div.entry ol li { list-style:outside; }
body div#main div.navigation a { color:#444;background:#e0e0e0;border-color:#999; }
body div#main div.navigation .left a:hover, body div#main div.navigation .right a:hover { color:#000;background:#bbb; }

body div#main div#comments h3, body div#main div#comments .add_your_comment a { color:#666; }
body div#main div#comments h3 a img { vertical-align: middle; }
body div#main ol.commentlist { color:#555; }
body div#main ol.commentlist li, body div#main ol.commentlist li ul.children li { background-color:#efefef;border-color:#999; }
body div#main ol li div.right div.commentmetadata, body div#main ol li div.right div.commentmetadata a { color:#444 !important; }
body div#main ol.commentlist li div.right p a { color:#006dff; }

body div#main div#respond { background-color:#eee;border-color:#999; }
body div#main div#respond h3 { color:#444; }
body div#main div#respond label.desc, body div#main div#respond form#commentform p { color:#444; } 
body div#main div#respond form#commentform p a { color:#006dff;font-weight:bold; }
body div#main div#respond form#commentform div.submit_button input.button { background-image:none;border:1px solid #444;color:#000;background:#fafafa; }
body div#main div#respond form#commentform div.submit_button input.button:hover { background:#000;color:#fafafa; }

body div#sidebar { background-color:#eee;border-color:#aaa;color:#666; }
body div#sidebar ul { background:none; }
body div#sidebar ul li h2, body div#sidebar ul li.widget h2 a { background:#e0e0e0;color:#000;font-weight:bold; }
body div#sidebar .textwidget p { color: #444; }
div#sidebar ul li.widget, div#sidebar ul li.widget_archive, div#sidebar ul li.widget_categories, div#sidebar ul li.widget_search, div#sidebar ul li.widget_tag_cloud div, div#sidebar ul li.widget_text { background-image:none; }
body div#sidebar ul li ul li { background-image:none;padding-left:10px }
body div#sidebar ul li ul li a { color:#444; }
body div#sidebar ul li ul li:hover { background-color:#e0e0e0; }
body div#sidebar ul li.widget_tag_cloud div a { color:#444; }
body div#sidebar .screen-reader-text { display:none; }

body div#footer { background-image:none;border-top:1px solid #aaa;background-color:#eee; }
body div#footer .footer_wrapper a { color:#666; }
body div#footer .footer_wrapper a:hover { color:#000; }

body div.info { background-color:#e0e0e0;color:#444; }
body div.info strong { color:#000; }

body div#main .promobox { margin-bottom:20px; }
body div#main .promobox ul li a { color:#006dff; }
}
