@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(https://login.yahoo.com/config/),
               url-prefix(https://login.yahoo.com/config/login_verify)
{
 *[class*="deepblue"],
 *[id*="yregiclst"],
 *[id*="yregintusr"],
 *[class*="second yregbx"], 
 *[id*="yregft"],
 *[class*="yregnewssl"],
 *[class*="yreglgmd"],
 *[src*=".gif"],
 *[id*="ygmaproplinks"],
 *[class*="yregdsilu"],
 *[class*="yreglgsub"],
 *[id*="yregbnrt"],
 *[id*="yregbnrti"],
 *[id*="yregbnrtii"] *[id*="yregbnr"],
 *[alt="Filter"],
 *[class*="purple"],
 *[class*="lblue"],
 *[class*="oxblood"],
 *[class*="yellow"],
 *[class*="bluegreen"],
 *[class*="rootbeer"],
 *[class*="lgreen"],
 *[id*="yregtxt"],
 #flashCache,
 #rcta,
 #yregwp > div:not([id]),
 #yschlf,
 #yschrt,
 .ct, .cl, .cb
  {
    display: none !important;
  }

 *
  {
    background: #222 !important;
  }
 .yregbx,
 .yregbx *
  {
    background: #333 !important;
    color: #bbb !important;
  }
 input, select, textarea
  {
    border: #000 1px solid !important;
  }
 .yregbx
  {
    border: 1px solid #000 !important;
    margin: 3px 5px !important;
    -moz-border-radius: 7px !important;
  }
 #yreglg
  {
    float:inherit !important;
  }
 #yregwp
  {
    width: 240px !important;
  }
}

@-moz-document url-prefix(https://login.yahoo.com/config/),
               url-prefix(https://login.yahoo.com/config/login_verify),
               domain("address.yahoo.com"),
               domain("edit.yahoo.com"),
               domain("mail.yahoo.com"),
               domain("open.login.yahoo.com")
{

/* Global */
 *
  {
    border-color: #000000 !important;
    color: #1879C9 !important;
    -moz-appearance: none !important;
  }
 html
  {
    overflow-x: hidden !important;
  }
 html,
 body,
 [bgcolor]
  {
    background: #000000 !important;
font-family:"Calibri" !important
  }
 html,
 body
  {
    margin: 0 !important;
    padding: 0 !important;
  }

/* Form Elements */
 button,
 input:not([type="hidden"]),
 select,
 textarea,
 .button
  {
    border: 1px solid #bbb !important;
    background-color: #444 !important;
    color: #fff !important;
    margin: 2px !important;
    -moz-border-radius: 0 !important;
  }
 button,
 input[type="button"],
 input[type="submit"],
 .button
  {
    background: #000000  !important;
    background-repeat: repeat-x !important;
    border-color: #1879C9  !important;
    padding: 0px !important;
  }
 input[type="password"],
 input[type="text"],
 input:not([type])
  {
    background: #000000  !important;
    background-repeat: repeat-x !important;
    border-color: #ffffff !important;
    padding: 2px !important;
  }
 input[type="password"],
 input[type="text"],
 input:not([type]),
 textarea#tofield
  {
    background: #093155 !important;
    background-repeat: repeat-x !important;
    border-color: #1879C9 !important;
    color: #ffffff !important;
  }
 input[type="checkbox"],
 input[type="radio"]
  {
   margin: 0 2px !important;
  }
 button:hover,
 input:not([type="hidden"]):hover,
 select:hover,
 textarea:hover
  {
    border: 1px outset #bbb !important;
    color: #000 !important;
  }
 select:hover,
 textarea:hover
  {
    background-color: #222 !important;
  }
 button:hover,
 input[type="radio"]:hover,
 input[type="submit"]:hover,
 select:hover
  {
    cursor: pointer !important;
  }
 button:active, 
 input:not([type="hidden"]):active, 
 select:active
  {
   background-color: #000 !important;
   border: 1px inset #bbb !important;
  }
}


@-moz-document domain("address.yahoo.com"),
/* coming soon              domain("calendar.yahoo.com"),
               domain("edit.yahoo.com"),
               domain("notepad.yahoo.com"),
*/               domain("mail.yahoo.com"),
               domain("open.login.yahoo.com")

{
/* Yahoo Mail Classic */

/* Destroy */
 [id$="_ad"],
 [style*="url(http://ads.yimg."],
 div[style*="padding: 4px 0pt 4px 17px;"][style*="cursor: pointer;"][style*="white-space: nowrap;"][style*="font-weight: bold;"][style*="color: rgb(129, 0, 159);"],
 img[alt="Yahoo! Mail"][height="33"],
 img[src^="http://richmedia.yimg.com/customer/"][alt="click here"][height="600"][width="425"],
 img[src$="/ma_ab_1.gif"],
 img[src$="/qb_smdiagram1.gif"],
 img[src$="/tr14x15_1.gif"],
 img[width="1"][height="1"],
 table.applicationcontainer
  > tbody > tr > td.content 
  > table > tbody > tr > td:first-child 
  > br + table[width="100%"],
 td[width="300"]:not(:first-child),
 #AvtTD,
 #ecardpromo,
 #mailoptions > tbody > tr > td.last,
 #MWA2,
 #northbanner,
 #nwad,
 #rcta,
 #swads,
 #ygmafrm,
 .advertisement,
 .ad_slug,
 .bgc > td > img,
 .content > table:last-child > tbody > tr:last-child > td:not(:first-child),
 .modulecontainer > div.first,
 .modulecontainer > div.last,
 .ne, .nw, se, .sw,
 .rounded > span.bl,
 .rounded > span.br,
 .rounded > span.tl,
 .rounded > span.tr,
 .sidebar > div.first,
 .sidebar > div.last,
 .simplemodule > .sft,
 .simplemodule > .shd,
 .splittertop,
 .switchbox
  {
    display: none !important;
  }
 input[style="border: 0pt none ; background-color: transparent; width: 1px;"]
  {
    visibility: hidden !important;
  }
 a.divider,
 #bd,
 #bd > div,
 #composepage,
 #content,
 #doc3,
 #first,
 #leftNav,
 #leftnavspacer,
 #maincontent,
 #messages,
 #misc,
 #options > li,
 #pri,
 #priwrap,
 #refinementcontrols,
 #sec,
 #sec > ul,
 #spacer,
 #stateindicator,
 #thinBlueBox,
 #ygma,
 #ygmamu,
 #ygmasrchfrm,
 .applicationcontainer,
 .bd,
 .blueBoxRCTop,
 .composeview,
 .composeview table,
 .composeview tbody,
 .composeview td,
 .composeview tr,
 .content,
 .contentbuttonbar,
 .errorpage,
 .ft,
 .ft > div,
 .ft > div > ul,
 .hd > h2,
 .hdrCont,
 .imageinfo > div,
 .infomodule td,
 .infomodule tr,
 .modulecontainer div:not(.icon):not(.replyicon),
 .msgbox,
 .options,
 .propertylist,
 .roundcorner,
 .sendheaderBg,
 .sidebar div ul li,
 .sprite_bg,
 .tabtitlerow,
 .top,
 .top *,
 .tosBody,
 .yui-b,
 .yui-t1
  {
    background: transparent !important;
    border: none !important;
  }

/* Fix padding */
 #content,
 #maincontent,
 .content
  {
    padding: 0 !important;
  }
 #content,
 #maincontent,
 #applicationcontainer,
 .applicationcontainer,
 .content,
 div[style^="margin-right:"]
  {
    margin: 0 !important;
    width: 100% !important;
  }
 #pri,
 #applicationcontainer,
 .applicationcontainer
  {
    padding: 0 10px 0 0 !important;
  }
 #contact_actions > small + div
  {
    padding: 10px 0 0 0 !important;
  }
 #contact_actions
  {
    margin: -5px 0 0 0 !important;
  }

 #bd,
 #doc3,
 #mg_footer,
 #newsmodule,
 .msgheader,
 .roundcorner
  {
    margin: 3px !important;
    padding: 2px !important;
    width: auto !important;
  }

 .hd
  {
    min-height: 1em !important;
  }

 #pageContent,
 #pageContent > div,
 #pageContent > div > div,
 #doc3, .yui-t1
  {
    width: 95% !important;
  }

 .bottompagination > .pagination_right
  {
    margin-top: 2em !important;
  }
 .bottompagination > .pagination_left
  {
    margin-top: 4em !important;
  }

 #newscat > li,
 #ygmahelpmenu
  {
    background: #000000 !important;
    border: 1px solid #000 !important;
  }

 #newscat > li.selected > a > div
  {
    background: #111 !important;
    border: 1px solid #ccc !important;
  }

/* Header navigation bar */
 #ygmatop,
 .ygmabk
  {
    background: #000000 !important;
    border: none !important;
    border-bottom: 1px solid #000 !important;
    margin: 0 !important;
    padding: 3px !important;
  }
 #ygmatop > ul
  {
    margin: 0 !important;
    padding: 0 !important;
  }
 #ygmabot
  {
    background: #000000 !important;
    border: none !important;
    border-bottom: 2px solid #000 !important;
    margin: 0 0 10px 0 !important;
    padding: 3px !important;
  }
 .ygmatcrn
  {
    background: #444 !important;
    border: none !important;
    border-left: 1px solid #000 !important;
    margin: 0 !important;
    padding: 3px !important;
  }
 .ygmacrn
  {
    background: #444 !important;
    border: none !important;
    border-bottom: 1px solid #000 !important;
    border-left: 1px solid #000 !important;
    margin: 0 !important;
    padding: 3px !important;
  }

/* Tab bar */
 #pimtabs,
 #pimnav,
 #pimtabs > li > a,
 #pimnav > div,
 #pimnav > div > ul > li,
 #topnav > div,
 #topnav,
 #topnav > div > span
  {
    background: transparent !important;
    border: none !important;
  }
 #topnav
  {
    margin: 0 5px 5px 5px !important;
  }
 #pimtabs > li,
 #pimnav > div > ul > li,
 #topnav > div > span
  {
    margin: 0 3px !important;
  }
 #categorylistitems > li,
 #networkcatlistitems > li,
 #pimtabs > li,
 #pimnav > div > #apps > li,
 #topnav > div > span > span,
 #ygmamu > li,
 .bd > tbody > .msgolds,
 .buttonmenu > ul > li,
 .shortcuts > div > ul > li
  {
    background: #093155 !important;
    background-repeat: repeat-x !important;
    border: 1px solid #1879C9 !important;
  }
 #categorylistitems > li:hover,
 #categorylistitems > li.selected,
 #networkcatlistitems > li:hover,
 #networkcatlistitems > li.selected,
 #pimnav > div > #apps > .selected,
 #pimtabs > li.selected,
 #topnav > div > .selected > span,
 #ygmamu > li:hover,
 .buttonmenu > ul > li:hover,
 .shortcuts > div > ul > li:hover,
 span.yshortcuts:hover,
 .focus
  {
    background: #093155 !important;
    background-repeat: repeat-x !important;
    border: 1px solid #1879C9 !important;
  }
 .shortcuts > div > ul > li
  {
    padding: 2px !important;
  }
 #ygmamu > li
  {
    margin: 0 !important;
    padding: 0 !important;
  }
 #ygmamu > li > a
  {
    margin: 0 !important;
    padding: .25em .5em !important;
    text-decoration: none !important;
  }

 #composebuttonbarbottom,
 #composebuttonbartop,
 #doc,
 #globalbuttonbarbottom,
 #globalbuttonbartop,
 #searchmessageview > .globalnavigation,
 .alertbox,
 .contentnav,
 .globalnavigation,
 .messageinfo,
 .splittercontent
  {
    background: #000000 !important;
    border: 1px solid #093155 !important;
    height: auto !important;
    margin: 3px 5px !important;
    padding: .3em !important;
    width: auto !important;
    -moz-border-radius: 7px !important;
  }
 .contentnav
  {
    line-height: 1.5em !important;
    min-height: 2em !important;
    margin: .25em 0 !important;
    -moz-border-radius: 5px !important;
  }
 .alertbox,
 .contentnav,
 .messageinfo
  {
    padding: .3em .75em !important;
  }
 .contentnav > .first
  {
    margin-left: .75em !important;
  }
 .contentnav > .last
  {
    margin-right: .75em !important;
  }

 table.applicationcontainer td.content div.contentbuttonbar,
 .taskbar
  {
    background: none !important;
    border: none !important;
    margin: 3px 5px !important;
    padding: 5px 5px 10px 5px !important;
  }

/* Navigation sidebar */
 .modulecontainer.sidebar div ul li
  {
    margin: 1px 0 3px 0 !important;
  }
 .modulecontainer.sidebar > div:not([class]),
 .simplemodule
  {
    background: #000000 !important;
    border: 1px solid #093155 !important;
    padding: 5px 1px !important;
    -moz-border-radius: 7px !important;
  }
 #customfolders
  {
    border-top: 1px dotted #000 !important;
  }

 .customfolder,
 .listings li
  {
    background: transparent !important;
    border: 1px solid transparent !important;
  }

/* Page content */

 .content .modulecontainer.nomessages > div > div,
 .fieldset,
 .msgheader,
 .lightfill
  {
    background: #000000 !important;
    border: 1px solid #093155 !important;
    padding: 5px !important;
    -moz-border-radius: 7px !important;
  }

 .content .modulecontainer div.toptitle,
 .hd,
 .tabfoldertitle
  {
    background: #555 !important;
    border: 1px solid #000 !important;
    margin: 0 !important;
    padding: 5px 1px !important;
    -moz-border-radius-topleft: 7px !important;
    -moz-border-radius-topright: 7px !important;
  }
 #contact_details,
 #refinementoptions,
 .content .modulecontainer div.dashcontent,
 .imageinfo
  {
    background: #000000 !important;
    border-bottom: 1px solid #093155 !important;
    border-left: 1px solid #093155 !important;
    border-right: 1px solid #093155 !important;
    margin: 0 !important;
    padding: 3px !important;
    -moz-border-radius-bottomleft: 7px !important;
    -moz-border-radius-bottomright: 7px !important;
  }

 #simpleview.minwidth #propertysheetwrapper,
 #simpleview.minwidth #propertysheetwrapper #propertysheet,
 #simpleview.minwidth table,
 #simpleview.minwidth .details_width_hasads
  {
    width: 100% !important;
  }

 .alertbox *,
 .messageinfo *
  {
    border: none !important;
  }

 .msgheader,
 table.applicationcontainer td.content .contentnav
  {
    padding: .3em !important;
    text-align: right !important;
  }

/* View menu */
 #messageviewselector
  {
    background: #093155 !important;
    background-repeat: repeat-x !important;
    border: 1px solid #000 !important;
    height: 1.5em !important;
    padding: .1em 5px .1em .25em !important;
    text-decoration: none !important;
  }
 #messageviewselector:after
  {
    content: url(http://us.i1.yimg.com/us.yimg.com/i/us/pim/r/medici/all/bt_dd_2.gif) !important;
  }
 .applicationcontainer td.content > .infomodule,
 .buttonmenu,
 .shortcuts
  {
    background: #000000 !important;
    border: 1px solid #000 !important;
  }
 #messageviewmenu,
 #messageviewmenu > ul
  {
    border: 0 !important;
    padding: 0 !important;
  }
 #messageviewmenu li:hover a
  {
    color: #000 !important;
  }

/* Message Lists */
 .messageheader
  {
    border-spacing: 3px !important;
  }
 .messageheader td
  {
    background: #3E3D39 !important;
    border: 1px solid #242422 !important;
    color: #ccc !important;
  }
 .messageheader td.label
  {
    background: #5A5950 !important;
    border: 1px solid #3D3C38 !important;
    color: #ccc !important;
  }
 #datatable,
 .datatable
  {
    border: 1px solid #000 !important;
  }
 .datatable.snippets
  {
    margin-top: 1em !important;
  }
 .datatable > caption
  {
    background: #333 !important;
    border: 1px solid #000 !important;
    font-size: 10pt !important;
    height: 2em !important;
    margin: .25em 0 .25em 0 !important;
    padding: .25em !important;
    width: auto !important;
  }
 #datatable > thead > tr > th,
 .datatable > thead > tr > th,
 .options > h3,
 .tbldata > thead > tr > th,
 #mailoptions > tbody > tr:first-child > td > h3,
 th[id^="yab_kiosk_header_"]
  {
    background: #000000 !important;
    border: 1px solid #000000 !important;
    margin: 3px !important;
    padding: 3px !important;
  }
 #datatable > thead > tr > th > a,
 .datatable > thead > tr > th > a
  {
    color: #ddd !important;
  }
 #datatable > tbody > tr:not(:first-child) > td
  {
    border-top: 1px dotted #000 !important;
  }
 tr.listA td,
 .datatable > tbody > tr > td,
 #datatable > tbody > .msgold > td
  {
    background: #093155 !important;
  }
 tr.listB td,
 .datatable > tbody > .unread > td
  {
    background: #69675C !important;
    color: #000 !important;
  }
 #datatable > tbody > .msgnew > td
  {
    background: #354242 !important;
    color: #BBC2B4 !important;
  }
 #datatable > tbody > .selected > td,
 #datatable > tbody > .selected > td a
  { 
    background: #000000 !important;
    color: #141414 !important;
  }
 .datatable > tbody > tr.snippet > td
  { 
    background: #444 !important;
    border-bottom: 1px solid #000 !important;
    border-left: 1px dashed #000 !important;
    border-right: 1px dashed #000 !important;
    border-top: 1px dashed #000 !important;
    padding: .25em !important;
  }
 .datatable > thead > tr > th.sort,
 #datatable > tbody > tr > td.sortcol,
 #datatable > thead > tr > th.sortcol
  { /* keep this below the others */
    background: #093155 !important;
    border: 1px solid #000000 !important;
    color: #1879C9 !important;
  }

/* Mail Compose */
 #RTEContainer
  {
    background: #666 !important;
    border: 1px solid #000 !important;
  }
 .composeview .fieldset > table
  {
    width: 100% !important;
  }
 .composeview > tbody > tr > td > .fieldset > table > tbody > tr > td > table,
 textarea#bodyfield
  {
   width: 100% !important;
   padding-bottom: 0 !important;
   padding-right: 0 !important;
  }
 .composeview > tbody > tr > td > .fieldset > table > tbody > tr > td > label,
 .composeview > tbody > tr > td > .fieldset > table > tbody > tr > td > #SA
  {
   padding-left: 1em !important;
   vertical-align: middle !important;
  }
 .composeview > tbody > tr > td > .fieldset > table > tbody > tr > td:first-child
  {
   width: 5em !important;
  }

/* Mail Read */
 blockquote[style*="border-left: 2px solid"],
 .replbq
  {
    border-left: 2px dotted #000 !important;
    width: auto !important;
  }
 span.yshortcuts
  {
    border: 1px solid transparent !important;
    line-height: 1.75em !important;
    padding: 2px !important;
  }

}

@-moz-document domain("address.mail.yahoo.com")
{

 .clearfix
  {
    display: none !important;
  }
 body
  {
    overflow-x: hidden !important;
  }
 body > form
  {
    margin: 0 !important;
    padding: 0 !important;
  }


}
