css:
@-moz-document url-prefix("https://www.linux.org.ru/") {
    html,
    body {
        --bg-color: #222222;
        --darker-bg-color: #111111;
        --fg-color: #eff0f1;
        --link-fg-color: #2980b9;
        --border-color: #565656;
        --accent-bg-color: #f67400;
        --accent-fg-color: #fdbc4b;
        --table-hover-color: #575B5F;
        color: var(--fg-color);
        background-color: var(--bg-color) !important;
    }
    .messages .msg,
    .infoblock,
    .boxlet,
    #user-menu,
    #contents,
    #ft-back-button,
    ul.tab-menu li,
    #nav-menu,
    .page-number,
    .btn,
    .btn-default,
    .message-table .tag {
        background-color: var(--bg-color) !important;
        color: var(--fg-color) !important;
        border-color: var(--border-color) !important;
        border-style: solid !important;
        border-width: 1px !important;
        border-radius: 1px !important;
        padding: 1em;
    }
    #ft {
        padding: 1em;
        border-top-style: solid;
        border-color: var(--border-color);
        border-top-width: 1px !important;
    }
    .message-table a {
        color: var(--fg-color) !important;
    }
    .page-number {
        padding: 0.5em;
    }
    .message-table .tag {
        padding: 2px 4px;
    }
    #nav-menu ul {
        border-bottom: 1px solid var(--link-fg-color) !important;
    }
    .code,
    #content-article pre,
    #toc {
        background-color: var(--bg-color) !important;
    }
    a,
    #hd .menu a {
        color: var(--link-fg-color) !important;
    }
    h1 a,
    h2 a,
    td a,
    .nav a {
        color: var(--link-fg-color) !important;
    }
    #bd a.tag,
    .message-table .tag {
        color: var(--accent-fg-color) !important;
    }
    #bd h2 * {
        color: var(--fg-color);
    }
    #bd .message-table tbody tr:hover {
        background-color: var(--table-hover-color) !important;
    }
    .group-label {
        background-color: var(--bg-color) !important;
    }
    .messages .msg:target {
        border: 1px solid var(--accent-bg-color);
    }
    .stars,
    #bd .sign a {
        color: var(--accent-fg-color) !important;
    }
    #bd .btn-default,
    #bd .nav li a {
        color: var(--fg-color) !important;
        background-color: var(--bg-color) !important;
    }
    #bd .btn-selected {
        color: var(--link-fg-color) !important;
        background-color: var(---darker-bg-color) !important;
    }
    #bd .nav li a.current {
        background-color: var(--bg-color) !important;
    }
    #bd .btn-danger {
        color: var(--fg-color) !important;
        background-color: var(--accent-bg-color) !important;
    }
    #bd .btn:hover,
    #bd .nav li a:hover {
        box-shadow: 0px 0px 1px 1px var(--border-color);
    }
    * img,
    .penguin_progress span {
        opacity: 0.5;
    }
    * img:hover {
        opacity: 1;
    }
}
