css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pplware.sapo.pt") {
  
  .sidebar {
    display: none;
  }
  
  .col-2cl .main-inner {
    background: white;
    padding-right: 0;
  }
  
   
  #bsu-v2-ctn {
    display: none !important;
  }
  
  body {
   all: initial;
   /** {
    all: unset;
   }*/
  }
  
  body {
    text-rendering: optimizelegibility;
    background: #222;
    font-size: 16px;
    /*line-height: 1.5em;*/
    color: #333;
    font-weight: 400;
    font-family: "Source Sans Pro",Arial,sans-serif;
    overflow-x: hidden;
  }
  
  #header {
    background-color: unset;
    box-shadow: unset;
  }
  
  #nav-header.nav-container {
    background-color: #0b1f38;
  }
  
  .large-pesquisa input.search, .small-pesquisa input.search {
    background: #0b1f38;
    color: #fff !important;
  }
  
  .content {
    background-color: #333;
    color: white;
    border-right: unset !important;
  }
  
  blockquote > p {
    color: #aaa;
  }
  
  .post-title, time, h1, h2, h3, h4 {
    color: #fff;
  }
  
  .post-title > a {
    color: #A4A4A4;
  }
  
  hr {
    color: #333;
    border-bottom: 2px solid #444;
  }
  
  .page-title {
    background-color: #121212;
    border-bottom: unset;
  }
  
  .post-tags a {
    background-color: #444;
  }
  
  #a_pais {
    display: none;
  }
  
  .post-nav {
    border-left: unset !important;
    border-top: unset !important;
    border-bottom: unset !important;
    border: unset !important;
  }
  
  .commentlist .fn, .commentlist .comment-body p {
    color: unset;
  }
  
  #commentform {
    background: unset;
  }
  
  #reply-title {
      margin-bottom: 0px;
      margin-left: 20px;
  }
  
  #respond {
    background-color: #0b1f38;
    padding-top: 20px;
  }
  
  .themeform label {
    color: #aaa;
  }
  
  #commentform textarea, #commentform input {
    background-color: #333;
    color: #fff;
  }
  
  #commentform textarea {
    height: 100px;
  }
  
  #disclaimer {
    background-color: unset !important;
  }
  
  .entry.excerpt {
    font-size: 16px;
    color: #A4A4A4;
  }
  
  .widgettitle {
    margin-bottom: 0px !important;
  }
  
  .footer-imgs {
    background-color: #333;
  }
}
