css:
@-moz-document domain("www.pandora.com") {
body, .AppBg {
    background-color: #0d112a !important;
    color: white;
}

/*.BlurredBackground__svg {
    filter: brightness(50%);
}*/

image.BlurredBackground__svg__image {
    filter: blur(16px);
}

.region-main__content,
.BackStage, .Backstage {
    background-color: rgba(0, 0, 0, 0.87);
}

.BackStage--withAds,
.region-main--rightRail,
.region-topBar--rightRail,
.MiniBar--rightRail,
.region-bottomBar--rightRail,
.ArtBg__img--rightRail,
.StationDetails--withAds {
    width: 100% !important;
}

.ArtBg--gradient {
    opacity: 0.8;
}

.HeaderBg::after {
    background-color: #0d112a;
}

/* Color lightTheme to dark */
.MiniBar--lightTheme .MiniBar__content__stationName, .MiniBar--lightTheme, .SimilarArtists--lightTheme, .nowPlayingTopInfo--lightTheme .nowPlayingTopInfo__stationName a, .SessionHistory--lightTheme .nowPlayingTopInfo__stationName a, .nowPlayingTopInfo--lightTheme, .Marquee--lightTheme, .nowPlayingTopInfo--lightTheme .nowPlayingTopInfo__current__albumName, .nowPlayingTopInfo--lightTheme .nowPlayingTopInfo__current__artistName, .nowPlayingTopInfo--lightTheme .nowPlayingTopInfo__current__link:hover, .Lyrics--lightTheme, .LyricsSubnugget--lightTheme, .ArtistDetailSubnugget--lightTheme, .SimilarArtistsSubnugget--lightTheme, .GlobalSearchInput__input, .FilletBio--lightTheme .FilletBio__artistDescription__header, .FilletBio--lightTheme .FilletBio__artistDescription__summary, .FilletBio--lightTheme .FilletBio__CTA__btn, .FilletBio--lightTheme a {
    color: white;
}

/* Hack to force background to extend to bottom of page */
.region-main__content::after {
    content: 'Â ';
    width: 100%;
    position: relative;
    font-size: 2rem;
}

/* Hides ads */

.DisplayAdController {
    display: none !important;
}
/* General changes */

a,
a:focus,
a:hover,
a:visited {
    color: #aab3cc;
}

svg,
.Nav--lightTheme .Nav__secondaryMenu__logo--icon,
.Nav--darkLogo .Nav__secondaryMenu__logo--icon,
.ShuffleButton__button--default>.icon-shuffle,
.SearchField .icon-search,
.BackstageAlbumTrack__icon>.icon-iconPlay,
.BackstageAlbumTrack__icon>.icon-iconPlay--filled {
    border-color: #aab3cc !important;
    fill: #aab3cc !important;
}

.RadioButton, .Checkbox {
	background-color: rgba(0,0,0,0.6);
    color: #aab3cc;
}
/* active SVG */

.ThumbUpButton--active > svg,
.ThumbDownButton--active > svg,
.TunerControl:hover > svg {
    fill: #4c97ff;
}

button:hover svg {
    fill: white;
}
/* Mini "coachmark" info popups and modal popups */

.MiniCoachmark {
    background-color: #202020;
}

.Modal__container {
    background-color: #202020;
}
.Modal__container__title {
    color: #aab3cc;
}
.Modal__container__body {
    color: rgba(255,255,255,0.8)
}
/* Dropdown menus */

.DropdownBox {
    background-color: #202020;
}

.DropdownOption {
    color: #aab3cc;
}
/* Buttons */
.Button--white, .btn, .btn--lightTheme {
    background-color: rgba(0,0,0,0.4);
    color: white;
    fill: white;
}
/* Search and search results */

.SearchField__input {
    color: white !important;
}

.GlobalSearchInput__input:focus, .GlobalSearchInput--overlayActive .GlobalSearchInput__input:focus {
    background-color: rgba(255,255,255,0.1);
    color: white;
}

.SearchResultsList:before {
    background-color: #0d112a;
}

.Nav--lightTheme.Nav--scrolled, .Nav--darkToLightTheme.Nav--scrolled, .Nav--lightTheme.Nav--scrolled {
    background-color: #202020;
}

.Search--active,
.Search--active.Search--rightRail {
    background-color: transparent;
}

.SearchField__clear,
.SearchField__clear:hover,
.SearchField__clear:visited,
.SearchField__placeholder {
    color: rgba(255, 255, 255, .6);
}

.SearchField__input {
    background-color: rgba(64, 64, 64, .1);
    border: none;
    border-radius: 2rem;
    box-sizing: border-box;
    color: rgba(0, 0, 0, .8);
    height: 3.4rem;
    padding: 0 6rem 0 2rem;
    width: 100%;
}

.SingleToggleButton__button {
    background-color: #202020;
    color: #aab3cc;
}

.AutocompleteMenu,
.AutocompleteMenu__fullSearch--wrapper {
    background-color: #202020;
}

.AutocompleteMenu__header {
    color: rgba(255, 255, 255, 0.6);
}

.AutocompleteMenu .AutocompleteMenu__fullSearch,
.AutocompleteMenu .AutocompleteMenu__sectionLink {
    color: #aab3cc;
}

.SearchListItem,
.SearchListItem:active,
.SearchListItem:focus,
.SearchListItem:hover,
.SearchListItem:visited,
.SearchListItem__text__secondLine {
    color: white;
}
/* Top bar (scrolled down) */

.Nav--darkTheme, .Nav--lightTheme {
    background-color: transparent !important; /* !important tag easily overrides ads changing nav bar color */
}

.Nav--darkTheme.Nav--scrolled, .Nav--lightTheme.Nav--scrolled, .Nav--transparentTheme {
    background-color: #202540 !important; /* !important tag easily overrides ads changing nav bar color */
}

.Nav--lightTheme .Nav__primaryMenu__item__link--active,
.Nav__primaryMenu__item > .Nav__primaryMenu__item__link:hover,
.NavHorizontal__item__link:hover, .NavHorizontal__item__link--active {
    border-bottom: 2px solid #eee;
}

.Nav--lightTheme .Nav__primaryMenu__item__link, .NavHorizontal__item__link, .NavHorizontal__item__link:hover, 
.NavHorizontal--light .NavHorizontal__item__link, .NavHorizontal--light .NavHorizontal__item__link:hover, .NavHorizontal--light .NavHorizontal__item__link:focus {
    color: white;
    border-color: #eee;
}

.ButtonLink--nav--upgrade--darkTheme,
.ButtonLink--nav--upgrade--darkTheme:focus,
.ButtonLink--nav--upgrade--darkTheme:hover,
.ButtonLink--nav--upgrade--darkTheme:visited {
    background-color: #202020;
    color: #aab3cc;
}

.MiniBar {
    background: #1b1b1b !important;
    box-shadow: 0px 1px 0px #404040 inset;
}
.nowPlayingTopInfo__current {
    width: 100% !important;
}
/* Larger font size for now playing main view */
.nowPlayingTopInfo__current__trackName {
    font-size: 2.7rem;
    line-height: 4rem;
}
.nowPlayingTopInfo__current--artistMessage .nowPlayingTopInfo__current__flag__text, .nowPlayingTopInfo__current__albumName, .nowPlayingTopInfo__current__artistName {
    font-size: 1.4rem;
    line-height: 2.2rem;
}
/* Tuner bar (currently playing) */

.Tuner {
    background-color: #202540 !important; /* !important tag easily overrides ads changing tuner bar color */
}

.Tuner__Audio__TrackDetail {
    opacity: 1 !important;
}

.Tuner__Audio__TrackDetail--Dark .Tuner__Audio__TrackDetail__artist, .Tuner__Audio__TrackDetail--Dark .Tuner__Audio__TrackDetail__artist:active, .Tuner__Audio__TrackDetail--Dark .Tuner__Audio__TrackDetail__artist:focus, .Tuner__Audio__TrackDetail--Dark .Tuner__Audio__TrackDetail__artist:hover, .Tuner__Audio__TrackDetail--Dark .Tuner__Audio__TrackDetail__artist:visited, .Tuner__Audio__TrackDetail--Dark .Tuner__Audio__TrackDetail__title, .Tuner__Audio__TrackDetail--Dark .Tuner__Audio__TrackDetail__title:active, .Tuner__Audio__TrackDetail--Dark .Tuner__Audio__TrackDetail__title:focus, .Tuner__Audio__TrackDetail--Dark .Tuner__Audio__TrackDetail__title:hover, .Tuner__Audio__TrackDetail--Dark .Tuner__Audio__TrackDetail__title:visited,
.Duration--Dark, .Duration--Light,
.VolumeDurationControl--Dark, .VolumeDurationControl--Light {
    color: rgba(255, 255, 255, 0.7);
}

.VolumeDurationControl--Dark, .VolumeDurationControl--Dark .VolumeDurationControl__VolumeSlider__Handle, .VolumeDurationControl--Dark .VolumeDurationControl__VolumeSlider__Trail {
    fill: rgba(255, 255, 255, 0.7);
}

.TunerProgress--Dark .TunerProgress__Progress {
    background-color: rgba(255,255,255,0.7);
}

.VolumeDurationControl--Dark .VolumeDurationControl__VolumeSlider__Handle, .VolumeDurationControl--Dark .VolumeDurationControl__VolumeSlider__Trail {
    background-color: white;
}
/* Misc */

.BackstageGrid__divider__button,
.Bio__divider__button {
    background-color: transparent;
    color: #aab3cc;
}

.BackstageGridItem:hover {
    background-color: rgba(64, 64, 64, 0.2);
}

.BackstageGridItem__text__first,
a.BackstageGridItem__text__first {
    color: white;
}

.BackstageGridItem__text__second,
a.BackstageGridItem__text__second {
    color: rgba(255, 255, 255, 0.8);
}

.BackstageAlbumTrack__track .BackstageAlbumTrack__track__link {
    color: white;
}

.BackstageAlbumTrack,
.BackstageAlbumTrack:hover {
    color: rgba(255, 255, 255, 0.8);
}

.BackstageAlbumTrack:hover {
    background-color: rgba(64, 64, 64, 0.2);
}

.Copyright__text, .Footer--light .Footer__copyrightText, .Footer--light .Footer__topMenu__item__link:visited, .Footer--light .Footer__topMenu__item__link {
    color: rgba(255, 255, 255, 0.4);
}

.ButtonT3--light {
    color: white;
}

/* Recommendations */

.SimilarArtistsItem:hover {
    background-color: rgba(64, 64, 64, 0.2);
}

.ListLayout__trackName,
.MyStationsListItem__trackName,
.RecommendationsListItem__leftContent .RecommendationsListItem__trackName {
    color: white;
}

.RowItem__centerCol__mainText {
    color: rgba(255, 255, 255, 0.8);
}

.RowItem__centerCol__subText, .RowItem__leftCol__number, .RowItem__rightCol__child {
    color: rgba(255, 255, 255, 0.5);
}

.RowItem--ArtistEventRowItem, .RowItem--BackstageRowItem, .RowItem--UserProfileV2TrackRowItem {
    border-color: rgba(255, 255, 255, 0.1);
}

/* Tickets / Event information */

.ArtistEventsItem__content__secondLine {
    color: rgba(255, 255, 255, 0.7);
}

.ArtistEventsItem__content__thirdLine {
    color: rgba(255, 255, 255, 0.5);
}

.ArtistEventsItem {
    background-color: rgba(122, 122, 122, 0.1);
}

.ArtistEventsItem__date__day {
    background-color: rgba(122, 122, 122, 0.33);
}

/* Context menu for managing music */

.SourceCard__menu, .SourceCard__subMenu {
    background-color: #20202b;
    color: rgba(255, 255, 255, 0.8);
}

.SourceCard__menu__item--focus.SourceCard__menu__item, .SourceCard__menu__item--focus.SourceCard__subMenu__item, .SourceCard__menu__item:focus, .SourceCard__menu__item:hover, .SourceCard__subMenu__item:focus, .SourceCard__subMenu__item:hover {
    background-color: rgba(128, 128, 128, 0.1);
    color: white;
}

/* Darken splash screen */

.Container__splash {
    background-color: #0d112a;
    background: linear-gradient(45deg, rgb(38,48,105) 0%, rgb(20,25,54) 75%);
}

.Container__splash__logo::before {
    content: "SimplyDark";
    position: relative;
    top: -3rem;
    font-family: inherit;
    font-size: 3rem;
}
.Container__splash__logo {
    opacity: 0.7;
}
.Container__splash__logo::after {
    content: "by andrewdoescss@outlook.com";
    position: relative;
    top: 80px;
    font-family: inherit;
    font-size: 1.2rem;
}
}

@-moz-document url-prefix("https://www.pandora.com/station"), url-prefix("https://www.pandora.com/browse"), url-prefix("https://www.pandora.com/music"), url-prefix("https://www.pandora.com/artist"), url-prefix("https://www.pandora.com/genre"), url-prefix("https://www.pandora.com/my-music") {
.Backstage {
    background-color: #0d112a;
}

/* Misc music/browse-related */

.animate-fadeIn {
    background-color: transparent !important;
}

.BrowseGenres, .BrowseGenres__gridContainer {
    background-color: #0d112a;
}

.HeaderBg__imgContainer:after {
    background-color: #0d112a;
}

.Button,
.SingleToggleButton__button--selected {
    background-color: #303555;
}

.Bio__text a {
    color: #aab3cc;
}

.AddedHeader--scrolled {
    background-color: rgba(0,0,0,0.9);
}

/* Removes weird "," character in top left of station icon on hover*/
.MyStationsListItem__thumbnail__overlay .icon-note+span {
    display: none;
}

/* Placeholder image for thumbnails with no image (e.g. Thumbprint Radio) */
.GridItem__art .ImageLoader,
.StationListItem__img .ImageLoader {
    background: url(http://i.imgur.com/zLKQZU1.png) center;
    background-size: 100% 100%;
}

/* "Recent" view */

.GridItem__metacontent__name>a, .GridItem__caption__main {
    color: white;
}

.GridItem__metacontent__second, .GridItem__caption__sub, .GridItem__caption__second, .GridItem__caption__third {
    color: rgba(255, 255, 255, 0.8);
}

.MyStationsListItem .MyStationsListItem__trackName,
.RecommendationsListItem .MyStationsListItem__trackName {
    color: white;
}

.MyStationsSorter__sorter {
    color: rgba(255, 255, 255, 0.4);
}

.MyStationsSorter__sorter:hover {
    color: #aab3cc;
}

.MyStationsListItem {
    background-color: rgba(128, 128, 128, 0.1);
    padding: 5px 10px 5px 10px;
    margin: 5px 0px 5px 0px;
}

.MyStationsListItem:hover {
    background-color: rgba(128, 128, 128, 0.2);
}

.MyStationsListItem__lastListened,
.MyStationsListItem__playTime,
.MyStationsListItem__thumbs .DisplayThumb__count {
    color: white;
}
/* "A-Z" view */

.LetterStrip__wrap {
    background-color: #101010;
}

.LetterStrip__letter--active {
    color: #aab3cc;
}

.LetterStrip__letter--inactive {
    color: rgba(255, 255, 255, 0.1);
    cursor: default;
}

/* Artist details */

.ContentSection__title,
.ContentSection__moreText, .ContentSection__moreText:focus, .ContentSection__moreText:visited,
.BackstageArtistHeader__albumCount, .BackstageArtistHeader__listenerCount,
.ArtistBio {
    color: rgba(255, 255, 255, 0.7);
}

.ContentSection__moreText:focus:hover, .ContentSection__moreText:hover, .ContentSection__moreText:visited:hover {
    color: rgba(255, 255, 255, 0.9);
}

.ArtistBio a {
    color: #5599dd;
}

/* Station details / editor */

.StationDetails {
	background-color: #0d112a;
}

.FeedbackList__ListHeader,
.SeedList .SeedList__ListHeader,
.HeaderBackstage__subTitle,
.DescriptionBackstage,
.DescriptionBackstage a{
    color: white;
}

.StationDetailsListItem__primaryText, .StationDetailsListItem__primaryText:hover, .StationDetailsListItem__primaryText:visited,
.StationDetails__body__addVarietyButton {
    color: #aab3cc;
}

.StationDetails__body__addVarietyButton:hover {
    text-decoration: underline;
}

.StationDetailsListItem__secondaryText, .StationDetailsListItem__secondaryTextUrl span, .StationDetailsListItem__tertiaryText,
.FeedbackList__placeholder {
    color: rgba(255, 255, 255, 0.4);
}

.DisplayThumb--inactive.DisplayThumb--Light, .DisplayThumb--inactive.DisplayThumb--Switch {
    fill: rgba(255, 255, 255, 0.4);
}
.DisplayThumb--Light, .DisplayThumb--Switch {
    color: rgba(255, 255, 255, 0.4);
}

.FocusTraits__divider__button {
    background-color: transparent;
    color: #aab3cc;
}

/* Manage thumbs up/down */

.StationDetailsListItem--hovered .StationDetailsListItem__rightAction {
    color: #aab3cc;
}
}

@-moz-document url-prefix("https://www.pandora.com/profile"), url-prefix("https://www.pandora.com/settings") {
.Nav--upgradeTheme, .Nav--upgradeTheme.Nav--scrolled {
    background-color: #202020;
}

.Nav--upgradeTheme .Nav__primaryMenu__item__link {
    color: white;
}

/* Upgrade page & Subscriptions settings page (Pandora Premium) */

.UpgradeCard--paymentsPage, .UpgradeCard--paymentsPage--premium,
.Payments__container {
    background-color: rgba(0,0,0,0.4);
    color: white;
}

.UpgradeCard__title {
    color: #aab3cc;
}

.CreditCardForm__subHeader, .CreditCardForm__subHeader--expirationDate, .CreditCardForm__subHeader--expiryYear,
.CreditCardForm__subHeader--price,
.CreditCardForm__checkbox-label,
.CreditCardForm__link, .CreditCardForm__link:active, .CreditCardForm__link:focus, .CreditCardForm__link:hover, .CreditCardForm__link:visited,
.Paypal__checkbox-label,
.Paypal__link, .Paypal__link:active, .Paypal__link:focus, .Paypal__link:hover, .Paypal__link:visited,
.Payments__subHeader--planBenefits, .Payments__subHeader--price,
.Subscription__cancelLink, .Subscription__cancelLink:active, .Subscription__cancelLink:focus, .Subscription__cancelLink:hover, .Subscription__cancelLink:visited {
    color: white;
}

.CreditCardForm__input--text, .CreditCardForm__input--text--expiryRow {
    background-color: #252525;
    color: white;
}

.Payments__link, .Payments__link:active, .Payments__link:focus, .Payments__link:hover, .Payments__link:visited,
.CreditCardForm__subscriptionDeleteCreditCard {
    color: #aab3cc;
}

.Payments__input--planPrice, .Payments__input--planPrice2 {
    background-color: #252525;
    color: white;
}

/* Profile thumbs up and bookmarks */

.UserProfile__Bookmarks__subHeader {
    color: rgba(255, 255, 255, 0.8);
}

.MediaListItem__primaryText, .MediaListItem__primaryText:hover, .MediaListItem__primaryText:visited {
    color: white;
}

.MediaListItem__secondaryText, .MediaListItem__secondaryText:hover, .MediaListItem__secondaryText:visited,
.MediaListItem__secondaryText, .MediaListItem__secondaryText:hover, .MediaListItem__secondaryText:visited, .MediaListItem__tertiaryText {
    color: rgba(255,255,255,0.6);
}

/* Other profile/settings */

.SideNav__list__link,
.SideNav__list__link__subLink,
.UserSetttings__DeleteAccount,
.AccountInfo__whyPandoraQuestion {
    color: rgba(255, 255, 255, 0.8);
}

.SideNav__list__link:hover,
.SideNav__list__link__subLink:hover,
.UserSetttings__DeleteAccount:hover {
    color: white;
}

.SideNav__list__link--active,
.SideNav__list__link--active:visited,
.SideNav__list__link--active:hover {
    color: #aab3cc;
    border-color: #aab3cc;
}

.UserProfile__Profile__header__name,
.UserProfile__Profile,
.UserProfile__Profile__header__row__col__val {
    color: rgba(255, 255, 255, 0.8);
}

.UserProfile__Profile__editButton,
.AccountInfo__editButton {
    color: #aab3cc;
}

.UserProfile__Profile__editButton:hover,
.AccountInfo__editButton:hover {
    color: white;
}

.UserSettings__Social__button--connected {
    background-color: rgba(0,0,0,0.4);
    color: white;
}

.Upgrade, .Redeem {
    color: white;
}

.Upgrade__link, .Upgrade__link:active, .Upgrade__link:focus, .Upgrade__link:hover, .Upgrade__link:visited,
.Redeem__link, .Redeem__link:active, .Redeem__link:focus, .Redeem__link:hover, .Redeem__link:visited {
    color: rgba(255,255,255,0.8);
}

.Upgrade__terms::after {
    border-bottom: .1rem solid white;
}

.UserSetttings__DeleteAccount__column .UserSetttings__DeleteAccount__link {
    color: #aab3cc;
}
.UserSetttings__DeleteAccount__column .UserSetttings__DeleteAccount__link:hover {
    color: #d07070;
}
}

@-moz-document url-prefix("https://www.pandora.com/search") {
.Nav--lightTheme, .SearchModalT3--focus {
    background-color: #0d112a;
}

.EmptySearch__content,
.SearchContainer,
.SearchResults__header,
.SideNav__list__link,
.SideNav__list__link__subLink {
    color: rgba(255, 255, 255, 0.8);
}

.SideNav__list__link--active, .SideNav__list__link--active:visited, .SideNav__list__link:hover {
    color: #aab3cc;
}

.SearchContainer > div{
    background-color: #0d112a;
}

.GlobalSearchInput--overlayActive .GlobalSearchInput__input {
    background-color: rgba(128,128,128,0.1);
    color: rgba(255,255,255,0.8);
}
}
