css:
@-moz-document domain("www.ponyvillesquare.com") {
/******* Backgrounds / overall effects */
/** Body */
body{
	background: #020202;
	color: #f3f3f3;
}
/** PVS Logo */
#elLogo img {
	filter: saturate(1) contrast(1) brightness(0.7);
}
/** Logo background */
#ipsLayout_header header{
	background-color: #1d1d1d;
}
/** Page background */
#ipsLayout_contentWrapper{
	background-color: #151515;
}
/** Box background */
.ipsBox:not( .ipsBox_transparent ):not( .ipsModerated ) {
    background-color: #1d1d1d;
}.ipsAreaBackground_reset, .ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):not( .ipsDataItem_new ):not( .ipsDataItem_success ):not( .ipsDataItem_warning ):not( .ipsDataItem_error ):not( .ipsDataItem_info ):not( .ipsDataItem_status ):nth-child(even){
    background: #1d1d1d;
}
/** All <p>, <li>, <span>, <a> */
p, li, span, a{
	background-color: transparent !important;
}
/** Focus outline */
.ipsApp .ipsToggle:focus, .ipsApp .ipsCustomInput input:focus + span, .ipsApp textarea:focus, .ipsApp input[type="text"]:focus, .ipsApp input[type="password"]:focus, .ipsApp input[type="datetime"]:focus, .ipsApp input[type="datetime-local"]:focus, .ipsApp input[type="date"]:focus, .ipsApp input[type="month"]:focus, .ipsApp input[type="time"]:focus, .ipsApp input[type="week"]:focus, .ipsApp input[type="number"]:focus, .ipsApp input[type="email"]:focus, .ipsApp input[type="url"]:focus, .ipsApp input[type="search"]:focus, .ipsApp input[type="tel"]:focus, .ipsApp input[type="color"]:focus, .ipsApp .ipsButton_important:focus, .ipsApp .ipsButton_primary:focus, #elSearch button:focus, .fa:focus, .ipsComposeArea_dummy:focus, .ipsApp select:not( [multiple] ):focus{
	border-color: rgba(150, 26, 8, 0.8);
	box-shadow: 0 1px 1px rgba(51, 51, 51, 0.075), 0px 1px 5px rgba(150, 26, 8, 0.6);
	outline:0;
}
/** Search background */
.ipsTabs_panel{
	background: #1d1d1d;
}
/** Images in topics */
.ipsImage_thumbnailed{
	border-color: transparent;
}

/******* Forums titles / Tabs / Bars */
/** Nav tab (showed on top when windows width < 980px) (for mobile but... yeah, why not ?) */
#elMobileNav{
	background: #961a08
}
/** Nav bar (contains "Browse" and "Activity" tabs just under the logo) */
.ipsNavBar_primary{
	background: #961a08
}.ipsNavBar_primary > ul > li.ipsNavBar_active > a {
    background: #1d1d1d !important;
    color: #f3f3ee;
}.ipsNavBar_secondary{
	background: #1d1d1d;
}.ipsNavBar_secondary > li.ipsNavBar_active a, .ipsNavBar_secondary > li > a, .ipsNavBar_secondary > li > a:hover{
    color: #f3f3ee;
}.ipsNavBar_primary > ul > li > a{
	background: rgba(0, 0, 0, 0.15) !important;
}.ipsNavBar_primary:not( .ipsNavBar_noSubBars ) > ul:before{
	background: #1d1d1d;
}
/** Side title (the ones with "Topics", "Recent Status Updates" and "Upcoming Events") */
.ipsWidget.ipsWidget_vertical .ipsWidget_title{
	background-color: #961a08;
	color: #f3f3f3;
}.ipsDataItem{
	border-color: #151515;
}.ipsWidget_inner{
	background: #1d1d1d;
}.ipsWidget .ipsCalendarDate{
	background: #333333;
}.ipsCalendarDate_date{
	color: #FFFFFF;
}.ipsCalendarDate_month{
	color: #FFFFFF;
}
/** Forum section title (the ones with "Announcements", "Minecraft Server", etc...) */
.ipsType_sectionTitle{
	background: #961a08;
}
/** Stats title */
.ipsWidget.ipsWidget_horizontal .ipsWidget_title{
	background: #961a08;
}
/** Some tabs (can contains "Activity", "About me" in profile or "Content Search", "Member Search" in search page, etc...) */
.ipsTabs{
