	color: #f3f3f3;
}
/** Pages bar (pagination table) */
.ipsButtonBar{
	background: #252525;
}.ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a{
	background: #656565 !important;
	color: #f3f3f3;
}

/******* Buttons */
/** basic buttons */
.ipsApp .ipsButton_important, .ipsApp .ipsButton_primary{
