}
/** buttons in activity filter */
.cStreamFilter{
	background: #1d1d1d;
	margin-bottom: 1px;
    margin-top: 1px;
	border: transparent;
}.cStreamFilter h3{
	color: #f3f3f3;
}.ipsTruncate.ipsTruncate_line{
	color: #f3f3f3;
}.cStreamFilter p em{
