/******* Miscellaneous */
/** Notifs ? (to be tested) */
.ipsApp .ipsNotificationCount{
	background: #404040;
}
/** mobile tab icon (sceen width < 980px) */
#elMobileNav li:not( #elMobileBreadcrumb ) a{
	color: #1d1d1d;
}
/** User nav (top right with "+ create", etc...) */
#elUserNav > li > a{
	color: #f3f3f3;
}
/** Popup Menus (when you click at "+ create" or your user name in user nav, etc...) */
.ipsMenu{
