}.ipsEmpty{
	color: #f3f3f3;
}
/** Alerts */
.ipsAlert{
	background: #1d1d1d;
}
/** For small screens (< 980px) */
.ipsDrawer_close, .ipsDrawer_menu{
	background-color: #1d1d1d;
}
/** Loading */
.ipsDialog .ipsDialog_loading, .ipsLoading{
	background: #1d1d1d;
}.ipsLoading:not( .ipsLoading_noAnim ):before{
	filter: brightness(4);
}

/******* Writings */
/** ALL links */
a{
	color: #f3f3f3;
}a:hover{
	color: #f3f3f3;
}
/** Navigation ("Home" > "something" > ...) */
.ipsBreadcrumb > ul > li > a, .ipsBreadcrumb > ul > li{
