}
/** Forums title (in forum page) */
.ipsType_pageTitle{
	color: #f3f3f3;
}
/** "This topic is now closed to further replies." */
.ipsComposeArea_unavailable .ipsComposeArea_editor{
	background: #1d1d1d
}.ipsComposeArea_dummy, .ipsComposeArea_editor, .cke_wysiwyg_frame, .cke_wysiwyg_div{
	background: #1d1d1d;
}html[dir="ltr"] .ipsComposeArea_unavailable.ipsComposeArea_withPhoto .ipsComposeArea_editor:before{
