css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.es"), domain("eldarya.com"), domain("eldarya.fr"), domain("eldarya.com.br") {

/*Leiftan Familiar*/  
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://68.media.tumblr.com/eef3aca89434d4a80ed1ba9fd7104e28/tumblr_oms8jbKUZ91trxrtqo1_500.jpg") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://68.media.tumblr.com/ded4fc929ebe6d9e1dcfbf5e46114ab9/tumblr_oms77nRB4C1trxrtqo1_500.jpg") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Leiftan" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Leiftan ahora te ama, ¡abrazalo una vez más!" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Lorialet" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("https://68.media.tumblr.com/9d97b02ed39f4ee4b3ec3106aabc147e/tumblr_oms8wgRx6N1trxrtqo1_250.jpg") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Palomitas Acarameladas" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Ezarel sur la page du profil*/

  #player-display-pet {
    background : url("https://68.media.tumblr.com/9adac497b0e5062ad2967d19d559dc4e/tumblr_olultmEnZd1tc51qto1_500.png") no-repeat ;
    background-size : auto 100% ;
    height : 560px !important ;
    width : 250px !important ;
  }

  #player-display-pet > img {display : none !important ;}
}
