/* ultimos detalles */
#managerbar {
    width: 100%;
    height: 30px;
    background: rgba(0, 0, 0, 0.38);
    border-bottom: 0px solid rgba(0, 0, 0, 0.09);
}
header {
    height: 100px;
    background: rgba(0, 0, 0, 0.38);
    padding-right: 20px;
    box-shadow: 0 5px 5px rgba(0, 0, 0, 0);
}
nav {
    margin-bottom: 20px;
    background: rgba(10, 5, 5, 0.4);
    /* background: linear-gradient(#000,#12121b); */
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
box-shadow: 0 5px 5px rgba(0, 0, 0, 0);
    font-family: Oswald;
}
nav {
    margin-bottom: 20px;
    /*background: #000;*/
    /*background: linear-gradient(#000,#12121b);*/
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
box-shadow: 0 5px 5px rgba(0, 0, 0, 0);
    font-family: Oswald;
}
nav>a:active, nav>a:hover {
    border-radius: 8px;
    color: #28b!important;
    background: rgba(10, 5, 5, 0.4);
    /*background: linear-gradient(#000,#223);*/
}
nav>a {
    border-right: 1px solid rgba(47, 56, 165, 0);
    text-transform: uppercase;
}

/*para abajo*/
.container .head {
    margin-bottom: 3px;
    padding: 10px;
    font: 18px/18px 'Oswald';
    background: rgba(0, 0, 0, 0);
    /* background: linear-gradient(#12121b,#000); */
    /* border-radius: 10px; */
}

.container {
    display: block;
    background: rgba(74, 74, 142, 0);
    /* background: linear-gradient(#121217,#000); */
    margin-bottom: 30px;
    border-radius: 10px;
    text-shadow: 0 -1px #000, 1px 0 #000, 0 1px #000, -1px 0 #000;
    border: 0px solid #000;
}
/*Botones arriba*/
header #searchBox button {
    padding: 2px 5px;
    width: 150px;
    height: 39px;
    /* border-width: 9px; */
    /* border: 1px solid #000; */
    font-size: 18px;
    line-height: 22px;
}
.redButton {
    background: red!important;
    border-radius: 4px !important;
    border: 1px solid #f00!important;
    color: #fff!important;
    font: 14px 'Open Sans',sans-serif!important;
}
.redButton:focus, .redButton:hover {
    background: red!important;
}
.blueButton {
    background: blue!important;
    /* background: linear-gradient(#28b,#000)!important; */
    border-radius: 4px!important;
    border: 1px solid #00f!important;
    color: #fff!important;
    font: 14px 'Open Sans',sans-serif!important;
}
.blueButton:focus, .blueButton:hover {
    background: blue!important;
}
header #searchBox button {
    padding: 2px 5px;
    width: 150px;
    height: 39px;
    border: 1px solid #000;
    box-shadow: inset 0px 0px 0px 0px #000;
    color: #fff;
    font-size: 18px;
    line-height: 22px;
}
header #searchBox #name, header #searchBox #realm {
    position: relative;
    top: 20px;
    padding: 2px 10px;
    height: 33px;
    background: rgba(66, 52, 50, 0.42);
    border-radius: 9px;
    /* background: linear-gradient(#223,#12121b); */
    border: 1px solid rgba(201, 202, 156, 0);
    /* box-shadow: inset 5px 5px 5px -5px #000; */
    color: #fff;
    font-size: 18px;
    line-height: 22px;
    vertical-align: top;
}
#realm-button {
    position: relative;
    top: 20px;
    display: inline-block!important;
    padding: 2px 0px!important;
    width: 50px!important;
    height: 33px!important;
    background: rgba(66, 52, 50, 0.42);
    /* background: linear-gradient(#223,#12121b)!important; */
    border: 1px solid rgba(0, 0, 0, 0)!important;
    border-left: 0!important;
    border-radius: 9PX !important;
    /* box-shadow: inset 5px 5px 5px -5px #000!important; */
    vertical-align: top;
}
.ui-selectmenu-menu ul {
    background: rgba(0, 0, 0, 0.35)!important;
    border: 1px solid rgba(34, 136, 187, 0)!important;
        border-radius: 9PX !important;
}
}
