css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.es"), domain("eldarya.com"), domain("eldarya.com.br"), domain("eldarya.fr"), domain("eldarya.com.br") {

/*Valkyon Familiar*/  
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://68.media.tumblr.com/555cd13822302e5b6a112deff8e84adf/tumblr_omskflYMnx1trxrtqo1_500.jpg") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://68.media.tumblr.com/0b8a7e13201d10c7b83b35044e719b25/tumblr_omskflYMnx1trxrtqo3_500.jpg") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Valkyon" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Valkyon te ha sonreido, ¡amale mucho más!" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Faelienne" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("https://68.media.tumblr.com/dd0ff6f3f6391275c8f8896f28149040/tumblr_on6x8u2zce1trxrtqo3_75sq.png") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Papas rellenas" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Ezarel sur la page du profil*/

  #player-display-pet {
    background : url("https://68.media.tumblr.com/7a242ada34d22704b13b8f7b934c2d97/tumblr_ngx87rTwrx1u4rbeio2_1280.png") no-repeat ;
    background-size : auto 100% ;
    height : 540px !important ;
    width : 230px !important ;
  }

  #player-display-pet > img {display : none !important ;}
}
