css:
@-moz-document domain(storiesonboard.com) {

/* boards main */
#storiesonboard-feedback, .tile-group-header-left {
    visibility: hidden !important;
}
.add-icon {
  background-image: none !important;
  display: inline-block;
  font: normal normal normal 14px/1 FontAwesome;
  font-size: 45px;
  text-rendering: auto;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
.add-icon:before {
    content: "\f055";
}
.dashboard #header #header-title {
    padding-left: 35px;
    color:#05335d;
}
.dashboard .tile-group > .tile-group-content > .tile-cell > .tile-btn {
    width: 245px;
    height: 156px;
    border: dotted 1px #7794ae;
    color: #7794ae;
}
.dashboard .tile-btn {
    background-color: #ddeaf5;
}
.dashboard .tile-btn:hover {
    background-color: #ddeaf5;
    border: dotted 2px #05335d;
    color: #05335d !important;
}
/* eliminated modal bouncing effect */
.animated.bounce-in {
    -webkit-animation-name: none;
    -moz-animation-name: none;
    -o-animation-name: none;
    animation-name: none;
}
.animated.speedy {
    -webkit-animation-duration: 0s;
    -moz-animation-duration: 0s;
    -o-animation-duration: 0s;
    animation-duration: 0s;
}
.animated {
    -webkit-animation-duration: 0s;
    -moz-animation-duration: 0s;
    -o-animation-duration: 0s;
    animation-duration: 0s;
    -webkit-animation-fill-mode: none;
   -moz-animation-fill-mode: none;
    -o-animation-fill-mode: none;
    animation-fill-mode: none;
}
/* dialog - content area moved up higher */
.modal-dialog.default .modal-area .content {
    padding-top: 35px;
}
/* exclude intercom */
body:not(#intercom-container-body) {
    background-color: #ddeaf5;
}
/* menu bar from black to navy blue */
#header {
    background: #05335D;
}
 
/* top bar icons */
.fullscreen-icon:before {
    content: "\f0b2";
}
.more-icon:before {
    content: "\f141";
}
.share-icon:before {
    content: "\f0c0";
}
.integration-icon:before {
    content: "\f0c1";
}
.history-icon:before {
    content: "\f0ca";
}
.history-alert-icon:before {
    content: "\f0ca";
}
.twolevelboard-icon:before {
    content: "\f009";
}
.threelevelboard-icon:before {
    content: "\f00a";
}
.plus-icon:before {
    content: "\f00e";
}
.minus-icon:before {
    content: "\f010";
}
.collapse-icon:before {
    content: "\f066";
}
.expand-icon:before {
    content: "\f065";
}
.board-activities-panel-button > a.btn-icon:before {
        content: "\f00d";
}
 
.board-search-icon-filtered:before {
   content: "\f0b0";
}   
 
.board-search-icon:before {
   content: "\f0b0";
}   
 
.shr-icon:before {
   content: "\f0c0";
}

.persona-icon:before {
   content: "\f007";
}

.fullscreen-icon, .more-icon, .share-icon, .integration-icon, .history-icon, .twolevelboard-icon, .threelevelboard-icon, .plus-icon, .minus-icon, .expand-icon, .collapse-icon, .board-activities-panel-button > a.btn-icon, .shr-icon, .board-search-icon, .persona-icon {
  background-image: none !important;
  display: inline-block;
  font: normal normal normal 14px/1 FontAwesome;
  font-size: 20px;
  text-rendering: auto;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
.history-alert-icon, .board-search-icon-filtered  {
  background-image: none !important;
  display: inline-block;
  font: normal normal normal 14px/1 FontAwesome;
  font-size: 20px;
    color:red !important;
  text-rendering: auto;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
.btn-icon > i, .btn-icon > span {
    display: table-cell;
    vertical-align: middle;
    padding: 4px;
    color: #66b9e1;
}
.storymap #header #storymap-header-menu .jquery-slider .slider-track, .storymap #header #storymap-header-menu .jquery-slider .slider-connector-left {
    background-color: #66b9e1;
}
/* activity feed */
.notifications-pivot, .board-activities-feed-panel-conatiner {
    overflow-x: hidden;
}
#board-activities-panel .board-activity-user {
    color: #3b6d86;
}
.app-icon-share:before {
    content: "\f124";
}
.app-icon-share {
  background-image: none !important;
  display: inline-block;
  font: normal normal normal 14px/1 FontAwesome;
  font-size: 14px;
  text-rendering: auto;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
/* unscheduled tasks label */
a.trigger {
    color: #fff;
}
/* board card colors changed to pastel and shadow applied */
.board-card-title-text { /* ??? */
    font-size: 24px;
}
.board-card-color-white.board-active-card { /* ??? */
    border-color: #686868;
}
.board-card-color-blue { /* transparent craft style applied */
    line-height: 32px;
    background-color: #ddeaf5;
    border-color: #ddeaf5;
    border-left: 1px solid;
    color: #567591;
    font-weight: 600;
}
.board-card-color-yellow {
    background-color: #f2eaab;
    border-color: #e8cf01;
    color: #635207;
    box-shadow: 0 0 8px rgba(0, 0, 0, .1);
}
.board-card-color-white {
    background-color: #ffffff;
    border-color: #fff;
    color: #6193bf;
    background: #fff;
    box-shadow: 0 0 8px rgba(0, 0, 0, .1);
}
.board-card-color-orange {
    background-color: #f8d285;
    border-color: #ffc451;
    color: #402a00;
    box-shadow: 0 0 8px rgba(0, 0, 0, .1);
}
.board-card-color-gray {
    background-color: #e0e2e4;
    border-color: #dddddd;
    color: #4f4f4f;
    box-shadow: 0 0 8px rgba(0, 0, 0, .1);
}
.board-card-color-green {
    background-color: #ceeea7;
    border-color: #c2ee87;
    color: #253b08;
    box-shadow: 0 0 8px rgba(0, 0, 0, .1);
}
.board-card-color-pink {
    background-color: #f6ccdd;
    border-color: #ffb4cd;
    color: #2c000f;
    box-shadow: 0 0 8px rgba(0, 0, 0, .1);
}
.board-card-color-purple {
    background-color: #e0c8ea;
    border-color: #d7a8e2;
    color: #331239;
    box-shadow: 0 0 8px rgba(0, 0, 0, .1);
}
.board-card-color-red {
    background-color: #f6b4b6;
    border-color: #fc9798;
    color: #330000;
    box-shadow: 0 0 8px rgba(0, 0, 0, .1);
}
/* removed vertical dashed lines */
.board-line, .board-body-line {
    border-right: dashed 0px #ddeaf5 !important;
}
/* removed fixed header horisontal line */
.board-head-fixed-border {
    border-bottom: solid 0px white;
}
/* release horisontal line */
.board-release {
    padding-top: 2px;
    padding-bottom: 2px;
    margin-left: 15px;
    border-bottom: solid 1px #71bbff;
    height: 12px;
    position: relative;
    margin-top: 20px;
}
#board-body .board-release {
        border-bottom: solid 1px #71bbff;
}
#board-body .board-release > div {
    background-color: #71bbff;
    padding: 3px;
}
/* release info */
.board-release > div {
    background-color: #EEF4FA;
    position: absolute;
    background: #71bbff;
    color: #fff;
    font-size: 12px;
    padding: 0 15px;
    border-radius: 3px;
    left: -10px;
    top: -1px;
    height: 18px;
    line-height: 20px;
    white-space: nowrap;
    margin-left: -5px;
}
.board-release-name,
.board-card-indicator-icon-minus,
.board-card-indicator-icon-plus,
.caret.for-release {
    font-size: 14px;
    color: #fff;
    background-image: none !important;
}
.board-release-name {
               margin-left:15px;  
}
.board-release-details .board-release-estimation-tooltip .app-icon-statistics {
 color:#fff;
}
.board-release-details .board-release-estimated-effort {
    color:#fff;
}
.board-card-indicator-icon-plus:before {
    /*content: "\f065";*/
    margin-right: 5px;
}
.board-release-estimated-effort {
    font-size: 12px;
    font-weight: 100;
    color: #fff;
}
/* area with user stories */
.board-body-container-pinned {
    background: #E9EFF4;
}
/* modal */
.modal-dialog.default .modal-area > .header {
    position: absolute;
    top: 0;
    left: 0;
    font-size: 0px;
    text-align: left;
    color: #fff;
}
.modal-dialog legend + .legend-description {
    font-size: 12px;
    position: relative;
    top: 0px;
}
.control-group {
    margin-top: 10px;
}
/* card description edit */
.modal-dialog.card-details .description_container .inline-textarea-autoresize {
    min-height: 160px;
    border: dashed 1px #ddd;
    padding: 4px 4px 5px 4px;
    width: 605px;
    font-size: 14px;
    font-weight:500;
    font-family: 'Proxima-Nova', sans-serif;
    line-height: 1.4;
}
.modal-dialog.card-details .markdown-editor-formating-help {
  display: block;
  text-align: right;
  margin-bottom:-15px;
}
/* card description view */
.modal-dialog.card-details .description_container .description {
    font-size: 14px;
    line-height: 1em;
    width: 603px;
    display: block;
    min-height: 160px;
    cursor: pointer;
    overflow-wrap: normal;
    word-wrap: normal;
    hyphens: none;
    font-family: 'Proxima-Nova', sans-serif;
    padding: 0px 40px 0px 0px;
    margin: -4px 0px 0px 5px;
}
 
/* card description view - paragraph */
.modal-dialog.card-details .markdown-view-content p {
    margin-top: 9px;
    margin-bottom: 19px;
    line-height: 1.4em;
    text-align: left;
    font-size: 14px;
    hyphens: none;
}
.modal-dialog.card-details .name_container .name_display {
    color: #282828;
    font-weight: bold;
    font-size: 24px;
    margin: 11px 0px 0px 6px;
    padding: 0px 0px 7px 0px;
    min-height: 22px;
    width: 100%;
    cursor: pointer;
    display: block;
    font-family: 'Proxima-Nova', sans-serif;
}
.modal-dialog.card-details .name_container .inline-input {
    font-size: 24px;
    font-weight: bold;
    font-family: 'Proxima-Nova', sans-serif;
}
 
html,
div,
span,
h1,
h2,
h3,
h4,
h5,
h6,
p,
blockquote,
pre,
a,
cite,
code,
del,
dfn,
em,
img,
ins,
kbd,
q,
s,
samp,
small,
strike,
strong,
sub,
sup,
tt,
var,
b,
u,
center,
dl,
dt,
dd,
ol,
ul,
li,
fieldset,
form,
label,
legend,
table,
caption,
tbody,
tfoot,
thead,
tr,
th,
td,
article,
aside,
canvas,
details,
embed,
figure,
figcaption,
footer,
header,
hgroup,
menu,
nav,
output,
ruby,
section,
summary,
time,
mark,
audio,
video {
    margin: 0;
    padding: 0;
    border: 0;
    vertical-align: baseline;
    font-family: 'Proxima-Nova', sans-serif;
    font-weight: 100;
    line-height: 1.1;
    outline: 0;
    font-size: 14px;
}

}
