css:
@-moz-document url-prefix('https://trello.com') {


  /* i really want this to be global */
  #board-header .board-name .text, textarea.field, .list-title h2, div.action-comment,p, div.phenom-desc ul li, div.phenom-desc, span.card-label.red-label, h2.window-title-text.current.hide-on-edit, input.primary.confirm.js-save-edit, h2.hide-on-edit.current, a.list-card-title.clear, textarea.js-card-title, input.new-task-text.full, textarea.new-comment-input.js-no-ie-placeholder, p.current.hide-on-edit, .list-card-titleو .comment-box-input,  button, input, select, textarea, .list-card-title, .card-label, .editable, code  {
      font-family: "IRANSans", "b nazanin", tahoma!important;
  }
body, button, html, input, select, textarea{
  font-family: "IRANSans", "b nazanin", tahoma!important;
}
  
  .action-comment .current-comment, .js-card-desc, .window-main-col .edit textarea.field, .list-header, textarea2, .checklist-items-list, .card-detail-title, .list-card-labels, .editable, .list, .js-new-comment-input {
      direction: rtl!important;
  }
  .action-comment{direction:rtl;}

  .card-detail-window .action-comment .current-comment{
      min-width:400px;
  }


   .list-card-title{
      direction:rtl!important;            
      padding-left:20px;
  }

  .list-wrapper.mod-add{
       width:auto;   
  }
  .window-module-title-options, .window-header-inline-content, .card-detail-item-header-edit{
      float:left;
      direction: ltr;
  }

  .list-card-operation{
      left:0px;
      right: inherit;
      top:0px;
      color:#000;
  }
  .list-header-menu-icon{
      left: 0;
      right: inherit;
  }
  .list-header{
      padding: 8px 16px 8px 34px;
  }


  .list-header-extras{
      left:4px
  }

  .list-header.is-menu-shown, .list-header.is-subscribe-shown, .list-header.is-menu-shown.is-subscribe-shown{
      padding-right:10px;
  }
  .window-main-col{
      float:right;    
  }
  .window-sidebar{
      padding-right:0
  }
  .list-header-target{
      z-index:2;
  }
  .list-card-members{
      position:absolute;
      top:0;
      left:0;
  }
  .badges{
      float:right;
  }
  .list-card-details{
      padding-left: 30px;
  }

  .list-card a, .sticker-select-upload:hover .sticker-select-upload-icon{
      font-weight: 200;
      font-size: 0.9em;
  }

  .badges{
      padding-bottom:0;
  }
  .badge{
      min-height:0;
  }
  .icon-subscribe{
      display:none;
  }


  code{
      border:none;
      line-height: 1.7em;
  }

  .card-label.mod-card-front[title='دانش سرشمار'],
  .card-label.mod-card-front[title='داشبورد'],
  .card-label.mod-card-front[title='پروفایل'],
  .card-label.mod-card-front[title='ثبت سوال'],
  .card-label.mod-card-front[title='نمایش نظرسنجی'],
  .card-label.mod-card-front[title='درباره'],
  .card-label.mod-card-front[title='مرکز راهنمایی'],
  .card-label.mod-card-front[title='سوالات من'],
  .card-label.mod-card-front[title='صفحه نخست'],
  .card-label.mod-card-front[title='ویژگی‌ها'],
  .card-label.mod-card-front[title='سوالات متداول'],
  .card-label.mod-card-front[title='تلگرام'],
  .card-label.mod-card-front[title='تماس'],
  .card-label.mod-card-front[title='حریم خصوصی'],
  .card-label.mod-card-front[title='شرایط'],
  .card-label.mod-card-front[title='شرایط و قوانین'],
  .card-label.mod-card-front[title='اکانت']
  {
      line-height: 10px;
      font-size: 9px;
      width: auto;
      height: 10px;
      padding: 0 5px;
  }

  .list-card-labels .card-label{
    float:right;    
    width:15px;
    min-width:auto;
    height: 5px!important;
    font-size:0;
  }
  .card-label{
      text-align:right;
  }

  .list-wrapper:first-child .list{
      background-color:#c8d2aa;
  }
  
  .known-service-link{display:inline-block}
  code, pre{direction:ltr;display:inline-block;max-width: 100%;}
  pre{direction:ltr;display:block;max-width: 100%;}
  .boards-page-board-section-list-item{width:20%}
  .board-tile-details-name{font-family: IRANSans;}
  
  .markeddown ul>li{margin:8px 20px 8px 0; font-size: 0.9em;line-height: 1.8em;}
  .action-comment pre{max-height: 800px;}
  
  .list-card-labels{
    position: absolute;
    bottom: 0;
    left: 5px;
  }
  
  
  @media only screen and (max-width: 1280px) and (min-width: 901px)
  {
      .boards-page-board-section.mod-no-sidebar .boards-page-board-section-list-item {width: 20%;}
  }

  
}
