css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.es"), domain("eldarya.com"), domain("eldarya.fr"), domain("eldarya.com.br") {

/*Ezarel Familiar*/  
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://68.media.tumblr.com/b16d62d5abcfcc47f7543fe155a29a65/tumblr_omtqlobjp41trxrtqo1_500.jpg") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://68.media.tumblr.com/44e1df43f97fad992b792802a62aa3a4/tumblr_omtqlobjp41trxrtqo3_500.jpg") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Ezarel" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Ezarel te ha hecho enojar, ¡pero no le odies por favor!" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Elfo" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("https://68.media.tumblr.com/83ca4a3e84f3c4baec32b791ecfc0354/tumblr_omtqlobjp41trxrtqo2_75sq.jpg") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Miel" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Ezarel sur la page du profil*/

  #player-display-pet {
    background : url("https://68.media.tumblr.com/3664c7aa0c2415efc254bad480bb94f9/tumblr_omtr7xaZbc1trxrtqo1_1280.png") no-repeat ;
    background-size : auto 100% ;
    height : 530px !important ;
    width : 220px !important ;
  }

  #player-display-pet > img {display : none !important ;}
}
