css:
/* Blue-17    from albertd Mod V4

V03 14/03/17 
Various minor tweaks including enlarging some font sizes,
especially the date/time, and removing the white panel from the quote boxes

V02 04/03/17
V01

*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.digitalspy.com") 
{

body, td , nav {background-color: #e3e6f7 !important;
}  

.h1, h1 {font-size: 20px !important;}
  
 
  
  
  
  
.MessageList.Discussion .ItemDiscussion::before 
  {background-color: #e3e6f7  !important; }
   
   
   
  
   .Spoiler.SpoilerConfigured     { background: #e3e6f7  !important;}   /*  SPOILER  */  
  
  

  
  
  
    .span , .Ellipsis  {color: #1515AA  !important; 
background-color: #e3e6f7 !important; font-size: 12pt !important;  /*  profile font  */
}  
  
  

  
  
   
 a {color: #1515AA  !important;  font-size: 12pt !important;  /*  profile font  */
}    
   
   
  
  time {color: #1515c4   !important; 
background-color: #e3e6f7 !important; font-size: 12pt   !important;  /* profile font   */
   }  
  
  
  
div.nav-sticky
  {color: #000000 !important; position:relative !important;
background-color: #e3e6f7 !important; /* part of the top bar - #668888 */  
}

  
footer  
  { background-color: #e3e6f7 !important; /*   Bottom bar - #668888 */  
}

  
  
div.Options
  {color: #000000 !important;
background-color: #e3e6f7 !important; /* top bar - #668888   */
}   

  
div.Meta
{
    width: 30px;    /* Space for date/time - 135px */
}
  
  
.Meta.Meta-Discussion
{
    width: inherit ;    /* Space for profile discussion  */
}
 
  
div.Block.Wrap
{
  width: 300px; }
  
  
  
time {color: #000000 !important; font-weight: bold   !important;
    font-size: 13px !important; font-family: 'Trebuchet MS' !important;  /* All times 14px  */
}

.UserLink.BlockTitle{     font-family: 'Trebuchet MS' !important; font-size: 12px !important;}  /* User names in thread lists 16px Trebuchet MS  */
  
  
.Wrap > a,
.Wrap {
font-size: 14px !important; font-family:'Verdana' !important;   /* Most of thread list 16px  */
}

  

    
  .Item-Header      {color: #000000 !important;
background-color: #e3e6f7 !important; /* profile bar - #668888 */  
}  
  
  
  
  
  

div.row.site-content  
    {color: #000000 !important;
background-color: #e3e6f7 !important; /* Main side bars (L&R) - #668888 */  
}  
  
  
div.ds-container
  {color: #000 !important;
background-color: #e3e6f7 !important; /* background to top bar - #668888 */  
}  

  
div.info-panel.col-xs-12.col-md-12
 {color: #000000 !important;
background-color: #ffffff !important; /* background to forum path - white */  
}
  
div.profile-nav.col-xs-6.col-md-6  
 {color: #000000 !important;
background-color: #ffffff !important; /* background to options on right - white */  
}  


div.Wrap  
  {color: #000 !important;
background-color: transparent !important; /* Background to list of threads - transparent */
}  
  
div.P.PageDescription  
  {color: #000 !important;
background-color: #e3e6f7 !important; /* round the description of thread list pages - #668888 */
}   
  
div.Item-BodyWrap
  {color: #000 !important;
background-color: #f5f6fb !important;  ; padding-top: 60px !important; /* Background to posts - #ccdddd */
} 
    
div.QuoteText
  {color: #000 !important; font-weight: normal   !important; 
    font-size: 12px !important; font-family:'Trebuchet MS' !important;
background-color: #e3e6f7 !important; /* Background to text in quote boxes - transparent */
}

  
 div.QuoteText , .Quote ,  .UserQuote 
  {color: #000 !important;
background-color: #e3e6f7   !important; font-size: 12pt !important; /* Background to text in quote boxes - white */
} 
  
  



div.Message 
{color: #000000 !important;font-weight: normal   !important; 
    font-size: 16px !important; font-family:'Trebuchet MS' !important;
background-color: #f5f6fb   !important; /* round message display text - #ccdddd */
}  
  
  
  
  div.User  , .ItemContent.Discussion  , .Item.New.ItemDiscussion
{color: #000000 !important;font-weight: normal   !important; 
    font-size: 12px !important; font-family:'Trebuchet MS' !important;
background-color: #f5f6fb   !important; /* user profile */

  }
  
  
div.QuoteAuthor
  {color: #000 !important;
background-color: #e3e6f7 !important; /* Background to author line in quote boxes - transparent */
}  
  
div.DataTableWrap  
  {color: #000 !important;
background-color: #ccdddd !important;
/* Background to alternate lines in thread lists (and hover on the others)- #ccdddd */
/* Set to #ffffff (white) if you don't like the flashing */
}
 

div.Item-Body
{color: #000 !important;
background-color: #f5f6fb !important; /* Round message display area - #ccdddd */
}  
  
div.Message
{color: #000000 !important;font-weight: normal   !important; 
    font-size: 16px !important; font-family:'Trebuchet MS' !important;
background-color: #f5f6fb !important; /* round message display text - #ccdddd */
}  
    
 
div.CommentFormWrap 
{color: #000 !important;
background-color: #e3e6f7 !important; /* round message entering area - #668888 */
}
  
div.CommentsWrap
{color: #000 !important;
background-color: #e3e6f7 !important; /* between posts - #668888 */
}  

  
div.page-content
{color: #000 !important;
background-color: #e3e6f7 !important; /* Stripe above message entering area and a few other places - #668888 */
}
 

  
  
div.markering.mid
{color: #000 !important;
background-color: transparent !important; /* Stripe half way down messages - transparent */
}  

  
/*  ========================================== 
  
  The following sections were in use but now seem not to be serving any purpose,
  but I have left them in for now "just in case"!
  
  ==========================================  */
  
    
div.User  
    {color: #000000 !important;
background-color: #aabbbb !important; /* Redundant? - #aabbbb */  
}  
  

  
div.Profile  
    {color: #000000 !important;
background-color: #aabbbb !important; /* Redundant? - #aabbbb */  
}

  
  
  
.Item.ItemComment.has-ad
    {color: #000000 !important;
background-color: #e3e6f7 !important; /* Redundant? - #668888 */  
  }  
  
  
div.page-sidebar
  {color: #000 !important;
background-color: #e3e6f7 !important; /* Redundant? - #aabbbb */
}  

}
