@-moz-document domain("board.sonicstadium.org") {

/* latin */
@font-face {
  font-family: 'Muli';
  font-style: normal;
  font-weight: 400;
  src: local('Muli Regular'), local('Muli-Regular'), url(https://fonts.gstatic.com/s/muli/v10/z6c3Zzm51I2zB_Gi7146Bg.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}

/* latin */
@font-face {
  font-family: 'Muli';
  font-style: normal;
  font-weight: 700;
  src: local('Muli Bold'), local('Muli-Bold'), url(https://fonts.gstatic.com/s/muli/v10/WQIsflEaABH2ETkzZKPChw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
/* latin */
@font-face {
  font-family: 'Muli';
  font-style: italic;
  font-weight: 400;
  src: local('Muli Italic'), local('Muli-Italic'), url(https://fonts.gstatic.com/s/muli/v10/RGzT9VzvdKojoeSA3nPC6Q.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
/* latin */
@font-face {
  font-family: 'Muli';
  font-style: italic;
  font-weight: 700;
  src: local('Muli Bold Italic'), local('Muli-BoldItalic'), url(https://fonts.gstatic.com/s/muli/v10/nzxeg9Hd0-hLTMmG-EAtNFtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}

.ipsAreaBackground_light {
	background: #483470;
}
#ipsLayout_contentWrapper {
	width: 100%;
	/* background-color: #ffffff;*/
	
	background-color: #EBE3F2;
}
.ipsComment_subComments .ipsComment {
	padding: 10px;
	margin-bottom: 3px;
	background: #e9deff;
}
.ipsBox:not( .ipsBox_transparent):not( .ipsModerated) {
	box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
	background-color: #fff;
}
#ipsLayout_body {
	z-index: 1000;
	margin: 0 auto;
	box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
}
.ipsSideMenu {
	background-color: white;
}
.ipsDataList_large .ipsDataItem {
	border-bottom-width: 1px;
}
.ipsComment,
.ipsComment.ipsBox {
	border: 5px solid transparent;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23) !important;
}
.ipsQuote {
	border-color: #dac8fd #cab5f1;
	border-radius: 3px;
	border-width: 1px;
}
.ipsQuote_citation.ipsQuote_open [data-action='toggleQuote']:before {
	content: "\f13a";
	color: #e9deff;
}
.ipsCommentUnreadSeperator {
	margin: 15px 0;
	height: 0;
	padding: 0px;
	border-width: 2px 0 0 0;
	border-style: solid;
	border-radius: 4px;
	border-color: #FFFFFF;
}
.ipsApp .ipsButton_alternate {
	background: #483470;
	color: #ffffff;
}
.ipsWidget.ipsWidget_horizontal {
	padding: 0 0 10px 0;
	box-shadow: none !important;
}
.ipsComment_subComments .ipsComment {
	padding: 10px;
	margin-bottom: 1px;
	background: white;
	border-bottom: 1px #EBEBEB;
}
.ipsUserPhoto_large img,
img.ipsUserPhoto_large,
.ipsUserPhoto_large:after {
	width: 160px;
	height: 160px;
}
.ipsComment:not( .ipsModerated):not( .ipsComment_selected).ipsBox.ipsComment_popular {
	border-color: #37848b;
	border-width: 3px;
	/* border-top-width: 10px; */
}
.ipsDataList:not( .ipsGrid) .ipsDataItem:last-child,
.ipsDataList.ipsDataList_zebra > .ipsDataItem {
	border-bottom: 0;
	/*background-color: rgba(242, 242, 242, 1);*/
}
.ipsDataList {
	padding: 5px !important;
}
body {
	font-family: "Muli", Arial, sans-serif, "liberation sans";
}
.ipsType_normal {
	font-size: 16px;
}
.ipsDataItem_unread .ipsDataItem_title {
	font-weight: normal;
}
.ipsType_richText {
	word-break: break-word;
	word-wrap: break-word;
	-webkit-hyphens: auto;
	-moz-hyphens: auto;
	hyphens: auto;
}
.ipsImage_thumbnailed {
    border: 0px solid #E2E2E2; 
    padding: 1px;
}
}
