css:
@-moz-document domain("console.api.ai") {
body {
    color: rgb(190, 191, 199);
}
.img-thumbnail,
body {
    background-color: #2b313f;
}
.left-panel .navbar-header.navbar-header-white {
    background: #2b313f;
}
.navbar-default .navbar-nav>.open>a,
.navbar-default .navbar-nav>.open>a:focus,
.navbar-default .navbar-nav>.open>a:hover {
    background-color: #2b313f;
    color: #fff;
}
.scrollbar-forced::-webkit-scrollbar-thumb {
    border-radius: 0px;
    border: #2b313f;
    background-color: rgb(190, 191, 199);
}
.scrollbar-forced::-webkit-scrollbar-track {
    background-color: #2b313f;
    border-left: #2b313f;
}
.navbar-default {
    background-color: #2b313f;
    border-color: #e7e7e7;
}
.navbar-default .navbar-nav>li>a:focus,
.navbar-default .navbar-nav>li>a:hover {
    color: #ffffff;
    background-color: rgb(137, 139, 152);
}
.top-panel .inner-header {
    background: #2b313f;
    border-bottom: #2b313f;
}
.dashboard .content-box,
.dashboard .side-box {
    background: #2b313f;
}
.right-panel .navbar-header {
    background: #2b313f;
}
.right-panel {
    background: #2b313f;
    border-left: #2b313f;
}
.right-panel .navbar-header input {
    background-color: rgb(190, 191, 199);
}
.dropdown-menu {
    background-color: #535b6d;
}
.dropdown-menu>li>a {
    color: rgb(190, 191, 199);
}
.navbar-default .navbar-nav>li>a:focus,
.navbar-default .navbar-nav>li>a:hover {
    color: rgb(255, 255, 255);
    background-color: #535b6d;
}
.navbar-default .navbar-nav>li>a,
.navbar-default .navbar-text {
    color: rgb(190, 191, 199);
}
.left-panel {
    background: #2b313f;
    border-right: #2b313f;
}
.left-panel .navbar-header,
.right-panel .navbar-header {
    border-bottom: #2b313f;
}
.side-form .md-input.ng-valid:not(:focus):hover {
    border-color: rgb(137, 139, 152);
}
.right-panel .navbar-header input {
    background-color: #535b6d;
}
.side-box .text-box {
    background: #535b6d;
}
.form-control,
.form-control.active,
.form-control:active,
.form-control:focus,
.form-control:hover {
    color: #ffffff;
}
.right-panel .navbar-header .input-group {
    background-color: #2b313f;
}
.form-control {
    background-color: #535b6d;
    border: 1px solid #ffffff;
}
.mic-ico {
    color: rgb(190, 191, 199);
}
.search-form .input-group {
    background: #535b6d;
    border: #2b313f;
}
.btn-icon {
    color: rgb(137, 139, 152);
}
.md-button {
    background: #535b6d;
    color: rgb(190, 191, 199);
}
md-menu-content.md-default-theme,
md-menu-content {
    background-color: #535b6d;
}
.list-group-item a {
    color: #fff;
}
.list-group-item {
    background-color: #535b6d;
    border: 1px solid rgb(137, 139, 152);
}
.navbar-default .navbar-nav>li>a,
.navbar-default .navbar-text {
    color: rgb(190, 191, 199);
}
.control-panel .bottom {
    background: #2b313f;
}
.top-panel .toggle-left-panel {
    border-right: #2b313f;
    color: #535b6d;
}
.tab-control {
    border-bottom: #2b313f;
}
#agent-form .top-panel.agents-top-panel {
    background: #2b313f;
}
.tab-control li.active a {
    color: #03a9f4;
}
a:hover {
    color: #03a9f4;
}
container .md-input {
    color: rgb(255, 255, 255);
}
agent-avatar {
    background-color: #535b6d;
}
.form-control[disabled],
.form-control[readonly],
fieldset[disabled] .form-control {
    background-color: #2b313f;
}
.info-box.danger .head,
.info-box.danger .name {
    color: rgb(255, 0, 0);
}
.info-box .head {
    background: #535b6d;
}
md-input-container.md-default-theme .md-input,
md-input-container .md-input {
    color: rgb(255, 255, 255);
    border-color: rgb(137, 139, 152);
}
.api-tools .param {
    background: #535b6d;
    border-left: 1px solid rgb(137, 139, 152);
}
.api-tools>li.select .param {
    background-color: #535b6d;
}
md-select.md-default-theme .md-select-icon,
md-select .md-select-icon,
md-select.md-default-theme[disabled] .md-select-value,
md-select[disabled] .md-select-value,
md-select.md-default-theme[disabled] .md-select-value.md-select-placeholder,
md-select[disabled] .md-select-value.md-select-placeholder {
    color: #535b6d;
}
md-chips.md-default-theme .md-chips .md-chip-input-container input,
md-chips .md-chips .md-chip-input-container input {
    color: #ffffff;
}
md-chips.md-default-theme .md-chips,
md-chips .md-chips {
    box-shadow: 0 1px #535b6d;
}
.content-table th {
    background: rgb(137, 139, 152);
    color: #ffffff;
}
pre code,
table {
    background-color: rgb(137, 139, 152);
}
.content-table tr.autoparams {
    background-color: rgb(137, 139, 152);
}
.rich_response__content md-card-header {
    background: #535b6d;
    border-bottom: 0px;
}
md-card.md-default-theme,
md-card {
    color: rgb(255, 255, 255);
    background-color: rgb(137, 139, 152);
    border-radius: 0px;
}
.sti-multiple-input .prompt-cell.prompt-cell-first {
    background-color: rgb(190, 191, 199);
}
.md-button {
    background: #535b6d;
    background-color: rgb(83, 91, 109);
    color: rgb(190, 191, 199);
}
.b_domain_card {
    border: 0px solid #e7e7e7;
    cursor: pointer;
    -webkit-transition: box-shadow 0.1s, border-color 0.1s;
    position: relative;
    background-color: #535b6d;
}
training .top-panel {
    background: #2b313f;
}
.alert-general-light {
    color: rgb(190, 191, 199);
    background: #535b6d;
}
md-dialog.md-default-theme,
md-dialog {
    background-color: #535b6d;
    color: rgb(190, 191, 199);
}
training-conversations-item.active {
    background-color: #535b6d;
}
.cards .b_card {
    background-color: #535b6d;
    border: 0px solid #e7e7e7;
}
.b-integration-popup .b-domain-popup-header {
    background: #535b6d;
}
.b_integration {
    border: 0px solid #dfe0e2;
}
.b-domain-popup-header {
    border-bottom: 0px solid #e9e9e9;
}
md-autocomplete input:not(.md-input) {
    background: #2b313f;
}
.b-integration-popup md-dialog-actions {
    background: #535b6d;
}
.rich_response .rich_response__message .rich_response__add_content {
    background: #535b6d;
}
.md-button.md-default-theme.md-raised,
.md-button.md-raised {
    color: rgb(190, 191, 199);
    background-color: #535b6d;
}
.md-button.md-default-theme.md-raised:not([disabled]):hover,
.md-button.md-raised:not([disabled]):hover {
    background-color: rgb(137, 139, 152);
}
md-select-menu.md-default-theme md-content,
md-select-menu md-content {
    background: #535b6d;
}
md-select-menu.md-default-theme md-content md-option,
md-select-menu md-content md-option {
    color: rgb(190, 191, 199);
}
.top-panel .toggle-left-panel.active {
    color: #535b6d;
}
.api-tools>li,
.selectmenu,
.selectmenu-menu {
    border: 1px solid rgb(137, 139, 152);
}
.info-box {
    border: 1px solid rgb(137, 139, 152);
}
.agent-tools,
.info-box .head {
    border-bottom: 1px solid rgb(137, 139, 152);
}
.b-training-conversations-list-item {
    border: 1px solid rgb(137, 139, 152);
}
.table-box table th {
    color: #040404;
}
md-select-menu.md-default-theme md-content md-option:not([disabled]):focus,
md-select-menu md-content md-option:not([disabled]):focus,
md-select-menu.md-default-theme md-content md-option:not([disabled]):hover,
md-select-menu md-content md-option:not([disabled]):hover {
    background: #2b313f;
}
.ui-autocomplete {
    background-color: #535b6d;
}
.ui-autocomplete>li>div {
    color: rgb(190, 191, 199);
}
}
