css:
@-moz-document domain(karachan.org) {

body:not(#menu) {
	color: #c1b492;
  /*obrazki tła mocno podjebane xD*/
	background-image: url("https://fauux.neocities.org/15c.gif"), url("data:image/gif;base64,R0lGODlhAwADAJEDANIAAAAAigBzAAAAACH/C05FVFNDQVBFMi4wAwEAAAAh+QQFBAADACwAAAAAAwADAEACBIyFoAUAIfkEBQQAAwAsAAAAAAMAAwBAAgSUgaEFACH5BAUEAAMALAAAAAADAAMAQAIEhIOiBQA7");
	background-repeat: no-repeat, repeat;
	background-position: 125% 10%, 0% 0%;
	background-attachment: fixed, scroll;
	
}

/*.captchaClickHere {
    background-image: url("https://fauux.neocities.org/15c.gif");
}

#postForm.postForm input.captchaField { 
    color: red;
}*/

.haze {
	color: #c1b492;
	background-color: #991b39;
	font-size: 10px;
	font-weight: bold;
	padding: 0px 2px 0px 2px;
}

.postOptions {
	background-color: #991b39;
	color: #c1b492;
	font-weight: bold;
	border: 1px solid #c1b492;
}
	
.postOptionsItem:not(:last-of-type) {
        border-bottom: 1px dotted #c1b492!important;
}


h2.boardHeader {
	background: #000;
	border: 1px solid #c1b492;
}

.ladda-button,.ladda-button .ladda-spinner,.ladda-button .ladda-label{
	-moz-transition:none!important;
}

.ladda-button {
	border: 1px solid #c1b492;
	color: #d2738a;
}

.ladda-button:hover {
	border: 1px solid #c1b492;
	color: #c1b492;
}

.ladda-button[data-loading] {
	background-color: #000!important;
	border: 1px solid #c1b492;
}

.ladda-progress {
	background: #000!important;
}

.ladda-button {
	background-color: #000!important;
}

.ladda-button:hover {
	background-color: #000;
}

.btn {
    color: #d2738a;
    border: 1px solid #c1b492;
    background-color: #000;
}

.btn:hover {
	color: #c1b492;
	background-color: #000;
}

.BBButton {
    color: #d2738a;
    border: 1px solid #c1b492;
    background-color: #000;
}

.BBButton:hover {
	background-color: #000;
}

.tmoved {
	color: yellow;
}

a,a:visited {
	color: #d2738a;
	text-decoration: none!important;
	-webkit-transition-duration: 0.2s;
	outline: none;
}

a.replylink {
	-webkit-transition-duration: 0.2s;
	outline: none;
}

a.replylink:not(:hover) {
	color: #d2738a!important;
	-webkit-transition-duration: 0.2s;
}

a:hover {
	color: #c1b492!important;
}

hr {
	border: none;
	border-top: 0px solid #d2738a;
}

input,textarea,select {
	border: 1px solid  #c1b492;
	background-color: #000;
	color: #c1b492;
}

.submit {
	color: #c1b492;
	background-color: #000!important;
}

a,#absbot a,.button {
	color: #d2738a;
	-webkit-transition-duration: 0.2s;
	/*text-shadow: 0 0 0 #00E0FF, 0 0 11px #00A0B6;*/
	
}

a:hover, #absbot a:hover, .button:hover {
	color: #c1b492!important;
	text-shadow: 0 0 12px #CCCCCC;
}

div.boardBanner>div.boardTitle {
	font-family: inherit;
	text-transform: uppercase;
	color: #c1b492;
	font-weight: normal;
}

div#boardLinks a:hover {
	font-weight: normal;
	padding: 1px;
	color: #d2738a;
	text-shadow: 0 0 12px #CCCCCC;
}

div.sideArrows {
	color: #CCC;
}

div.thread {
	background: rgba(15, 0, 0, 0.50);
	border-left: 1px solid #c1b492;
	border-right: 1px solid #c1b492;
	border-bottom: 1px solid #c1b492;
	padding-bottom: 5px;
	margin-bottom: 10px;
    max-width: 100%;
	/* -hr + bottom */
}

div.reply {
  background: #000;
	color: #c1b492;	
}

div.post div.postInfo span.postNum a {
	text-decoration: none;
	color: #d2738a!important;
	
}

div.post div.postInfo span.postNum a:hover,.posteruid .hand:hover {
	color: #c1b492!important;
}

div.op div.postInfo {
	padding-top: 5px;
	padding-bottom: 5px;
	background: #000;
}

div.post div.postInfo span.nameBlock span.name {
	color: #d2738a;
	-webkit-transition-duration: 0.2s;
}

div.post div.postInfo span.nameBlock span.name:hover {
	font-weight: bold;
	color: #c1b492;
}

div.post div.postInfo span.nameBlock span.postertrip {
	color: #c1b492;
}

.quoteLink,.quotelink,.deadlink {
	color: #d2738a!important;
}

div.postingMode {
	color: #707070;
}

div.pagelist {
	background: #000;
	border-right: 1px solid #c1b492;
	border-left: 1px solid #c1b492;
	color: #c1b492;
}

div.pagelist div.pages a {
	text-decoration: none!important;
	outline: none;
}

div.pagelist strong {
	color: #c1b492;
	font-weight: bold;
}

div.pagelist div.cataloglink {
	border-left: 1px solid #c1b492;
}

#postForm input[type=password] {
	background-color: #000;
	color: #c1b492;
	border: 1px solid #c1b492;
}

table.postForm>tbody>tr>td:first-child {
	color: #c1b492;
}

table.logForm>tbody>tr>td:first-child {
	color: #c1b492;
}

#postForm input[type=text],input[type=password],textarea {
	border: 1px solid #c1b492;
}

#postForm input[type=text]:focus,input[type=password]:focus,input:not([type]):focus,textarea:focus {
	border: 1px solid #c1b492!important;
}

tbody textarea {
	color: #c1b492;
}

.postblock {
	background-color: #EA8;
	color: #800;
	border: 1px solid #c1b492;
}

.highlightPost {
    border: 2px solid #c1b492;
}

.op:target, .op.highlight, .reply:target,.reply.highlight {
    border: 1px dotted #c1b492;
}

span.spoiler {
	color: #000!important;
	background: #000!important;
}

span.spoiler:hover,span.spoiler:focus {
	color: #fff!important;
}

s,s a:not(:hover) {
	color: #991b39;
	background: #991b39!important;
	text-decoration: none;
}

s:hover,s:focus,s:hover a {
	color: #fff!important;
}

.useremail:not(:hover) .name,.useremail:not(:hover) .postertrip {
	color: #d2738a!important;
	text-decoration: none!important;
	transition: none!important;
}

.useremail:hover * {
	color: #c1b492!important;
	transition: none!important;
}

#postForm input[type="text"], textarea {
	border: 1px solid #c1b492;
	background-color: rgba(50, 0, 0, 0.5);
	color: #c1b492;
}

input[type="password"] {
	border: 1px solid #c1b492;
	background-color: rgba(50, 0, 0, 0.5);
	color: #c1b492;
}

#quote-preview {
    background: rgba(0, 0, 0, 1);
	border: 1px solid #c1b492;
}

#captcha > td:nth-child(2) > div:nth-child(2) {
	border: 1px solid #c1b492!important;
}

.blockquoteReply > div:nth-child(2) {
	border: 1px solid #c1b492!important;
}


.fastform > .blockquoteReply > .captchaBlock {
	border: 1px solid #c1b492;
}

#menu {
	background: #d2738a!important;
	color: #d4d4d4;
}

#menu .coll {
	background: #d2738a!important;
	border: 1px dotted #005e7e;
}

#menu .coll:hover {
	background: #00151f;
	border: 1px dotted #005e7e;
}

#menu li a {
	color: #d2738a!important;
	text-decoration: none!important;
}

#menu li a:hover {
	text-decoration: none!important;
	color: #c1b492!important;
}

#menu h2 {
	background-color: rgba(0,0,0,1);
	color: #c1b492;
	border: 1px solid #c1b492;
}

.skandalBox {
	background: none repeat scroll 0% 0% #000;
	border: 1px solid #c1b492;
}

.skandalBox h2 {
	background-color: rgba(0, 0, 0, 0.6);
	border: 1px solid #c1b492;
	color: #c1b492;
}

.skandalBox button {
	border: 1px solid #c1b492;
	background: #000;
	color: #c1b492;
}

.skandalBox button:hover {
	background: none repeat scroll 0% 0% #03454E;
}

table:not(#postForm)>thead>tr>td {
	background-color: #000;
	color: #c1b492;
	border: 1px solid #c1b492;
}

table:not(#postForm)>tbody>tr>th {
	background-color: #000;
	color: #c1b492;
	border: 1px solid #c1b492;
}

table:not(#postForm):not(#fastReplyButtons) tbody tr:nth-of-type(odd) {
	background-color: rgba(0, 0, 0, 1);
}

.box-outer {
	border: 1px solid #c1b492;
}

.boxbar {
	background: rgba(0,0,0,0.6);
	color: #c1b492;
}

.top-box {
	background: rgba(15, 0, 0, 0.85);
}

a:visited {
	color: #d2738a;
	
}

.content {
	color: #c1b492;
	background: rgba(0, 0, 0, 0.5);
}

.content h3 {
	background: #000;
	border: 1px solid #c1b492;
	color: #c1b492;
}

.lnkSmall {
	color: #d2738a;
}

.lnkSmall:hover {
	color: #c1b492!important;
	text-decoration: none;
}

.lnkSmall:visited {
	color: #d2738a;
}

.postnew {
	border-left: 2px solid #c1b492;
	border-right: 2px solid #c1b492;
}

#settingsDiv {
	background: #000;
	border: 1px solid #c1b492;
}

#settingsTitle {
	background: #c1b492;
}

#watcher_box {
	border: 1px solid #c1b492;
	background: rgba(0, 0, 0, 1);
}

#watcher_title {
	background: #00151f;
}

.meny h2 {
	background: #F3DBA7!important;
}

.meny li:hover {
	background: #F3DBA7;
}

.meny a {
	color: #455700!important;
	font-family: sans-serif!important;
	font-size: 86%;
}

.meny-left .meny-arrow {
	border-left: 16px solid #f1dba6!important;
}


div.sub-menu {
	box-shadow: 0px 2px 3px rgba(6, 94, 129, 0.3);
	background: #000;
	color: #d2738a;
}

.modal-cont {
	background: #000000!important;
	border: 1px solid #c1b492;
}

.modal-cont label {
	color: #c1b492!important;
}

.modal-cont input[type="button"] {
	background: #000!important;
	color: #c1b492!important;
	border: 1px solid #c1b492;
	transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275) 0s;
}

.modal-cont input[type="button"]:hover {
	background-color: #d2738a!important;
}

#menu .modal-nav li {
	color: #c1b492;
	border-right: 1px solid #c1b492;
	border-bottom: 1px solid #c1b492;
	background-color: #991b39;
}

#menu .modal-nav li:hover {
	background-color: #000;
}

.group-options a:hover, .group a:hover {
	color: #c1b492!important;
}

.group-options a, .group a {
	color: #d2738a!important;
}

@media only screen and (max-width: 1024px) {

	#open {
	color: #00E0FF!important;
	}
	a.tab {
	color: #CCC!important;
	}
	.modal-cont {
	border: none!important;
	}
	#menu-close {
	color: #CCC!important;
	}
	.group a {
	background: #000!important;
	border: 1px solid #c1b492;
	}
	#menu {
	background: #000!important;
	color: #FFF;
	}
	a.tab:hover, a.tab-opened {
	background: #000!important;
	}
	#just-a-menu-bg {
	background: #c1b492;
	}
}



.ui-corner-all {
	background: rgba(0, 0, 0, 0.7);
	border: 1px solid #c1b492;
}

.ui-widget-header {
	background: rgba(0,0,0,0.6);
	color: #CCC;
	border: 1px solid #c1b492;
}
#charsCounterNormal {
	color: #FFF;
}

#charsCounterFastReply {
	color: #FFF;
}
}
