/*This style has been written to have Twitter's hearts replaced by the good ol' fav star.
Current Version: 1.4 #NewTwitter Third Release.

Created by: Sighij
Contact me on Twitter: @lordsighij
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

  
.GalleryTweet.GalleryTweet--small .HeartAnimation, .HeartAnimation {
    background-image: url(data:image/png;base64,); 
      
     /* For reasons Stylish doesn't allow to load images from external links, so I use URI to code it there: TROLOLOLOLOLOLOLLOLÓ! */
		}
		
  .tweet.Tweet--invertedColors .HeartAnimation {
    background-image: url(data:image/png;base64,);
}

  
  
  
  
	
	.HeartAnimation{ 
  position: absolute;
	background-position:left;
	background-repeat:no-repeat;
	background-size:2900%;
	height:50px;
	width:50px;
	left:50%;
	top:50%;
  -ms-transform:translate(-50%,-47.5%);
	transform:translate(-50%,-47.5%);

}
	
    .Icon--heart:before {
	  content:"☆";
		font-size: 22px;
}
.Icon--heartBadge:before {
	content:"☆";
		font-size: 22px;
}

	.Icon--colorHeart,.Icon--circleActiveHeart,.Icon--heartBadge {
	color:#fab105
}
	
.tweet.show-undesirable-visual-indicator .time a {
	color:#fab105
}
	
    .ProfileTweet-action--favorite:hover .ProfileTweet-actionButton,.ProfileTweet-action--favorite:hover .ProfileTweet-actionCount,.ProfileTweet-action--favorite .ProfileTweet-actionButton:focus,.ProfileTweet-action--favorite .ProfileTweet-actionButton:focus .ProfileTweet-actionCount,.favorited .ProfileTweet-action--favorite .Icon--heart,.favorited .ProfileTweet-action--favorite .ProfileTweet-actionButtonUndo {
	color:#fab105
}
	
	.stream-container .grid-tweet .grid-tweet-actions .favorite .Icon--heart:hover,.stream-container .grid-tweet .grid-tweet-actions .unfavorite .Icon--heart {
	color:#fab105
		}
		
		body:not(.edge-design) .tweet>.context .with-icn .Icon--heartBadge {
			color:#fab105
				} 
	
		.GalleryTweet.GalleryTweet--small .ProfileTweet-action--favorite .ProfileTweet-actionButtonUndo .ProfileTweet-actionCount {
	color:#fab105
}
	
		.GalleryTweet.GalleryTweet--small .ProfileTweet-action--favorite:hover .ProfileTweet-actionButton,.GalleryTweet.GalleryTweet--small .ProfileTweet-action--favorite:hover .ProfileTweet-actionCount {
	color:#fab105
}
	
		.FoundMediaSearch-categoryContainer--bg1,.FoundMediaSearch-itemContainer--bg1 {
	background:#fab105
}	
	
	.UIWalkthrough-step--like .UIWalkthrough-icon {
	background-color:#fab105
}
	
.UIWalkthrough-step--like .UIWalkthrough-title {
	color:#fab105
}	
	.tweet.Tweet--invertedColors .ProfileTweet-action--favorite .ProfileTweet-actionButtonUndo .ProfileTweet-actionCount,.tweet.Tweet--invertedColors .ProfileTweet-action--favorite:hover .ProfileTweet-actionButton,.tweet.Tweet--invertedColors .ProfileTweet-action--favorite:hover .ProfileTweet-actionCount {
		color:#fab105 !important;}	
		
}
    

