css:
@-moz-document domain("mangachan.me") {
body {
    background: #000!important;
    background-color: #212121!important;
}
.main_fon, #pagination, #pagination_related {
    background-color: #2f2f2f!important;
    border-color: #560000!important;
}
.content_row {
    background: #353535!important;
    border: 1px solid #001c46!important;
}
.manga_row1 > div{
	background-color: #525252!important
}
.item, .item2, .tags, .manga_images, #pagination > b, #description, div  {
	color: #b1b1b1!important
}
.manga_row2 h3 a, .manga_row3 h3 a, .genre a, .translation a, a {
	color: #ffffff!important;
}
.manga_row2 h3 a:hover, .manga_row3 h3 a:hover, .genre a:hover, a:hover , .title_top_a:hover {
	color: #ff4c4c!important;
}
#footer{
	display:  none!important
}
div{ 
	border: 0px solid #000000!important
}
#pagination a, #pagination_related a {
    background: #848484!important;
    color: #000000!important;
    border: 1px solid #292929!important
}
#pagination a:hover, #pagination_related a:hover {
    background: rgba(204, 0, 0, 0.3)!important;
    border-color: #380d0d!important;
    color: #000000!important;
}
#pagination span b {
    color: #c3c3c3!important;
}
#top_left li a, #posting_top_left li a, #bot_left li a {
    color: #b1b1b1!important;
}
.extaraNavi {
    border-bottom: 1px solid #0086fb!important;
    background-color: #525252!important;
}
.comment_top, #info_wrap > div {
	background-color: #696969!important
}
.extra_on {
    background: #696969!important;
    border: 1px solid #0086fb!important;
}
.zaliv {
    background-color: #464646!important;
}
.no_zaliv {
    background-color: #4e4e4e!important;
}
.table_cha {
    border: 2px solid #000!important;
    box-shadow: 2px 2px 6px 1px black!important;
}
.table_cha .zagol_manga, .table_cha .zagol_date {
    background-color: #1b1b1b!important;
}
#add_comment {
    background-color: rgba(0, 0, 0, 0)!important;
}
a > .ext:hover {
	background-color: #bb0000!important;
}
#comments {
	background-color: #5a5a5a!important;
    color: #0096db!important;
}
#dle_emo {
	background-color: #292929!important;
}
.ui-dialog .ui-dialog-title{
	color: black!important
}
.area_right {
    background-color: #484848!important;
    border: 1px solid #000000!important;
	margin-bottom: 0px!important
}
.content_row_week {
    background: rgba(68, 68, 68, 0)!important;
}
.area_rightSpace, .area_rightSpace > span, .area_rightSpace > span > a {
    background: #232323!important;
    color: #cacaca!important;
}
.area_rightSpace {
	    box-shadow: 0 0 2px 4px rgb(0, 0, 0)!important;
}
.manga_row1_week > div {
	background-color: #272727!important
}
.baseTitle, .baseTitle > div > a {
    background: #272727!important;
    color: #a0a0a0!important;
}
#navigator {
    background: #000000!important;
}
#navigator .main_set li a.active, #navigator .main_set a:hover {
    background: #383838!important;
}
#extra_main_news {
	background-color: rgb(72, 72, 72)!important;
    border: 1px solid rgb(0, 0, 0)!important;
}
#extra_main_news > div {
	background-color: rgb(72, 72, 72)!important;
}
div {
	background-color: rgba(0, 0, 0, 0)!important;
}
#navigator .navigator_menu ul {
    background-color: #353535!important;
    box-shadow: 0 0 8px 3px black!important;
}
.profile_ch_no_zaliv {
    background-color: #464646!important;
}
.profile_ch_zaliv {
    background-color: #2d2d2d!important;
}
.profile_ch_zaliv  > a , .profile_ch_no_zaliv > a {
    color: #969696!important;
}
.profile_ch_zaliv  > a:hover , .profile_ch_no_zaliv > a:hover {
    color: #ff4c4c!important;
}
.table_cha a {
    color: #b7b7b7!important;
}
.title_top_a {
	color: #1d1d1d!important;
}
.name_row h1, .related_info h2 a {
	text-shadow:0px 0px 0px rgba(19, 19, 19, 0.19)!important
}
span > b, span > font, .manga_images > div > font > b {
	color: #00ff21!important;
}
#top_user > a, #top_site > a {
	color:black!important
}

}

@-moz-document regexp("http://mangachan.me/related/?.*"), regexp("http://mangachan.me/download/?.*") {
#content > div{
	background-color: rgba(255, 255, 255, 0)!important;
}
#right > div {
	background-color: #464646!important;
    padding: 10px!important;
    margin-bottom: 10px!important;
    box-shadow: 15px 0px 6px 0px #464646!important;
}
}
