css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("klart.se") {
    
iframe, .ad-wrapper.-desktop:not(.is-hidden), .page-place-days-weather .notification-wrapper .sponsor-message, .announcement,  #overlay {
    display: none !important;}

.ad-wrapper.-banner {height: 0px !important;}

html, body, #search-modal {
    background: rgba(231,240,247,1)  !important;
    font-family: MS Shell Dlg \32 !important;
    font-weight: 100 !important;
    text-shadow: 1px 1px 0 rgba(255,255,255, .33) !important;}

#day-1, #day-8{ 
    margin-left: 10px;}

.place-day {
    margin-bottom: 20px;
    width: 157px;}

.place-days > .scroll {
    display: flex;  
    flex-wrap: wrap;
    overflow-x: hidden;}

.place-days.horizontal-scroll.-initial .scroll-indicator, .horizontal-scroll .scroll-indicator { 
    background: transparent !important;}
    
.page-place-days-weather  .scroll-btn { 
    display:none !important;}

.site-header, .page-place-days-weather .print-link {
    padding-left: 10px;}

.btn.-search {
    width: 81px;}

.site-wrapper > .page {
    box-shadow:   0px -2px 1px  rgba(0,0,0,.1) !important;
    border-radius: 4px 4px 0 0  !important;
    background-color: rgba(231,240,247,1) !important;
    width: calc(100%) !important;
    min-width: 1280px !important;}

.page-header.-boxed {
    background-color: rgba(231,240,247,1) !important;}

.nav-tabs > .list > .tab.-active > .btn, .btn.-paging.-disabled, a.btn.-paging, a.btn.-paging.-number.-disabled, .scroll-btn {
    color: #00357b !important;
    border: 2px solid;
    -moz-border-top-colors: rgba(255,255,255,.08) rgba(0,0,0,.5) !important;
    -moz-border-right-colors: rgba(255,255,255,.7) rgba(0,0,0,.15) !important;
    -moz-border-left-colors: rgba(255,255,255,.08) rgba(0,0,0,.3) !important;
    -moz-border-bottom-colors: rgba(255,255,255,1) rgba(200,200,200,.9) !important;
    box-shadow: 0px 1px 3px rgba(255,255,255,.5), 0px -1px 10px rgba(255,255,255,.5), inset 0px 0px 10px rgba(0,0,0,.05) !important;
    background: rgb(221 ,230, 237) !important;
    border-radius: 4px !important; }

.nav-tabs > .list > .tab.-active > .btn:hover {
    background: linear-gradient(to top, rgba(180,180,180,.2), rgba(180,180,180,.4)) !important; }

.place-link-list > .item > .link, a.btn.-number{
    color: #00357b !important;
    border: 2px solid;
    -moz-border-top-colors: rgba(255,255,255,.08) rgba(0,0,0,.5) !important;
    -moz-border-right-colors: rgba(255,255,255,.7) rgba(0,0,0,.15) !important;
    -moz-border-left-colors: rgba(255,255,255,.08) rgba(0,0,0,.3) !important;
    -moz-border-bottom-colors: rgba(255,255,255,1) rgba(200,200,200,.9) !important;
    box-shadow: 0px 1px 3px rgba(255,255,255,.5), 0px -1px 10px rgba(255,255,255,.5), inset 0px 0px 10px rgba(0,0,0,.05) !important;
    background: rgb(221 ,230, 237) !important;
    border-radius: 4px !important;}

.nav-tabs > .list > .tab > .btn, .nav-tabs > .place-link-list.list > .item.tab > .link:hover, .places-list .nav-tabs > .place-link-list.list > .place.-secondary-action.item.tab > span:hover, .nav-tabs > .list > .tab > .btn:focus, .nav-tabs > .place-link-list.list > .item.tab > .link:focus, .places-list .nav-tabs > .place-link-list.list > .place.-secondary-action.item.tab > span:focus, .nav-tabs > .list > .tab > .btn:active, .nav-tabs > .place-link-list.list > .item.tab > .link:active, .places-list .nav-tabs > .place-link-list.list > .place.-secondary-action.item.tab > span:active, a.btn.-paging.-number, .place-link-list > .item > .link:hover {
    color: #00357b !important;
    font-weight: bold; 
    text-shadow: 1px 1px 0px #fff;
    border-width: 2px !important;
    border: 2px solid ;
    -moz-border-top-colors: rgba(255,255,255,.7) transparent !important;
    -moz-border-right-colors: rgba(255,255,255,.6) rgba(222,222,222,.1) !important;
    -moz-border-left-colors: rgba(150,150,150,.3) rgba(255,255,255,.3) !important;
    -moz-border-bottom-colors: rgba(0,0,0,.3) transparent !important;
    background: linear-gradient(to bottom, #cddae5 0%, #dde6ed 100%) !important;
    box-shadow: inset 0px 1px 0px rgba(255, 255, 255, 0.3), 0px 1px 2px rgba(0,0,0, 0.3) !important;
    border-radius: 4px !important;}

.place-day {
    border-radius: 4px !important;
    border-width: 1px !important;
    border-style: solid !important;
    border: 1px solid #0092C9!important;
    background: linear-gradient(to bottom, #1ab2ff 0%, #33c9ff 100%) !important;
    background-repeat: repeat-x;
    box-shadow: inset 0px 1px 0px rgba(255, 255, 255, 0.3), 0px 1px 2px rgba(0, 0, 0, 0.5) !important;}

.item.-secondery.-last.visible-medium-up {
    background-color: transparent !important;
    border-radius: 0 0 4px 4px !important;}

.place-day .item.-secondery{background-color: transparent !important;}

.place-day .date .time, .place-day .temp-low {
    color: #595959;}

body{
    background: linear-gradient(transparent, rgba(231,240,247,1)), url("http://i.imgur.com/pFGu3OH.jpg") no-repeat center top!important;
    background-size: cover!important; 
    height: 400px;
    padding: 10px 0;
    border-top: 1px solid #e7f0f7;}

.site-footer {
    background: linear-gradient(rgba(231,240,247,1), transparent), url("http://i.imgur.com/4UiyC8h.png") no-repeat center bottom!important;
    background-size: cover!important; margin-bottom: -20px !important;}

.site-footer .content {
    width: calc((100% / 3) - 0px) !important;
    padding-left: 100px;
    text-align: center !important;}

.site-footer .link, .site-footer .places-list .place.-secondary-action > span, .places-list .site-footer .place.-secondary-action > span {
    margin-right: 60px;}

.popular-places .heading, ul.links{ 
    text-align: center !important;}

.site-footer .publisher { 
    text-align: center !important;
    margin-right: 120px !important;}

.popular-places .list .item {
    width: calc((100% / 9) - 100px / 9) !important;
    margin-right: 10px !important;
    text-align: center !important;}
    
.page-header > .heading small{ 
    display: none !important;}
    
.popular-places .heading {
    margin-bottom: 10px;}
    
.popular-places {
    margin: 10px;
    border-top: solid 0px ;
    margin-top: 20px;
    padding-top: 0px;}
    
.place-days > .scroll {
    padding-bottom: 0px;}
    
.site-footer .links {
    margin-left: 70px;
    margin-bottom: 15px;}
    
.weather-sponsor .logo {
    display: none !important;}

.search-form .field {
    border-radius: 4px;
    border-width: 2px !important;
    width: calc(100% - 48px - 48px) !important;}

.form-field {
    border: 2px solid;
    -moz-border-top-colors: rgba(255,255,255,.08) rgba(0,0,0,.5) !important;
    -moz-border-right-colors: rgba(255,255,255,.7) rgba(0,0,0,.15) !important;
    -moz-border-left-colors: rgba(255,255,255,.08) rgba(0,0,0,.3) !important;
    -moz-border-bottom-colors: rgba(255,255,255,1) rgba(200,200,200,.9) !important;
    box-shadow: 0px 1px 3px rgba(255,255,255,.5), 0px -1px 10px rgba(255,255,255,.5), inset 0px 0px 10px rgba(0,0,0,.05) !important;
    background: linear-gradient(to top left, transparent, rgba(0,0,0,.05)), linear-gradient(to bottom, transparent, rgba(0,0,0,.02)) !important;
    border-radius: 4px !important;}

.btn.-search, .btn.-favorite, .btn.-simple, .btn.-alternative, .place-hours .head .btn-graph, .page-place-graph-weather .btn-list{
    margin-right: 2px !important;
    height: 41px;
    line-height: 20px !important;
    font-size: 1.5rem;
    font-weight: 400;
   color: #00357b !important;
    font-weight: bold; 
    text-shadow: 1px 1px 0px #fff;
    border-width: 2px !important;
    border: 2px solid ;
    -moz-border-top-colors: rgba(255,255,255,.7) transparent !important;
    -moz-border-right-colors: rgba(255,255,255,.6) rgba(222,222,222,.1) !important;
    -moz-border-left-colors: rgba(150,150,150,.3) rgba(255,255,255,.3) !important;
    -moz-border-bottom-colors: rgba(0,0,0,.3) transparent !important;
    background: linear-gradient(to bottom, #cddae5 0%, #dde6ed 100%) !important;
    box-shadow: inset 0px 1px 0px rgba(255, 255, 255, 0.3), 0px 1px 2px rgba(0,0,0, 0.3) !important;
    border-radius: 4px !important;}

.place-hours .head .btn-graph, .page-place-graph-weather .btn-list {
    width: 91px;}
    
.page-pollen .btn.-simple {
    width:150px; height:41px;}
    
.page-pollen .pollen-map {
    width: 490px;}
    
.page-pollen .pollen-map .type-selector {
    margin-left:60px;margin-right: 10px !important;}
  
.page-pollen .pollen-map .type-selector p {
    text-align: center ;}
 
.select.select {
    width:110px; 
    height:41px;}

.head .btn-graph {
    margin-right: 1px !important;}

.rangeslider-horizontal .rangeslider__handle {
    height: 41px;
    text-shadow: 1px 1px 0 rgba(255,255,255, .33) !important;
    border-width: 2px !important;
    border-style: solid !important;
    border: 1px solid #0092C9!important;
    background: #33c9ff  url("https://assets.klart.se/assets/img/range-slider.png") no-repeat 50%;
    background-size: 55%;
    background-repeat: no-repeat;
    border-radius: 4px  !important;
    box-shadow: inset 0px 1px 0px rgba(255, 255, 255, 0.3), 0px 1px 2px rgba(0, 0, 0, 0.5) !important;}

.link {
    color: #00357b;
    text-decoration: none;
    border-bottom: 1px solid transparent;}

.place-hours > .day > .head {
    border:none;
    padding: 5px 10px;
    background: transparent;} 

.place-hours .head .col {
    text-align: center !important;}

.place-hours > .day, .hours-list > .content > .row {
    border-bottom: 1px solid transparent;}

.place-hours .head .row:first-child {
    padding-bottom: 5px !important;} 

.place-hours .head .row, .place-hours .head .col.-time {
    font-weight: bold;}

.place-hours {
    width: 865px !important;}

.select.select {
    color: #00357b !important;
    font-weight: bold; 
    text-shadow: 1px 1px 0px #fff;
    border-width: 2px !important;
    border: 2px solid ;
    -moz-border-top-colors: rgba(255,255,255,.7) transparent !important;
    -moz-border-right-colors: rgba(255,255,255,.6) rgba(222,222,222,.1) !important;
    -moz-border-left-colors: rgba(150,150,150,.3) rgba(255,255,255,.3) !important;
    -moz-border-bottom-colors: rgba(0,0,0,.3) transparent !important;
    background: linear-gradient(to bottom, #cddae5 0%, #dde6ed 100%) !important;
    box-shadow: inset 0px 1px 0px rgba(255, 255, 255, 0.3), 0px 1px 2px rgba(0,0,0, 0.3) !important;
    border-radius: 4px !important;
    padding: 10px 20px 10px 15px;}
   
.page-pollen .pollen-details .toggle-container {
    background: transparent !important;}   

.select option {
    background: #fff;}
    
#viewerContainer {
    overflow:initial !important;}
}
