css:
@-moz-document domain("isas.zshulin.cz") {
/* html tagy */

*
{
	border: none;
	margin: 0;
	padding: 0;
}

body
{
    padding: 0;
    font-size: 0.75em;
    font-family: 'Roboto', Helvetica, Arial, sans-serif;
    background-color: black;
}

a
{
    text-align: center;
    color: #000000;
    font-weight: bold;
    text-decoration: none;
}

a:hover
{
    text-decoration: underline;
}

hr /* oddělovač skupiny */
{
    height: 0px;
    background-color: #ccc;
    margin: 0;
}

/* rozvržení vzhledu */

#stranka
{
    color: #000000;
    background: #b9b8b8;
    border: noen;
    border-collapse: separate;
    border-spacing: 0;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    width: 100%;
}

#stranka td.zahlavi
{    
    padding: 6px;
    vertical-align: middle;
    background: #b9b8b8;
}

#stranka td.menu
{
    width: 210px;
    height: 500px;
    vertical-align: top;
}

#stranka td.obsah
{
    padding: 0.5eM 0.5eM;
    background-color: #a59e9e;
    vertical-align: top;
    border: none;
}

#stranka td.zapati
{
    padding: 2px 8px;
    color: #606060;
    font-size: 8pt;
    text-align: center;
    vertical-align: middle;
}

/* prvky vzhledu */

#nazev-skoly
{
    font-size: 14pt;
    font-weight: bold;
    white-space: nowrap;
}

#adresa-skoly
{
    font-weight: bold;
    white-space: nowrap;
}

#minimalni-sirka
{
    min-width: 700px;
    height: 0;
}

#sirka-menu
{
    min-width: 210px;
    height: 0;
}

.levy-blok {
	float: left;
	padding: 4px 0;
}

.pravy-blok {
	height: 100%;
    float: right;       
}

.uklizec {
	clear: both;
}

/* iSASMenu - Hlavní nabídka */

#isas-menu /* seznam položek menu */
{
    margin: 0;
}

#isas-menu li /* běžná položka menu */
{
    list-style-type: none;
    border: none;
    padding: 1px 3px;
    margin: 0px 0px 2px 0px;
}

#isas-menu li a /* odkaz v menu */
{
    color: #000000;
    font-weight: normal;
    text-decoration: none;
}

#isas-menu li a:hover
{
    text-decoration: underline;
}

#isas-menu .polozka-menu /* položka menu */
{
    padding: 5px;
}

#isas-menu .polozka-menu :nth-child(11) {
	background: red;
}

#isas-menu .sekce-menu /* sekce menu */
{
    font-weight: bold;
    background: #000;
    padding: 5px;
    color: #fff;
}

#isas-menu .sekce-menu a /* odkaz v sekci menu */
{
    font-weight: bold;
    background: none;
    color: #fff;
}

/* iSASBar.class - trámec */

.isas-tramec /* trámec */
{
    border: none;
    white-space: nowrap;
    margin: 0 0 0.5eM 0;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
}

.isas-tramec .titulek
{
    font-weight: bold;
    background-color: #000;
    padding: 4px 6px;
    color: #fff;
}

.isas-tramec .odkazy
{
    border: none;
    padding: 4px 8px;
}

/* iSASWarning.class - chyba pro koncového uživatele */

.isas-varovani /* oddíl s chybou */
{
    color: #000000;
    background-color: #ffffff;
    padding: 0.4eM 0.6eM;
}

.isas-varovani .text /* chybové hlášení */
{
    color: #f00000;
}

.isas-varovani .duvod /* důvod chyby */
{
}

.isas-varovani .reseni /* řešení chyby */
{
}

/* iSASSwitch.class - přepínač */

.isas-prepinac /* oddíl přepínače */
{
    margin: 0 0.4eM 0.4eM 0.4eM;
}

.isas-prepinac .skupina/* skupina voleb */
{
    display: inline;
}

.isas-prepinac .prepinac /* volba přepípanče */
{
}

.isas-prepinac .vybrany /* aktuální volba */
{
}

.isas-prepinac .vybrany a /* odkaz aktuální volby */
{
    color: #000000;
}

.isas-prepinac .nevybrany /* neaktuální volba */
{
}

/* iSASNavigation.class - navigace přes třídy a žáky */

.isas-navigace /* oddíl navigace */
{
}

.isas-navigace .tridy /* navigace přes třídy */
{
    padding: 0.5eM 2eM 1eM 1eM;
    margin-right: 1eM;
    float: left;
}

.isas-navigace .zaci /* navigace přes žáky */
{
    padding: 1eM 2eM 1eM 1eM;
    margin-right: 1eM;
    float: left;
}

.isas-navigace .zahlavi /* nadpis navigace */
{
    font-weight: bold;
    margin-top: 0.5eM;
    margin-bottom: 0.5eM;
}

.isas-navigace ul /* všechny seznamy navigace */
{
    margin-left: 2eM;
    list-style-type: none;
}

.isas-navigace .seznam-rocniku /* seznam ročníků */
{
    margin-left: 1eM;
    margin-bottom: 0.5eM;
}

.isas-navigace .seznam-klas-skupin /* seznam klasifikačních skupin */
{
    margin-left: 1eM;
    margin-bottom: 0.5eM;
}

.isas-navigace .seznam-trid /* seznam tříd */
{
    margin-left: 1eM;
}

.isas-navigace .seznam-zaku /* seznam žáků */
{
    margin-left: 1eM;
}

/* iSASTable.class - generovaná tabulka - seznam */

.isas-tabulka /* tabulka */
{
    width: 100%;
    margin: 0 auto;
    color: #000000;
    background: #a59e9e;
    border: none;
    border-collapse: separate;
    border-spacing: 2px;
    border-radius: 3px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
}

.isas-tabulka td /* buňka tabulky */
{
    padding: 2px 6px;
}

.isas-tabulka .lichy /* buňka tabulky na lichém řádku */
{
    background-color: #b9b8b8;
}

.isas-tabulka .sudy /* buňka tabulky na sudém řádku */
{
    background-color: #b9b8b8;
}

.isas-tabulka .zahlavi /* buňka tabulky v záhlaví */
{
    background-color: #0e0e0e;
    font-weight: bold;
    color: #fff;
}

.isas-tabulka .zapati /* buňka tabulky v zápatí */
{
    background-color: #fff;
}

/* iSASCard.class - generovaná tabulka - karta */

.isas-karta
{
    margin: 0 0 0.5eM 0;
    border: none;
}

.isas-karta-zalozky /* seznam záložek */
{
    padding: 2px 6px 1px 9px;
    width: 100%;
}

.isas-karta-zalozky .zalozka /* záložka */
{
    border: none;
    padding: 2px 6px 1px 9px;
    margin: 0px 2px 0px 0px;
    float: left;
    white-space: nowrap;
}


.isas-karta-zalozky .vybrana a /* odkaz aktulání záložky */
{
    color: #000000;
	background: none;
}

.isas-karta-polozky /* tabulka položek karty */
{
    border-collapse: separate;
    border: none;
    border-spacing: 2px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    width: 700px;
    clear: left;
}

.isas-karta-polozky td /* buňka tabulky karty */
{
    padding: 2px 6px;
}

.isas-karta-polozky .nazev /* buňka s popiskou */
{
}

.isas-karta-polozky .hodnota /* buňka s hodnotou */
{
}

.isas-karta-polozky .samotny /* samostatná buňka na řádku */
{
}

.isas-karta-polozky .skupina /* skupina */
{
    color: #606060;
    font-size: 80%;
    font-weight: bold;
}

.isas-karta-polozky .sudy  /* buňka na sudém řádku */
{
    background-color: #fff;
}

.isas-karta-polozky .lichy /* buňka na lichém řádku */
{
    background-color: #ffffff;
}

/* iSASDiary.class - zápisník*/

.isas-zapisnik
{
    padding: 0.4eM;
}

.isas-zapisnik .neformatovany /* neformátovaný zápisník */
{
    font-family: monospace;
}

.isas-zapisnik .formatovany /* formátovaný zápisník */
{
}

.isas-zapisnik .prazdny /* prázdný zápisník */
{
    color: #a0a0a0;
    font-size: 9pt;
}

/* oznámení na informační nástěnce */

.isas-oznameni /* rámec oznámení */
{
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    padding: 0.5eM 1eM;
    margin-bottom: 1eM;
}

.isas-oznameni.sloupce {
	margin-right: 1eM;
}

.isas-oznameni.typ-3 .text {
	position: relative;
	height: auto;
	padding-left: 1.1eM;	
}

.isas-oznameni .zahlavi /* nadpis oznámení */
{
    font-weight: bold;
    margin: 0.4eM 0;
    padding: 0 0 0.4eM 0;
}

.isas-oznameni .zahlavi span.nove {
	position: relative;
	bottom: 0.5eM;
	font-size: 80%;
	font-weight: normal;
	font-style: italic;
	color: #ff6600;	
}

.isas-oznameni .text /* text oznámení */
{
    margin: 0.4eM 0;
    overflow: auto;
}

.isas-oznameni .odkazy /* odkazy oznámení */
{
    font-size: 90%;
}

.isas-oznameni .odkazy ul /* seznam odkazů oznámení */
{
    padding-left: 3eM;
}

.isas-oznameni .odkazy a /* odkaz oznámení */
{
    text-decoration: underline;
    font-weight: normal;
}

.isas-oznameni .paticka /* doplňující informace */
{
    font-size: 8pt;
    margin: 0.4eM 0;
    padding: 0.4eM 0;
}

.isas-oznameni .podbarveni-titulek 
{
	width: 1eM;
	height: 1eM;
	display: block;
	float: left;
	margin-right: 0.5eM;
}

.isas-oznameni .podbarveni-titulek.prazdny 
{
}

.isas-oznameni .podbarveni-text 
{
	position: absolute;
	left: 0;
	width: 1eM;
	top: 5%;
	height: 90%;
}

.isas-oznameni .podbarveni-text.prazdny 
{
}

/* plán akcí */

.isas-akce /* rámec akce */
{;
    -webkit-border-radius: 7px;
    -moz-border-radius: 7px;
    border-radius: 7px;
    padding: 0.5eM 1eM;
    margin-bottom: 1eM;
}

.isas-akce a /* odkaz */
{
    color: #000000;
    font-weight: normal;
}

.isas-akce .titulek /* titulek akce */
{
    font-weight: bold;
    margin: 0.4eM 0;
    padding: 0 0 0.4eM 0;
}

.isas-akce .popis /* popis akce */
{
    margin: 0.4eM 0;
}

.isas-akce .upozorneni /* popis akce */
{
    color: #f00000;
}

/* hledání knihy */

.isas-sk-vysledky-hledani /* výsledky hledání */
{
}

.isas-sk-vysledky-hledani .zahlavi /* nadpis */
{
    font-weight: bold;
    margin-bottom: 1eM;
}

.isas-sk-vysledky-hledani .vysledek /* položka výsledku */
{
    margin-bottom: 1eM;
}

.isas-sk-vysledky-hledani .titul /* titul */
{
}

.isas-sk-vysledky-hledani .autor /* autor */
{
}

.isas-sk-vysledky-hledani .ev-skupina /* část školy */
{
}

/* iSASTimetable - rozvrh hodin */

.rozvrh-hodin /* tabulka s rozvrhem hodin */
{
    border-collapse: separate;
    border-spacing: 2px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    margin-bottom: 0.5eM;
}

.rozvrh-hodin td /* buňka rozvrhu */
{
    text-align: center;
    padding: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
}

.rozvrh-hodin .nazev /* buňka záhlaví */
{
    background-color: #ffdead;
    font-weight: bold;
}

.rozvrh-hodin .zahlavi /* buňka záhlaví */
{
    background-color: #ffdead;
    font-size: 9pt;
}

.rozvrh-hodin .den /* buňka s označením dne */
{
    background-color: #ffdead;
}

.rozvrh-hodin .listek /* buňka s lístkem */
{
}

.rozvrh-hodin .prazdne /* prázdná buňka */
{
    border: none;
}

.rozvrh-hodin .obed /* oběd */
{
}

.rozvrh-hodin .suplovaci-pohotovost /* suplovací pohotovost */
{
}

.rozvrh-hodin .specialni-hodina /* speciální hodina */
{
}

/* iSASSupply - suplování */

.suplovani /* tabulka se suplováním */
{
    border-collapse: separate;
    border-spacing: 2px;
    margin-bottom: 0.5eM;
}

.suplovani td /* buňka suplování */
{
    text-align: center;
    padding: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
}

.suplovani .zahlavi /* buňka záhlaví */
{
    background-color: #ffdead;
    font-size: 86%;
}

.suplovani .listek /* buňka s lístkem */
{
}

.suplovani .prazdne /* prázdná buňka */
{
    border: none;
}

.suplovani .zruseny {
	font-weight: normal;
	text-decoration: line-through;
}

.suplovani .suplovany {
    font-weight: bold;
}

/* iSASCalendar - kalendář */

.kalendar /* tabulka kalendáře */
{
    border-collapse: separate;
    border-spacing: 2px;
    margin-bottom: 0.5eM;
}

.kalendar td /* buňka kalendáře */
{
    text-align: center;
    padding: 2px;
    border-radius: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
}

.kalendar .zahlavi-navigace /* buňka záhlaví navigace */
{
    background-color: #ffdead;
    font-weight: bold;
}

.kalendar .zahlavi-datum /* buňka záhlaví */
{
    background-color: #ffdead;
}

.kalendar .nazev-dne /* název dne */
{
    background-color: #ffdead;
}

.kalendar .den /* den v kalendáři */
{
    width: 48px;
    height: 40px;
    font-size: 18pt;
}

.kalendar .dnes /* aktuální den v kalendáři */
{
    background-color: #ffdead;
}

.kalendar .vybrane {
    background-color: #e0e0e0;
}

.kalendar .prazdne /* prázdná buňka */
{
    width: 48px;
    height: 40px;
    color: #a0a0a0;
    border: none;
}

/* volitelné informace po otevření aplikace/přihlášení uživatele  */

.informacni-text /* Informační text pro uživatele */
{
    margin-bottom: 0.6eM;
    padding: 0.5eM 0.8eM;
    border-radius: 5px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
}

/* obchodní sdělení */

#obchodni-sdeleni {
	clear: both;
}

#obchodni-sdeleni .sdeleni
{
	float: left;
	width: 33%;		
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
	min-width: 250pt;
	margin: 0 1eM 1eM 0;
}

#obchodni-sdeleni .titulek
{
	font-weight: bold;
    background-color: #ffdead;
    padding: 4px 6px;
	
}

#obchodni-sdeleni .obsah
{
	
    color: rgb(0, 0, 0);
    padding: 10px;
	text-align: justify;
}
}
