css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("theverge.com") {

.c-social-buttons,
.main-social,
.l-col__sidebar,
.c-related-list,
.c-river,
.c-river--,
.c-global-header__social {
    display: none !important;
}

body,
.l-main-content,
.l-hub-wrapper {
    background-color: #2b2a2a;
}

cite,
.l-segment,
.l-main-content,
.c-page-title {
    color: white;
}

.c-entry-box--compact {
    background-color: black;
}

.c-comments__recommended {
    background: #937c5c;
}

}
