css:
@-moz-document regexp("https://www.chatwork.com/[^/]*") {


/* chatwork simple */

@font-face {
  font-family: 'FontAwesome';
  src: url('https://maxcdn.bootstrapcdn.com/font-awesome/4.6.3/fonts/fontawesome-webfont.eot?v=4.6.3');
  src: url('https://maxcdn.bootstrapcdn.com/font-awesome/4.6.3/fonts/fontawesome-webfont.eot?#iefix&v=4.6.3') format('embedded-opentype'), url('https://maxcdn.bootstrapcdn.com/font-awesome/4.6.3/fonts/fontawesome-webfont.woff2?v=4.6.3') format('woff2'), url('https://maxcdn.bootstrapcdn.com/font-awesome/4.6.3/fonts/fontawesome-webfont.woff?v=4.6.3') format('woff'), url('https://maxcdn.bootstrapcdn.com/font-awesome/4.6.3/fonts/fontawesome-webfont.ttf?v=4.6.3') format('truetype'), url('https://maxcdn.bootstrapcdn.com/font-awesome/4.6.3/fonts/fontawesome-webfont.svg?v=4.6.3#fontawesomeregular') format('svg');
  font-weight: normal;
  font-style: normal;
}

a {
  color: #0b79ad;
}

body,
pre,
input,
select,
textarea,
button {
  font-family: 'Hiragino Kaku Gothic Pro', 'ヒラギノ角ゴ Pro W3', Meiryo, メイリオ, Osaka, 'MS PGothic', arial, helvetica, sans-serif;
}

.wrapper {
  background-color: #e6e6e6;
}

[class*=" ico19"] {
  background: none;
  text-indent: 0;
  display: inline-block;
  vertical-align: middle;
  line-height: 1;
  font-weight: normal;
  font-style: normal;
  speak: none;
  text-decoration: none;
  text-transform: none;
  font-size: inherit;
  font-family: "FontAwesome";
  color: #666
}

.ico19PinOn:before,
.ico19PinOff:before,
.ico19PinHover:before {
  content: "\f005";
}

.ico19PinOff:before {
  content: "\f005";
  opacity: 0.5;
}


/* side */


/* side header */

.sideContent {
  background-color: #e6e6e6;
}

.sideContentMenu__header {
  height: 40px;
  background: transparent;
}

.sideContentMenu__addButton {
  height: 26px;
  padding: 5px;
  z-index: 2;
}

.sideContentMenu__addButton,
.sideContentMenu__addButton:hover {
  border: none;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.06);
}

.sideContentMenu__triangleDownIcon {
  display: none;
}

.chatFilterMenu {
  border: none;
  height: 40px;
  top: -40px;
  margin-bottom: -40px;
  box-shadow: 0 3px 3px -3px rgba(0, 0, 0, 0.1);
}

.chatFilterMenu .button {
  border: none;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.06);
}

.chatFilterMenu__home {
  top: 7px;
}

.chatFilterMenu__typeGroup {
  top: 7px;
  right: 74px;
}

.chatFilterMenu__typeGroup li {
  width: 33.3333%;
  min-width: 21px;
}

.chatFilterMenu__category {
  top: 7px;
  right: 44px;
}

.chatFilterMenu__unreadCount,
.chatFilterMenu__mentionCount {
  font-size: 10px;
  background: #328ba7;
}


/* side room list */

.chatRoomListArea {
  padding-bottom: 41px;
}

.roomListItem {
  min-height: 25px;
  border: none;
  border-radius: 0 3px 3px 0;
}

.roomListItem--selected {
  background-color: #969696;
}

.roomListItem:hover {
  background-color: #d6d6d6;
}

.roomListItem--selected:hover {
  background-color: #969696;
}

.roomListItem--selected span[class*=ico19]:before,
.roomListItem--selected:hover span[class*=ico19]:before {
  color: #fff!important;
}

.roomListItem--selected .roomListItem__roomName {
  text-shadow: none;
}

.roomListItem__iconContainer {
  height: 25px;
  width: 25px;
}

.roomListItem__iconContainer .avatarMedium {
  width: 25px;
  height: 25px;
}

.roomListItem__pinContainer {
  top: 10px;
  font-size: 14px;
}

.roomListItem__pin {
  display: none!important;
}

.roomListItem:hover .roomListItem__pin,
.roomListItem__pin--checked {
  display: inline-block!important;
}

.roomListItem__hasOthers {
  display: none;
}

.roomListItem__roomInfo {
  padding-top: 1px;
  max-width: calc(100% - 39px);
}

.roomListItem__roomName {
  display: inline-block;
  /*    max-width: 65%;*/
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  vertical-align: middle;
}

.roomListItem__roomName--noBadge {
  height: auto;
}

.roomListBadges {
  display: inline-block;
  padding-left: 2px;
  vertical-align: middle;
}

.roomListBadges li {
  font-size: 10px!important;
  font-weight: normal!important;
  padding: 0 6px!important;
  margin-right: 0px!important;
}

.roomListBadges li span {
  display: none;
}


/* main */


/* main header */

.mainContentHl {
  border: none;
  box-shadow: 0 0 3px rgba(0, 0, 0, 0.1);
  background: #fff;
}

.mainContentHl > span {
  vertical-align: baseline;
  display: inline;
}

.mainContentHl > span._roomTitleText {
  margin-right: 10px;
}

.chatRoomMemberInfo {
  padding-top: 5px;
  border-right: none;
  right: 142px;
}

.chatRoomHeaderBtn .button {
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.06);
  border: 1px solid #ddd;
}


/* main chat message */

.chatContent {
  border: none;
}

.chatTimeLine .actionBarSpacer {
  height: 0;
}

.chatTimeLineMessage.chatTimeLineBorder {
  border: none!important;
}

.chatTimeLineMessage .chatTimeLineMessageInner {
  border: none;
  padding: 7px 10px;
}

.chatTimeLineMessage .chatTimeLineMessageHover {
  background: #f3fbff;
}

.chatTimeLineMessage.chatTimeLineMessageMention .chatTimeLineMessageHover {
  background: #E7EFE4;
}

.chatTimeLineMessage .avatarSpeaker .avatarMedium {
  width: 35px;
  height: 35px;
  border-radius: 50%;
}

.chatTimeLineMessage .chatTimeLineItemHeader > .chatName {
  color: #333;
}

.chatTimeLineMessage .chatTimeLineItemHeader > .chatNameOrgname {
  color: #999;
}

.chatTimeLineMessage .chatTimeLineMessageArea {
  color: #333;
}

.chatTimeLineMessage .timeStamp,
.chatTimeLineMessage .timeStamp span {
  color: #999;
}

.chatTimeLineMessage .actionArea {
  bottom: 10px;
  right: 10px;
}

.chatTimeLineMessage .actionArea .actionNav {
  padding: 0 10px 2px;
  border-radius: 4px;
  border: 1px solid #ddd!important;
  background-color: #fff!important;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.06);
}


/* main chat send */

.chatSendArea {
  box-shadow: 0 0 3px rgba(0, 0, 0, 0.1);
  background: transparent;
}

.chatSendArea__chatInput {
  padding: 0;
}

.chatSendArea__areaScaleButtonLeft:hover {
  border-right: 1px solid #ddd;
}

.chatSendArea__areaScaleButtonRight:hover {
  border-left: 1px solid #ddd;
}

.chatInput {
  box-shadow: none;
  border: none;
  position: relative;
}

.chatInput .chatSendTextareaOther {
  background: #eef6f6;
}

.chatInput__tools {
  border: none;
}

.chatInput__settingEnterActionIcon {
  display: none;
}

.chatInput__submit {
  box-shadow: none;
  text-shadow: none;
}

.chatInput__externalUser {
  display: none!important;
}

.chatInput__tools [class*=" ico15"] {
  background: none;
  text-indent: 0;
  display: inline-block;
  vertical-align: middle;
  line-height: 1;
  font-weight: normal;
  font-style: normal;
  speak: none;
  text-decoration: none;
  text-transform: none;
  font-size: inherit;
  font-family: "chatwork";
  margin: 0;
  color: #444;
}

.chatInput__tools .ico15Checkbox:before {
  content: "\e62a";
  opacity: 0.3;
}

.chatInput__tools .ico15CheckboxActive:before {
  content: "\e62a";
}


/* header */

.header {
  height: 36px;
}

.globalHeader {
  height: 36px;
}

.globalHeader__branding {
  height: 36px;
}

.globalHeaderBranding__chatworkSvgLogo {
  width: 100px;
}

.globalHeader__search {
  left: 120px;
}

.globalHeaderSearch .search {
  height: 28px;
  box-shadow: none;
  padding: 5px 25px 0 34px;
  border-radius: 14px;
}

.globalHeaderSearch .search .icSearch {
  background: none;
  border: none;
  top: 2px;
}

.globalHeaderSearch .search .icSearchCancel {
  top: 4px;
}

.globalHeaderSearch .search input[type=text] {
  background: transparent;
}

.globalHeaderAssistant {
  display: none;
}

.globalHeaderNavigation {
  height: 36px;
  line-height: 36px;
}

.globalHeaderNavItem__button {
  font-size: 18px;
}

.globalHeaderInformation {
  height: 36px;
  line-height: 36px;
}

.globalHeaderInformation__icon {
  font-size: 18px;
}

.helpMenu {
  top: 36px;
}

.globalHeaderMyAccount {
  height: 36px;
  border: none;
  border-radius: 0!important;
  padding-left: 10px;
}

.globalHeaderMyAccount__avatar {
  height: 24px;
}

.globalHeaderMyAccount__avatar .avatarSmall {
  width: 24px;
  height: 24px;
  border-radius: 50%;
}

.globalHeaderMyAccount__name {
  padding-left: 5px;
}

.myAccountMenu {
  top: 36px;
}


/* [option] change header color */


/* header */

.header {
  background-color: #10435a;
}

.globalHeaderSearch .search {
  background: #416575;
}

.globalHeaderSearch .search .icSearch {
  color: #10435a;
}

.globalHeaderSearch .search input[type=text]::-webkit-input-placeholder {
  color: #abbcc3;
}

.globalHeaderNavItem__button {
  color: #abbcc3;
}

.globalHeaderInformation.helpMenuSelected {
  background-color: #416575;
}

.globalHeaderInformation__icon {
  color: #abbcc3;
}

.helpMenu {
  background-color: #416575;
}

.helpMenuItem__anchor:hover {
  background-color: #10435a;
}

.globalHeaderMyAccount {
  background-color: #10435a;
  color: #abbcc3;
}

.globalHeaderMyAccount:hover,
.globalHeaderMyAccount.myStatusButtonSelected {
  background-color: #416575;
}

.globalHeaderMyAccount__name .openAction {
  color: #abbcc3;
}

.myAccountMenu {
  background-color: #416575;
}

.myAccountMenu__anchor:hover {
  background-color: #10435a;
}


/* chatwork simple add */


/* ■■■■■■■■■■■■■■■■■■■■■    ベース    ■■■■■■■■■■■■■■■■■■■■■ */


/* -------------------- ヘッダー -------------------- */


/* すべてのタスクボタン */

.globalHeaderNavItem:nth-child(1),
.globalHeaderNavItem:nth-child(2),
.globalHeaderNavItem:nth-child(3) {
  font-weight: bold;
  font-size: 14px;
  padding-right: 48px;
}

.globalHeaderNavItem:nth-child(1):before {
  content: "タスク管理";
}

.globalHeaderNavItem:nth-child(1) .globalHeaderNavItem__button {
  display: none;
}

.globalHeaderNavItem__badge {
  display: inline-block !important;
  border-radius: 0;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
  display: none;
  position: absolute;
  top: 0;
  right: 0;
  box-sizing: border-box;
  height: 36px;
  min-width: 36px;
  max-width: auto;
  padding: 0 3px;
  font-size: 12px;
  line-height: 36px;
  border: 0;
  text-align: center;
  background-color: rgba(255, 255, 255, 0.3);
  font-size: 18px;
}


/* -------------------- 左カラム -------------------- */


/* タスクバッジ */

.roomListBadges__taskBadge {
  padding: 2px 6px !important;
  font-weight: bold;
}

.roomListItem--selected .roomListBadges__taskBadge {
  padding: 2px 6px !important;
}


/* タイムライン 日付 */

.dateHead {
  margin: 25px 0 15px;
}

.dateContent {
  top: -8px;
  left: 42%;
  padding: 0 10px;
  font-size: 14px;
}


/* -------------------- タイムライン -------------------- */

.chatTimeLine {
  padding-bottom: 30px;
}

.chatTimeLineMessage .actionArea {
  bottom: -17px;
  right: 54px;
}


/* チャットの見た目 [共通] */

.chatTimeLineMessage {
  width: 100%;
}

.chatTimeLineMessage pre {
  padding: 10px;
  border-radius: 4px;
  margin-right: 60px;
  position: relative;
  width: auto;
  min-width: 220px;
}

.chatTimeLineMessage pre:after {
  border: 8px solid transparent;
  border-left-width: 0;
  left: -8px;
  content: "";
  display: block;
  top: 8px;
  position: absolute;
  width: 0;
}


/* チャットの見た目 [自分のみ] */

.chatTimeLineMessage.chatTimeLineMessageMine {}

.chatTimeLineMessageMine pre {
  margin-left: 60px;
  margin-right: 0;
}

.chatTimeLineMessageMine pre:after {
  border: 8px solid transparent;
  border-right-width: 0;
  right: -8px;
  left: auto;
  content: "";
  display: block;
  top: 8px;
  position: absolute;
  width: 0;
}

.chatTimeLineMessageMine .avatarSpeaker {
  display: none;
}

.chatTimeLineMessageMine .chatTimeLineItemHeader {
  display: none;
}


/* チャットの見た目 [自分宛] */

.chatTimeLineMessageMention {
  background: #fff;
}
.chatTimeLineBorder.chatTimeLineMessageMention {
  background: #eaf4e7;
}
.chatTimeLineBorder.chatTimeLineMessageMention pre {
  box-shadow: 1px 1px 2px rgba(0,0,0,0.2);
}


/* チャットの見た目（タスク枠） */

.chatInfo {
  position: relative;
  border: 0px solid #ddd;
  margin: -10px;
  padding-top: 30px;
  background: transparent;
}

.chatInfo .title {
  position: absolute;
  top: 3px;
  left: 5px;
  z-index: 10;
  width: 100%;
  box-sizing: border-box;
  border: 0px solid #ddd;
  background: rgba(0, 0, 0, 0.02);
  padding: 4px 10px;
  font-weight: bold;
}

.chatInfo .chatInfoTaskArea {
  padding-top: 30px;
  margin-top: -30px;
  border-radius: 4px;
  background: transparent;
}

.chatInfo .taskMine.chatInfoTaskMetaArea,
.chatInfo .taskMine.chatInfoTaskMetaArea.taskComplete {
  border-bottom-left-radius: 4px;
  border-bottom-right-radius: 4px;
}


/* アバター位置調整 */

.chatTimeLineMessage .avatarSpeaker {
  margin-top: 20px;
}


/* チャット入力画面 */

.chatSendArea {
  border-top: 2px solid #ccc;
}


/* タイムライン 時間 */

.timeStamp {
  color: #BFCDE5 !important;
  top: 16px;
  width: 30px;
  text-align: right;
  font-size: 9px;
}


/* hover時の色 */

.chatTimeLineMessageInnerHover {
  background: transparent !important;
}


/* -------------------- 右カラム -------------------- */

.subRoomInformation__descriptionText pre {
  line-height: 1;
  margin-top: 5px;
}

.promotionArea {
  opacity: 0;
  height: 1px;
  overflow: hidden;
}

.subContentTask>li {
  padding: 10px;
}

.subContentSection {
  margin: 5px 10px;
}

.subContentSection ._taskCheck {
  font-size: 10px;
  line-height: 20px;
  min-width: 26px;
  min-height: 20px;
  height: 20px;
  margin-bottom: 0;
}

.completeCheckboxArea {
  margin-bottom: 0;
}

.subContentSection .taskDateLimit {
  font-size: 11px;
}

.subContentSection .taskMetaArea img {
  width: 16px;
  height: 16px;
}


/* ■■■■■■■■■■■■■■■■■■■■■    サイズ    ■■■■■■■■■■■■■■■■■■■■■ */


/* -------------------- タイムライン -------------------- */

.chatTimeLineMessage pre {
  font-size: 13px;
}


/* ■■■■■■■■■■■■■■■■■■■■■    色変更    ■■■■■■■■■■■■■■■■■■■■■ */


/* -------------------- ヘッダー -------------------- */

.header {
  background-color: #061e3d;
}


/* 検索 */

.globalHeaderSearch .search {
  background: #2e425c;
}

.globalHeaderSearch .search .icSearch {
  color: #7a8899;
}

.globalHeaderMyAccount {
  background-color: #061e3d;
}


/* すべてのタスクボタン */

.globalHeaderNavItem:nth-child(1) {
  background: #FF433C;
  color: #fff;
}

.globalHeaderNavItem__badge {
  color: #fff;
}


/* -------------------- 左カラム -------------------- */

.sideContent {
  background-color: #061e3d;
}

.roomListItem {
  color: #adbbcc;
}

.roomListItem--selected {
  background-color: #c5d0db;
  color: #333;
}

[class*=" ico19"] {
  color: #738D98;
}


/* タスクバッジ */

.roomListBadges__taskBadge {
  color: #fff;
  background: #F9423A;
}

.roomListItem--selected .roomListBadges__taskBadge {
  color: #fff;
  background: #F9423A;
}


/* -------------------- タイムライン -------------------- */


/* チャットの見た目 [共通] */

.chatTimeLineMessage pre {
  background: #F0F4F8;
}

.chatTimeLineMessage pre:after {
  border-right-color: #F0F4F8;
}


/* チャットの見た目 [自分のみ] */

.chatTimeLineMessageMine pre {
  background: #D9F4FD;
}

.chatTimeLineMessageMine pre:after {
  border-left-color: #D9F4FD;
}


/* チャットの見た目（タスク枠） */

.chatInfo ._task {
  background: #EBD7D6;
}

.chatInfo ._task.taskComplete {
  background: #D9E9D0;
}




}
