css:
@-moz-document url-prefix('https://web.telegram.org') {



  * {
      font-family: IRANSans, Tahoma!important;
  }
  /* i really want this to be global */

  .tg_head_split,
  .im_page_wrap,
  .im_message_wrap,
  .im_send_panel_wrap,
  .im_send_panel_wrap {
      max-width: 100%
  }
  .audio_player_title_wrap,
  .audio_player_title {
      max-width: none;
      display: inline;
  }

  .im_message_body
  {
      background-color: #f9f9f9;
      border-radius: 5px;
      padding: 1em;
  }

  .im_bot_intro_message_wrap{
      background-color: #f0f4f7;
      border-radius:1em;
  }

  .im_message_date_split_text{
      font-weight:bold;
  }
  
	div.im_message_author, div.im_message_body{display:inline-block;max-width:500px;}
  
  .im_content_message_wrap{text-align: right;}
  .pull-left{float:right!important}
  .pull-right{float:left!important}
  a.im_message_contact_photo, a.im_message_from_photo{margin:0 15px;}
  .im_dialogs_col_wrap{ border-right:none;border-left: 2px solid #E9EBED;float: right;}
  div.im_dialog_photo {margin: 0 15px;}
  .im_dialogs_col_wrap .emoji{font-size: 0px;vertical-align: middle;}
  .nav>li>a{text-align:right;}
  .im_grouped_short .im_message_body, .im_grouped .im_message_body, .im_grouped_fwd .im_message_body, .im_grouped_fwd_short .im_message_body{margin-left: 0;margin-right: 70px;}
  .emoji{font-size:0;vertical-align: -webkit-baseline-middle;}
  pre{background-color:#2f343d;color:#ddd;padding:1em 2em;text-align: left;direction: ltr;}
  .im_grouped_short .im_content_message_wrap{margin:2px;margin:0px 1em;}
  .im_grouped_short .im_message_body, .im_grouped .im_message_body, .im_grouped_fwd .im_message_body, .im_grouped_fwd_short .im_message_body{padding:0.5em 1em;}
  .im_service_message{border-top:1px solid #eee;}

  .im_history_message_wrap .im_message_body{transition:0.3s;border:1px solid transparent}
  .im_history_message_wrap:hover .im_message_body{border-color:#777}
  .im_message_fwd_from{text-align:left}
  .im_message_reply{padding:0.5em 1em}
  .im_message_document_info{text-align:left}
  .im_message_audio, .im_message_document, .im_message_upload_file, 
  .im_message_video{overflow:hidden;margin-bottom: 1em;}
  a.im_message_photo_thumb, a.im_message_video_thumb{margin:1em 1em}
  
  .icon-message-views{vertical-align:middle;}
  .im_message_views_wrap{width:100%}
  .im_message_views{top:-2em; left:0}
  .im_message_wrap{width:80%;}
  .im_dialog_meta{text-align:left;}
  
.im_message_in .pull-left{float:left!important;}
.im_message_in .pull-right{float:right!important;}
.im_grouped_short .im_message_in .im_message_body, .im_grouped .im_message_in .im_message_body, .im_grouped_fwd .im_message_in .im_message_body, .im_grouped_fwd_short .im_message_in .im_message_body{margin-right: 0;margin-left: 70px;}


.im_message_meta{position: absolute;right: 0;bottom: 5px;}
.im_content_message_wrap{position:relative;}
.im_message_date, .im_message_fwd_date{padding:0}
.im_message_in .im_message_meta{left: 0;right:inherit;}


.im_message_in.im_content_message_wrap{text-align:left;}
.im_message_in .im_message_text{text-align:right}
.im_message_in .im_message_document_caption, .im_message_in .im_message_photo_caption, .im_message_in .im_message_video_caption{text-align:right}



  
}
